6e\u3067\u2026\u3002\u305d\u308c\u306b\u5f8c\u3092\u7d99\u304e\u305f\u3044\u3068\u3082\u601d\u3063\u3066\u3044\u307e\u305b\u3093\u3057", "\u305d\u3046\u306a\u306e\uff1f", "\u306f\u3044", "\u306a\u30fc\u3093\u3060\u3002\u3060\u3063\u305f\u3089\u9ebb\u592e\u306f\u597d\u304d\u306a\u4eba\u3068\u7d50\u5a5a\u3067\u304d\u308b\u3088\u3046\u306b\u4eca\u304b\u3089\u6839\u56de\u3057\u3059\u308c\u3070\u3044\u3044\u308f", "\u3069\u3063\u304b\u3089\u7d50\u5a5a\u306e\u8a71\u304c\u51fa\u305f\u306e\u3088", "\u3060\u3063\u3066\u79c1\u9054\u306f\u3046\u304b\u3046\u304b\u3057\u3066\u3044\u308b\u3068\u653f\u7565\u7d50\u5a5a\u3067\u76f8\u624b\u3092\u89aa\u306b\u6c7a\u3081\u3089\u308c\u3066\u3057\u307e\u3046\u3058\u3083\u306a\u3044\u3002\u3060\u304b\u3089\u597d\u304d\u306a\u4eba\u304c\u51fa\u6765\u305f\u3089\u65e9\u3081\u306b\u624b\u3092\u6253\u305f\u306a\u3044\u3068", "\u306f\u3042\u2026\u3002\u5168\u304f\u3001\u7483\u3005\u5948\u306f\u5b50\u4f9b\u306e\u304f\u305b\u306b\u305d\u3093\u306a\u3053\u3068\u8003\u3048\u3066\u3044\u308b\u306e\uff1f", "\u8003\u3048\u308b\u306b\u6c7a\u307e\u3063\u3066\u308b\u3058\u3083\u306a\u3044\u3002\u79c1\u306f\u4e00\u4eba\u3063\u5b50\u3060\u304b\u3089\u304a\u5a7f\u3055\u3093\u3092\u3082\u3089\u308f\u306a\u304d\u3083\u3044\u3051\u306a\u3044\u3093\u3060\u3082\u306e\u3002\u9e97\u83ef\u3055\u3093\u3053\u305d\u304a\u6c17\u697d\u3059\u304e\u308b\u308f\u3002\u672c\u5f53\u306b\u306a\u306b\u3082\u8003\u3048\u3066\u3044\u306a\u3044\u306e\u306d", "\u2026\u3059\u307f\u307e\u305b\u3093"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/137/", "subtitle": "137", "discourse": ["\u30d9\u30a2\u305f\u3093\u306e\u306c\u3044\u3050\u308b\u307f\u306f\u3069\u3046\uff1f", "\u4ffa\u306e\u540d\u524d\u306e\u7531\u6765\u304c\u2026", "\u5927\u4e08\u592b\u3060\u3088\u3001\u30ca\u30eb\u541b\u3002\u79c1\u306f\u30ca\u30eb\u541b\u306e\u8a95\u751f\u65e5\u3092\u3061\u3083\u3093\u3068\u899a\u3048\u3066\u3044\u308b\u3088", "\u30aa\u30ea\u30b8\u30ca\u30eb\u306e\u56f3\u6848\u304c\u63cf\u3051\u306a\u3044\u304b\u3089\u3001\u305d\u3053\u304b\u3089\u30c0\u30e1\u306a\u3093\u3067\u3059\u3002\u5199\u771f\u3092\u898b\u3066\u4f5c\u308d\u3046\u306b\u3082\u306a\u304b\u306a\u304b\u4e0a\u624b\u304f\u3044\u304b\u306a\u304f\u3066\u2026", "\u3075\u3045\u3093\u3002\u305d\u306e\u5199\u771f\u3068\u9e97\u83ef\u306e\u63cf\u3044\u305f\u7d75\u3092\u898b\u305b\u3066\u304f\u308c\u308b\uff1f", "\u3053\u308c\u3067\u3059", "\u2026\u2026\u306a\u308b\u307b\u3069", "\u304a\u5144\u69d8\u3001\u7d75\u306e\u3054\u8da3\u5473\u304c\u3042\u3063\u305f\u306e\u3067\u3059\u304b\uff1f\uff01", "\u3044\u3084\u3001\u7279\u306b\u305d\u3046\u3044\u3046\u3053\u3068\u306f\u306a\u3044\u3051\u308c\u3069\u3001\u307e\u3041\u9069\u5f53\u306b\u63cf\u3044\u3066\u308b\u3060\u3051", "\u304a\u5144\u69d8\u306f\u306a\u3093\u3067\u3082\u51fa\u6765\u308b\u3093\u3067\u3059\u306d\u30fc", "\u305d\u3093\u306a\u3053\u3068\u306a\u3044\u3088\u3002\u30b3\u30c4\u304c\u308f\u304b\u3063\u3066\u3044\u308b\u3060\u3051", "\u305d\u306e\u30b3\u30c4\u3068\u3084\u3089\u304c\u3001\u79c1\u306b\u306f\u308f\u304b\u308a\u307e\u305b\u3093\u2026", "\u305d\u3046\u3044\u3048\u3070\u4eca\u5ea6\u3001\u9e97\u83ef\u3068\u4ef2\u306e\u3044\u3044\u5b50\u4f9b\u3055\u3093\u304c\u6cca\u307e\u308a\u306b\u6765\u308b\u3093\u3060\u3063\u3051\uff1f", "\u3048\u3048\u3001\u305d\u3046\u306a\u3093\u3067\u3059\u3002\u65e9\u8568\u9ebb\u592e\u3061\u3083\u3093\u3068\u3044\u3063\u3066\u745e\u9e1e\u306e\u521d\u7b49\u79d1\u306b\u901a\u3046\u5973\u306e\u5b50\u306a\u3093\u3067\u3059\u3051\u3069\u3001\u3068\u3063\u3066\u3082\u53ef\u611b\u3044\u3093\u3067\u3059\u3088\uff01", "\u305d\u3046\u306a\u3093\u3060\u3002\u697d\u3057\u307f\u3060\u306d\u3002\u78ba\u304b\u5e02\u4e4b\u5009\u5bb6\u306e\u6674\u6597\u3055\u3093\u306e\u59ea\u5fa1\u3055\u3093\u3060\u3063\u305f\u304b\u306a", "\u305d\u3046\u3067\u3059\u308f\u3002\u304a\u5144\u69d8\u3082\u5e02\u4e4b\u5009\u69d8\u306f\u3054\u5b58\u3058\u306a\u3093\u3067\u3059\u3088\u306d\uff1f", "\u3046\u3093\u3002\u4f55\u5ea6\u304b\u304a\u4f1a\u3044\u3057\u305f\u3053\u3068\u304c\u3042\u308b\u3088\u3002\u4e00\u5ea6\u30d1\u30fc\u30c6\u30a3\u30fc\u3067\u3082\u59ea\u304c\u59b9\u3055\u3093\u306b\u304a\u4e16\u8a71\u306b\u306a\u3063\u3066\u3044\u307e\u3059\u3068\u304a\u793c\u3092\u8a00\u308f\u308c\u305f\u3053\u3068\u3082\u3042\u308b\u3057\u306d", "\u305d\u3046\u3067\u3057\u305f\u304b\u30fc\u3002\u3067\u3082\u304a\u4e16\u8a71\u306b\u306a\u3063\u3066\u3044\u308b\u306e\u306f\u79c1\u306e\u307b\u3046\u306a\u3093\u3067\u3059\u3051\u3069\u306d\u3002\u307b\u3089\u3001\u4f55\u5ea6\u3082\u304a\u98df\u4e8b\u306b\u9023\u308c\u3066\u884c\u3063\u3066\u3082\u3089\u3063\u305f\u308a", "\u3042\u3041\u3001\u305d\u3046\u3060\u306d", "\u305d\u308c\u3067\u3001\u304a\u5144\u69d8\u306b\u304a\u9858\u3044\u304c\u3042\u308b\u3093\u3067\u3059\u3002\u524d\u306b\u6c34\u65cf\u9928\u306b\u9023\u308c\u3066\u884c\u3063\u3066\u304f\u308c\u308b\u3068\u7d04\u675f\u3057\u3066\u304f\u308c\u305f\u3067\u3057\u3087\u3046\uff1f\u3042\u308c\u3063\u3066\u305b\u3063\u304b\u304f\u3060\u304b\u3089\u9ebb\u592e\u3061\u3083\u3093\u3082\u4e00\u7dd2\u306b\u9023\u308c\u3066\u884c\u3063\u3066\u6b32\u3057\u3044\u3093\u3067\u3059\u3002\u30c0\u30e1\u3067\u3059\u304b\uff1f", "\u304b\u307e\u308f\u306a\u3044\u3088\u3002\u3058\u3083\u3042\u9ebb\u592e\u3061\u3083\u3093\u304c\u904a\u3073\u306b\u6765\u308b\u65e5\u306e\u30b9\u30b1\u30b8\u30e5\u30fc\u30eb\u3092\u78ba\u8a8d\u3057\u3066\u307f\u308b\u3088\u3002\u9ebb\u592e\u3061\u3083\u3093\u304c\u4e00\u7dd2\u3060\u304b\u3089\u8fd1\u5834\u3067\u3044\u3044\u304b\u306a", "\u3048\u3048\uff01\u9ebb\u592e\u3061\u3083\u3093\u3082\u304d\u3063\u3068\u559c\u3093\u3067\u304f\u308c\u307e\u3059\u308f\uff01", "\u304a\u5b22\u69d8", "\u304a\u5b22\u69d8\u3001\u9811\u5f35\u3063\u3066\u30fc\uff01", "\u304a\u5b22\u69d8\u306f\u3060\u3044\u3076\u8d70\u308b\u306e\u306b\u6163\u308c\u3066\u304d\u307e\u3057\u305f\u306d\uff01\u590f\u4f11\u307f\u306e\u7d42\u308f\u308a\u306b\u306f\u7687\u5c45\u306b\u884c\u3063\u3066\u307f\u307e\u3057\u3087\u3046\uff01", "\u3048\u3048\u3063\uff01\u7687\u5c45\u3067\u3059\u304b\uff1f\uff01\u30e0\u30ea\u3067\u3059\u3001\u79c1\u306b\u306f\u6577\u5c45\u304c\u9ad8\u3059\u304e\u307e\u3059\uff01", "\u304a\u5b22\u69d8\u3001\u76ee\u6a19\u306f\u9ad8\u304f\u63b2\u3052\u3066\u3053\u305d\u3067\u3059\u3002\u5927\u4e08\u592b\u3001\u7687\u5c45\u306e\u5468\u308a\u306f\u7d04\uff15\u30ad\u30ed\u3068\u3044\u3063\u305f\u3068\u3053\u308d\u3067\u3059\u304b\u3089\u3001\u4eca\u304b\u3089\u7df4\u7fd2\u3059\u308c\u3070\u8d70\u308c\u307e\u3059\uff01", "\u3048\u3048\u3063\uff01\uff15\u30ad\u30ed\u3063\u3066\u4eca\u3088\u308a\u3082\uff12\u30ad\u30ed\u3082\u5897\u3048\u308b\u3058\u3083\u306a\u3044\u3067\u3059\u304b\uff01", "\u304a\u5b22\u69d8\u306a\u3089\u304d\u3063\u3068\u8d70\u308c\u307e\u3059\u3002\u6bce\u65e5\u9811\u5f35\u3063\u3066\u3001\u305d\u3057\u3066\u6765\u5e74\u306b\u306f\u30db\u30ce\u30eb\u30eb\u306b\u884c\u304d\u307e\u3057\u3087\u3046\uff01", "\u3048\u3048\u30fc\u3063\uff01\u30db\u30ce\u30eb\u30eb\uff1f\uff01", "\u8d70\u308b\u524d\u306b\u8ae6\u3081\u308b\u306a\uff01", "\u81ea\u5206\u306e\u5fc3\u306e\u5f31\u3055\u306b\u6253\u3061\u52dd\u3064\u52aa\u529b\u3092\uff01", "\u3054\u304d\u3052\u3093\u3088\u3046\u3001\u9e97\u83ef\u304a\u59c9\u69d8\uff01\u4eca\u65e5\u306f\u3088\u308d\u3057\u304f\u304a\u9858\u3044\u3044\u305f\u3057\u307e\u3059", "\u3044\u3089\u3063\u3057\u3083\u3044\u3001\u9ebb\u592e\u3061\u3083\u3093\u3002\u3053\u3061\u3089\u3053\u305d\u3088\u308d\u3057\u304f\u306d", "\u9e97\u83ef\u3055\u3093\u3001\u672c\u65e5\u306f\u5a18\u304c\u304a\u4e16\u8a71\u306b\u306a\u308a\u307e\u3059", "\u305d\u3093\u306a\u3001\u306a\u3093\u306e\u304a\u3082\u3066\u306a\u3057\u3082\u3067\u304d\u307e\u305b\u3093\u306e\u3088\uff1f\u305f\u3060\u5927\u4e8b\u306a\u9ebb\u592e\u3061\u3083\u3093\u306f\u8cac\u4efb\u3092\u6301\u3063\u3066\u304a\u9810\u304b\u308a\u3044\u305f\u3057\u307e\u3059\u306e\u3067\u3001\u3069\u3046\u304b\u3054\u5b89\u5fc3\u306a\u3055\u3063\u3066\u304f\u3060\u3055\u3044", "\u307e\u3041\u65e9\u8568\u69d8\u3054\u304d\u3052\u3093\u3088\u3046\u3002\u4eca\u65e5\u306f\u304a\u5b22\u3055\u3093\u304c\u6cca\u307e\u308a\u306b\u3044\u3089\u3057\u305f\u306e\u3067\u3059\u308f\u306d\uff1f", "\u3054\u304d\u3052\u3093\u3088\u3046\u3001\u5409\u7965\u9662\u69d8\u3002\u5a18\u304c\u304a\u4e16\u8a71\u306b\u306a\u308a\u307e\u3059\u3002\u3069\u3046\u305e\u3088\u308d\u3057\u304f\u304a\u9858\u3044\u3044\u305f\u3057\u307e\u3059", "\u3088\u308d\u3057\u304f\u304a\u9858\u3044\u3044\u305f\u3057\u307e\u3059", "\u3042\u3089\u53ef\u611b\u3089\u3057\u3044\u304a\u5b22\u3055\u3093\u3060\u3053\u3068", "\u9ebb\u592e\u3061\u3083\u3093\u3001\u304a\u6bcd\u69d8\u304c\u5e30\u3063\u3066\u3057\u307e\u3063\u3066\u5bc2\u3057\u304f\u306a\u3044\uff1f", "\u5e73\u6c17\u3067\u3059\u308f\u3002\u308f\u3041\u3063\u3001\u9e97\u83ef\u304a\u59c9\u69d8\u306e\u304a\u90e8\u5c4b\u3001\u53ef\u611b\u3044\uff01"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/136/", "subtitle": "136", "discourse": ["\u304a\u30fc\u3044\uff01", "\u304a\u307e\u305f\u305b\uff01\u9023\u308c\u3066\u304d\u305f\u3088\u3001\u30d9\u30a2\u30c8\u30ea\u30fc\u30c1\u30a7\uff01", "\u53ef\u611b\u3044\uff01", "\u53ef\u611b\u3044\u306d\u3047", "\u30d9\u30a2\u30c8\u30ea\u30fc\u30c1\u30a7\uff5e", "\u53ef\u611b\u3044\u3063\u3066\u3001\u826f\u304b\u3063\u305f\u306d\uff5e\u3001\u30d9\u30a2\u305f\uff5e\u3093", "\u72ac\u3082\u96fb\u8eca\u306b\u4e57\u308c\u308b\u3093\u3067\u3059\u306d", "\u3046\u3093\u3002\u304d\u3061\u3093\u3068\u30d0\u30c3\u30b0\u306b\u5165\u308c\u3066\u4e57\u8eca\u6599\u91d1\u6255\u3048\u3070\u4e57\u308c\u308b\u3088", "\u30ef\u30f3\u30c3\uff01", "\u30d9\u30a2\u305f\uff5e\u3093\uff01\u72ed\u304b\u3063\u305f\u306d\uff5e\uff01\u3067\u3082\u3044\u3044\u5b50\u306b\u3057\u3066\u305f\u306d\uff5e\uff01", "\u805e\u304d\u3057\u306b\u52dd\u308b\u30c7\u30ec\u3063\u3077\u308a\u3060\u306a", "\u3055\u3041\uff01\u30d9\u30a2\u305f\u3093\u3001\u4eca\u65e5\u306f\u304a\u53cb\u9054\u306e\u9e97\u83ef\u305f\u3093\u306e\u30e2\u30c7\u30eb\u3055\u3093\u306b\u306a\u3063\u3066\u3042\u3052\u308b\u3093\u3060\u3088\uff5e\u3002\u53ef\u611b\u304f\u64ae\u3063\u3066\u3082\u3089\u3046\u305f\u3081\u306b\u3001\u304d\u308c\u3044\u304d\u308c\u3044\u306b\u3057\u3088\u3046\u306d\uff5e", "\u307b\uff5e\u3089\u304d\u308c\u3044\u306b\u306a\u3063\u305f\uff01\u30d9\u30a2\u305f\u3093\u3001\u9e97\u83ef\u305f\u3093\u9054\u306b\u3054\u6328\u62f6\u3057\u3088\u3046\uff01", "\u9e97\u83ef\u305f\u3093\u2026\uff1f", "\u308f\u3042\u3063\uff01", "\u3046\u308f\u3042\u3063\uff01\u306a\u306b\uff1f\uff01\u306a\u306b\uff1f\uff01", "\u304a\u3044\u3001\u6885\u82e5\uff01", "\u30d9\u30a2\u305f\u3093\u3001\u5927\u597d\u304d\u306a\u9e97\u83ef\u305f\u3093\u306b\u4f1a\u3048\u3066\u5b09\u3057\u3044\u3093\u3060\u306d\uff5e\u3002\u9e97\u83ef\u305f\u3093\u306e\u6bdb\u3065\u304f\u308d\u3044\u3092\u3057\u3066\u3042\u3052\u3066\u308b\u306e\u304b\u3041\u3002\u30d9\u30a2\u305f\u3093\u306f\u512a\u3057\u3044\u306d\uff5e", "\u30d9\u30a2\u305f\u3093\u559c\u3093\u3067\u308b\u3001\u559c\u3093\u3067\u308b\u3002\u81ea\u5206\u3068\u540c\u3058\u304f\u308b\u304f\u308b\u30d8\u30a2\u30fc\u3060\u3082\u3093\u306d\u3047\u3002\u4ef2\u9593\u306b\u4f1a\u3048\u3066\u5b09\u3057\u3044\u306d\u3047", "\u3044\u3084\u6885\u82e5\u3001\u5409\u7965\u9662\u3055\u3093\u304c\u9650\u754c\u3060\u2026\u3002\u52a9\u3051\u3066\u3084\u308c", "\u305d\u3046\u304b\uff1f\u5409\u7965\u9662\u3055\u3093\u3001\u9650\u754c\uff1f\u305d\u3063\u304b\u3002\u3058\u3083\u3042\u30d9\u30a2\u305f\u3093\u3001\u3053\u3063\u3061\u304a\u3044\u3067\u30fc", "\u5409\u7965\u9662\u3055\u3093\u3001\u5927\u4e08\u592b\uff1f\uff01", "\u2026\u3048\u3048\u3001\u5927\u4e08\u592b\u3067\u3059\u308f\u3002\u5c11\u3057\u9a5a\u3044\u3066\u3057\u307e\u3063\u305f\u3060\u3051\u3067\u2026", "\u3067\u3082\u9aea\u3001\u307c\u308d\u307c\u308d\u3060\u3088\uff1f\u4e00\u5ea6\u30c8\u30a4\u30ec\u3067\u76f4\u3057\u3066\u304d\u305f\u307b\u3046\u304c\u3044\u3044\u304b\u3082\u2026", "\u3046\u308f\u3042\u3063\uff01\u51c4\u3044\u53ef\u611b\u3044\u3063\uff01\u3053\u308c\u3082\u3089\u3063\u3066\u3044\u3044\u306e\uff1f\uff01\u3042\u308a\u304c\u3068\u3046\u5409\u7965\u9662\u3055\u3093\uff01", "\u307b\u3089\u3001\u9e97\u83ef\u305f\u3093\u304b\u3089\u30d9\u30a2\u305f\u3093\u306b\u3063\u3066\u53ef\u611b\u3044\u30d0\u30c3\u30b0\u3082\u3089\u3063\u305f\u3088\u30fc\u3002\u308f\uff01\u3042\u30fc\u305f\u3093\u3068\u30d9\u30a2\u305f\u3093\u306e\u540d\u524d\u3082\u5165\u3063\u3066\u308b\u3088\u3001\u5b09\u3057\u3044\u306d\u3047", "\u304a\u30ea\u30dc\u30f3\u306f\u5909\u3048\u305f\u307b\u3046\u304c\u3044\u3044\uff1f\u3044\u308d\u3044\u308d\u6301\u3063\u3066\u304d\u305f\u3088", "\u3046\u308f\u3063\u3001\u51a0\u307e\u3067\u3042\u308b\u306e\u304b\u3088", "\u3082\u3061\u308d\u3093\u3002\u30d9\u30a2\u305f\u3093\u306f\u304a\u59eb\u69d8\u3060\u304b\u3089\u306d\u30fc\u3002\u30c6\u30a3\u30a2\u30e9\u306f\u5fc5\u9700\u54c1\u3060\u3088\u3002\u306d\u30fc\u3001\u30d9\u30a2\u305f\u3093", "\u5f85\u3066\uff5e\uff01", "\u3064\u304b\u307e\u3048\u305f\uff01", "\u304d\u3083\u3046\u3093\u304d\u3083\u3046\u3093\uff01", "\u30d9\u30a2\u305f\u3093\u5c0f\u60aa\u9b54\uff5e\uff01", "\u6885\u82e5\u3063\u3066\u3053\u3046\u3044\u3046\u30e4\u30c4\u3060\u3063\u305f\u3093\u3060\u2026"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/135/", "subtitle": "135", "discourse": ["\u9e97\u83ef\u3055\u3093\u3001\u4eca\u56de\u306e\u30d7\u30e9\u30f3\u306f\u3044\u304b\u304c\u3060\u3063\u305f\u304b\u3057\u3089\uff1f", "\u306f\u3044\u3002\u81ea\u7136\u98df\u3092\u8eab\u8fd1\u306b\u611f\u3058\u308b\u3053\u3068\u304c\u3067\u304d\u3066\u3001\u3068\u3066\u3082\u6709\u610f\u7fa9\u306a\uff12\u65e5\u9593\u3067\u3057\u305f", "\u305d\u3046\u8a00\u3063\u3066\u3082\u3089\u3048\u308b\u3068\u79c1\u3082\u5b09\u3057\u3044\u308f\u3002\u9e97\u83ef\u3055\u3093\u306f\u75e9\u305b\u3066\u3044\u308b\u304b\u3089\u30c0\u30a4\u30a8\u30c3\u30c8\u306f\u5fc5\u8981\u306a\u3044\u3082\u306e\u306d\u3047\uff1f", "\u3068\u3093\u3067\u3082\u3042\u308a\u307e\u305b\u3093\u308f", "\u9e97\u83ef\u3055\u3093\u306f\u590f\u4f11\u307f\u306f\u3069\u306e\u3088\u3046\u306b\u904e\u3054\u3059\u4e88\u5b9a\u306a\u306e\u304b\u3057\u3089\uff1f", "\u587e\u306e\u590f\u671f\u8b1b\u7fd2\u3084\u7fd2\u3044\u4e8b\u306b\u901a\u3063\u305f\u308a\u3001\u304a\u53cb\u9054\u3068\u4f1a\u3046\u7d04\u675f\u306a\u3069\u304c\u3044\u304f\u3064\u304b\u3042\u308a\u307e\u3059\u3002\u3042\u3068\u306f\u4e21\u89aa\u3068\u306e\u5bb6\u65cf\u65c5\u884c\u3067\u3057\u3087\u3046\u304b", "\u3042\u3089\u3088\u308d\u3057\u3044\u308f\u306d\u3002\u4eca\u5e74\u306f\u3069\u3061\u3089\u306b\u884c\u304b\u308c\u308b\u306e\uff1f", "\u6bcd\u306e\u5e0c\u671b\u3067\u30aa\u30fc\u30b9\u30c8\u30ea\u30a2\u3067\u3059\u3002\u6e56\u4e0a\u30aa\u30da\u30e9\u304c\u89b3\u305f\u3044\u305d\u3046\u306a\u306e\u3067", "\u307e\u3041\u7d20\u6575\u306d\uff01\u6e56\u4e0a\u30aa\u30da\u30e9\u306a\u3089\u79c1\u3082\u524d\u306b\u89b3\u306b\u884c\u3063\u305f\u3053\u3068\u304c\u3042\u308b\u306e\u3088\u3002\u5727\u5012\u7684\u306a\u8feb\u529b\u3067\u3001\u3068\u3066\u3082\u7d20\u6674\u3089\u3057\u304b\u3063\u305f\u308f\uff01\u9e97\u83ef\u3055\u3093\u306f\u30aa\u30da\u30e9\u3082\u304a\u597d\u304d\u306a\u306e\uff1f", "\u6bcd\u306e\u5f71\u97ff\u3067\u3001\u30e8\u30fc\u30ed\u30c3\u30d1\u306b\u884c\u3063\u305f\u6642\u306b\u597d\u304d\u306a\u6f14\u76ee\u304c\u4e0a\u6f14\u3055\u308c\u3066\u3044\u308c\u3070\u3001\u89b3\u5287\u3044\u305f\u3057\u307e\u3059\u3002\u305f\u3060\u79c1\u306f\u304a\u6065\u305a\u304b\u3057\u306a\u304c\u3089\u6bce\u56de\u697d\u3057\u304f\u9451\u8cde\u3057\u3066\u3044\u308b\u3060\u3051\u3067\u3001\u30aa\u30da\u30e9\u305d\u306e\u3082\u306e\u306b\u306f\u3042\u307e\u308a\u8a73\u3057\u304f\u306f\u3042\u308a\u307e\u305b\u3093\u306e\u3002\u3082\u3063\u3068\u77e5\u8b58\u3092\u6df1\u3081\u3066\u304b\u3089\u9451\u8cde\u3059\u3079\u304d\u3068\u306f\u601d\u3063\u3066\u3044\u308b\u306e\u3067\u3059\u3051\u3069\u2026", "\u305d\u3093\u306a\u3053\u3068\u306a\u3044\u308f\uff01\u982d\u3067\u3063\u304b\u3061\u3067\u96e3\u3057\u304f\u9451\u8cde\u3059\u308b\u3088\u308a\u3082\u3001\u9e97\u83ef\u3055\u3093\u306e\u3088\u3046\u306b\u7d20\u76f4\u306a\u5fc3\u3067\u697d\u3057\u3080\u307b\u3046\u304c\u79c1\u306f\u3044\u3044\u3068\u601d\u3046\u308f\u3088\uff1f\u79c1\u3082\u30aa\u30da\u30e9\u306f\u5927\u597d\u304d\u306a\u306e\u3002\u9e97\u83ef\u3055\u3093\u306e\u597d\u304d\u306a\u30aa\u30da\u30e9\u306f\u306a\u306b\u304b\u3057\u3089\uff1f", "\u6708\u4e26\u307f\u3067\u3059\u304c\u3001\u30ed\u30fc\u30a8\u30f3\u30b0\u30ea\u30f3\u3068\u304b\u9b54\u7b1b\u306a\u3069\u3067\u3057\u3087\u3046\u304b", "\u30ed\u30fc\u30a8\u30f3\u30b0\u30ea\u30f3\u304c\u304a\u597d\u304d\u306a\u306e\u306d\uff1f\u3067\u306f\u540c\u3058\u30ef\u30fc\u30b0\u30ca\u30fc\u3067\u30c8\u30ea\u30b9\u30bf\u30f3\u3068\u30a4\u30be\u30eb\u30c7\u306f\u3069\u3046\u304b\u3057\u3089\uff1f", "\u3068\u3066\u3082\u60c5\u71b1\u7684\u3060\u3068\u601d\u3044\u307e\u3059", "\u307e\u3041\uff01", "\u305d\u3046\u3060\u308f\uff01", "\u4eca\u5ea6\u3001\u86cd\u72e9\u308a\u306e\u4f1a\u3092\u958b\u304f\u306e\u3002\u9e97\u83ef\u3055\u3093\u3001\u305c\u3072\u3044\u3089\u3057\u3066\uff01\u5e7b\u60f3\u7684\u306a\u4e16\u754c\u304c\u304a\u597d\u304d\u306a\u9e97\u83ef\u3055\u3093\u306a\u3089\u304d\u3063\u3068\u6c17\u306b\u5165\u308b\u3068\u601d\u3046\u306e\uff01", "\u3048\u2026", "\u86cd\u72e9\u308a\u3060\u306a\u3093\u3066\u7d20\u6575\u3060\u308f\u3002\u9e97\u83ef\u3055\u3093\u826f\u304b\u3063\u305f\u308f\u306d\u3002\u9e97\u83ef\u3055\u3093\u306f\u6e90\u6c0f\u7269\u8a9e\u304c\u597d\u304d\u3060\u3082\u306e\u306d", "\u3042\u3089\u3001\u9e97\u83ef\u3055\u3093\u306f\u6e90\u6c0f\u304c\u304a\u597d\u304d\u306a\u306e\uff1f\u3067\u3057\u305f\u3089\u5f53\u65e5\u306f\u9e97\u83ef\u3055\u3093\u306b\u7389\u9b18\u306b\u306a\u3063\u3066\u3044\u305f\u3060\u304b\u306a\u3044\u3068", "\u307e\u3041\u3001\u307b\u307b\u307b\u3002\u3067\u306f\u86cd\u5175\u90e8\u537f\u5bae\u306f\u3069\u306a\u305f\u304b\u3057\u3089\uff1f", "\u3046\u3061\u306e\u96c5\u54c9\u306a\u3069\u3044\u304b\u304c\uff1f", "\u3042\u3089\u3041\u3063\u9e97\u83ef\u3055\u3093\uff01\u3069\u3046\u3057\u307e\u3057\u3087\u3046\u3002\u826f\u304b\u3063\u305f\u308f\u306d\u3047", "\u96c5\u54c9\u3055\u3093\u306e\u304a\u6bcd\u69d8\u306b\u6c17\u306b\u5165\u3089\u308c\u3066\u308b\u3093\u3060\u304b\u3089\u3001\u304a\u8fd1\u3065\u304d\u306b\u306a\u308b\u30c1\u30e3\u30f3\u30b9\u3088", "\u307f\u306a\u3055\u3093\u3001\uff12\u65e5\u9593\u304a\u75b2\u308c\u69d8\u3067\u3057\u305f\u3002\u4eca\u65e5\u306f\u606f\u5b50\u306e\u96c5\u54c9\u3082\u9023\u308c\u3066\u53c2\u308a\u307e\u3057\u305f\u306e\u3088", "\u307e\u3041\u3063\u96c5\u54c9\u3055\u3093\u306b\u304a\u4f1a\u3044\u3067\u304d\u308b\u306a\u3093\u3066\u5b09\u3057\u3044\u308f\uff01", "\u304a\u75b2\u308c\u69d8\u3067\u3057\u305f", "\u304a\u3063\u3001\u306a\u3093\u3060\u304a\u524d\u3082\u6765\u3066\u305f\u306e\u304b", "\u3054\u304d\u3052\u3093\u3088\u3046\u93d1\u6728\u69d8", "\u8a00\u3063\u3068\u304f\u304c\u3001\u3053\u3093\u306a\u30d7\u30e9\u30f3\u3058\u3083\u75e9\u305b\u306a\u3044\u305e\u3002\u904b\u52d5\u3057\u308d", "\u79c1\u306f\u6bcd\u306b\u4ed8\u304d\u6dfb\u3063\u3066\u304d\u305f\u3060\u3051\u3067\u3059\u304b\u3089\u2026\u3002\u7279\u306b\u75e9\u305b\u308b\u305f\u3081\u306b\u53c2\u52a0\u3057\u3066\u3044\u308b\u308f\u3051\u3067\u306f\u3042\u308a\u307e\u305b\u3093\u306e\u3088\uff1f", "\u3075\uff5e\u3093\u3002\u307e\u3041\u3069\u3063\u3061\u3067\u3082\u3044\u3044\u3051\u3069", "\u590f\u4f11\u307f\u3060\u3063\u3066\u3044\u3046\u306e\u306b\u3001\u6bcd\u89aa\u3068\u30c0\u30a4\u30a8\u30c3\u30c8\u30d7\u30e9\u30f3\u306b\u53c2\u52a0\u306a\u3093\u3066\u3054\u82e6\u52b4\u306a\u3053\u3068\u3060\u306a", "\u30c7\u30c8\u30c3\u30af\u30b9\u30d7\u30e9\u30f3\u3067\u3059\u3051\u3069\u306d\u3002\u3054\u81ea\u5206\u3082\u304a\u6bcd\u69d8\u306b\u4f9d\u983c\u3055\u308c\u3066\u4eca\u65e5\u9854\u3092\u51fa\u3055\u308c\u305f\u306e\u3067\u3057\u3087\u3046\uff1f\u4f3c\u305f\u3088\u3046\u306a\u3082\u306e\u3067\u306f\u3042\u308a\u307e\u305b\u3093\u304b", "\u307e\u3042\u306a", "\u96c5\u54c9\u69d8\u3001\u4eca\u65e5\u306f\u4f1a\u3048\u3066\u5b09\u3057\u3044\u3067\u3059\u308f\uff01\u3053\u306e\u524d\u306e\u30d1\u30fc\u30c6\u30a3\u30fc\u3067\u3082\u306a\u304b\u306a\u304b\u304a\u8a71\u3057\u51fa\u6765\u306a\u304b\u3063\u305f\u3093\u3067\u3059\u3082\u306e\u3002\u96c5\u54c9\u69d8\u306e\u3053\u306e\u5f8c\u306e\u3054\u4e88\u5b9a\u306f\uff1f\u3088\u304b\u3063\u305f\u3089\u4e0b\u306e\u30e9\u30a6\u30f3\u30b8\u3067\u304a\u8336\u3092\u3054\u4e00\u7dd2\u306b", "\u3053\u306e\u5f8c\u306f\u4eba\u3068\u4f1a\u3046\u7d04\u675f\u304c\u3042\u308b\u304b\u3089", "\u3058\u3083\u3042\u306a", "\u6628\u65e5\u9e97\u83ef\u3055\u3093\u306b\u8db3\u3092\u8e0f\u307e\u308c\u307e\u3057\u305f\u306e\uff5e", "\u3042\u3042\u3001\u305d\u3046"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/134/", "subtitle": "134", "discourse": ["\u4eca\u65e5\u306f\u307f\u306a\u3055\u3093\u3001\u53c2\u52a0\u3057\u3066\u304f\u3060\u3055\u3063\u3066\u3042\u308a\u304c\u3068\u3046\uff01\u4eca\u56de\u306e\u30d7\u30e9\u30f3\u306f\u79c1\u9054\u306e\u30db\u30c6\u30eb\u304c\u4eca\u4e00\u756a\u81ea\u4fe1\u3092\u6301\u3063\u3066\u304a\u85a6\u3081\u3057\u3066\u3044\u308b\u30d7\u30e9\u30f3\u3067\u3059\u306e\u3088", "\u307e\u305f\u9e97\u83ef\u69d8\u3068\u4e00\u7dd2\u3067\u5b09\u3057\u3044\u308f\u3002\u5e74\u306e\u8fd1\u3044\u5b50\u304c\u3044\u308b\u3068\u5b89\u5fc3\u3060\u3082\u306e", "\u79c1\u3082\u3067\u3059\u308f\u3002\u4eca\u56de\u306f\u304a\u98df\u4e8b\u304c\u51fa\u308b\u304b\u3089\u53bb\u5e74\u3088\u308a\u306f\u697d\u305d\u3046\u3067\u3059\u308f\u306d", "\u3075\u3075\u3063", "\u9e97\u83ef\u3055\u3093\uff01\u305d\u308c\u306b\u8000\u7f8e\u3055\u3093\u3082\u3001\u3088\u304f\u3044\u3089\u3057\u3066\u304f\u308c\u305f\u308f\u306d\uff01\u4eca\u65e5\u306f\u4f1a\u3048\u308b\u306e\u3092\u697d\u3057\u307f\u306b\u3057\u3066\u3044\u305f\u306e\u3088\u3002\u7279\u306b\u9e97\u83ef\u3055\u3093\u306f\u30d1\u30fc\u30c6\u30a3\u30fc\u306b\u3082\u306a\u304b\u306a\u304b\u9854\u3092\u51fa\u3057\u3066\u304f\u308c\u306a\u3044\u304b\u3089\u5bc2\u3057\u304f\u3066", "\u3054\u304d\u3052\u3093\u3088\u3046\u3002\u672c\u65e5\u306f\u3088\u308d\u3057\u304f\u304a\u9858\u3044\u3044\u305f\u3057\u307e\u3059", "\u304a\u4e45\u3057\u3076\u308a\u3067\u3059\u3001\u93d1\u6728\u69d8", "\u9e97\u83ef\u3055\u3093\u3001\u5b66\u9662\u751f\u6d3b\u306f\u3069\u3046\uff1f\u3046\u3061\u306e\u96c5\u54c9\u306f\u306a\u3093\u306b\u3082\u8a71\u3057\u3066\u304f\u308c\u306a\u3044\u306e\u3088\u3002\u3060\u304b\u3089\u305c\u3072\u9e97\u83ef\u3055\u3093\u306b\u745e\u9e1e\u306e\u8a71\u3092\u805e\u304b\u305b\u3066\u3082\u3089\u3044\u305f\u3044\u308f", "\u307e\u3041\u2026\u3002\u79c1\u3067\u306f\u305f\u3044\u3057\u305f\u8a71\u3082\u3067\u304d\u306a\u3044\u3068\u601d\u3044\u307e\u3059\u304c\u2026\u3002\u96c5\u54c9\u69d8\u306f\u8a66\u9a13\u3067\u3082\u5e38\u306b\u30c8\u30c3\u30d7\u306e\u6210\u7e3e\u3067\u3001\u6bce\u65e5\u304a\u53cb\u9054\u3068\u5145\u5b9f\u3057\u305f\u751f\u6d3b\u3092\u9001\u3063\u3066\u3044\u308b\u3088\u3046\u3067\u3059\u308f", "\u305d\u3046\uff1f\u3042\u306e\u5b50\u611b\u60f3\u306e\u306a\u3044\u5b50\u3067\u3057\u3087\u3046\uff1f\u9e97\u83ef\u3055\u3093\u306b\u8ff7\u60d1\u3092\u304b\u3051\u3066\u3044\u308b\u3093\u3058\u3083\u306a\u3044\u304b\u3057\u3089\uff1f\u7c97\u66b4\u306a\u632f\u308b\u821e\u3044\u3067\u3082\u3057\u305f\u3089\u3001\u3059\u3050\u306b\u79c1\u306b\u304a\u3063\u3057\u3083\u3063\u3066\u306d", "\u7c97\u66b4\u306a\u632f\u308b\u821e\u3044\u3060\u306a\u3093\u3066\u3001\u3068\u3093\u3067\u3082\u3042\u308a\u307e\u305b\u3093\u308f\u3002\u96c5\u54c9\u69d8\u306f\u96e8\u306e\u65e5\u306b\u306f\u30b5\u30ed\u30f3\u3067\u30b7\u30e7\u30d1\u30f3\u3092\u594f\u3067\u3089\u308c\u308b\u3088\u3046\u306a\u304b\u305f\u3067\u3059\u304b\u3089\u2026", "\u307e\u3041\uff01\u3042\u306e\u5b50\u3063\u305f\u3089\u305d\u3093\u306a\u3053\u3068\u3092\u3057\u3066\u3044\u308b\u306e\uff1f\uff01", "\u4eca\u65e5\u306f\u79c1\u3082\u98df\u4e8b\u306b\u306f\u53c2\u52a0\u3059\u308b\u306e\u3088\u3002\u305d\u306e\u6642\u306b\u307e\u305f\u304a\u8a71\u3092\u3057\u305f\u3044\u308f", "\u306f\u3044\u3002\u697d\u3057\u307f\u306b\u3057\u3066\u3044\u307e\u3059", "\u96c5\u54c9\u69d8\u306e\u304a\u6bcd\u69d8\u306b\u3061\u3087\u3063\u3068\u6c17\u306b\u5165\u3089\u308c\u305f\u304b\u3089\u3063\u3066\u3001\u8abf\u5b50\u306b\u4e57\u3089\u306a\u3044\u3067\u3088\u306d\uff01", "\u2026\u3042\u306e\u3001\u9e97\u83ef\u3055\u3093\u5927\u4e08\u592b\uff1f\u305a\u3044\u3076\u3093\u7768\u307e\u308c\u3066\u3044\u305f\u3051\u308c\u3069\u2026", "\u79c1\u306f\u5168\u7136\u5e73\u6c17\u3067\u3059\u308f\u3002\u306a\u3093\u3060\u304b\u4e00\u65b9\u7684\u306b\u30e9\u30a4\u30d0\u30eb\u8996\u3055\u308c\u3066\u3044\u307e\u3059\u306e", "\u305d\u3046\u306a\u306e\u2026\u3001\u5927\u5909\u306d\u2026", "\u81ea\u5206\u306b\u8ca0\u3051\u308b\u306a\uff01", "\u541b\u306a\u3089\u51fa\u6765\u308b\uff01", "\u9e97\u83ef\u3055\u3093\u305f\u3089\u306a\u306b\u30e0\u30ad\u306b\u306a\u3063\u3066\u308b\u306e\uff1f\u9aea\u632f\u308a\u4e71\u3057\u3061\u3083\u3063\u3066\u898b\u82e6\u3057\uff5e\u3044\u3002\u52dd\u624b\u306b\u7af6\u4e89\u5fc3\u71c3\u3084\u3057\u3066\u304d\u3066\u3001\u76f8\u624b\u306b\u3057\u3066\u3089\u3093\u306a\u3044\u308f", "\u606f\u3042\u304c\u3063\u3061\u3083\u3063\u3066\u3093\u3058\u3083\u306a\u3044\u3002\u307b\u30fc\u3093\u3068\u5fc5\u6b7b", "\u821e\u6d5c\u3055\u3093\u3053\u305d\u3054\u7121\u7406\u306a\u3055\u3063\u3066\u3044\u308b\u3093\u3058\u3083\u306a\u3044\uff1f\u76ee\u306e\u4e0b\u306b\u30af\u30de\u304c\u51fa\u6765\u3066\u3044\u307e\u3057\u3066\u3088\u3002\u3042\u3089\uff1f\u3054\u3081\u3093\u306a\u3055\u3044\u3001\u79c1\u306e\u898b\u9593\u9055\u3044\u3067\u3057\u305f\u308f\u3002\u30af\u30de\u3067\u306f\u306a\u304b\u3063\u305f\u307f\u305f\u3044\u3002\u821e\u6d5c\u3055\u3093\u3001\u5dee\u3057\u51fa\u304c\u307e\u3057\u3044\u3088\u3046\u3067\u3059\u3051\u3069\u30de\u30b9\u30ab\u30e9\u306f\u30a6\u30a9\u30fc\u30bf\u30fc\u30d7\u30eb\u30fc\u30d5\u306b\u306a\u3055\u3063\u305f\u307b\u3046\u304c\u3088\u308d\u3057\u3044\u3093\u3058\u3083\u306a\u3044\u304b\u3057\u3089\u3041\uff1f", "\u3048\u3063\uff01", "\u8000\u7f8e\u3055\u3093", "\u3042\u3063\u3001\u9e97\u83ef\u3055\u3093\u3002\u3069\u3046\u3057\u305f\u306e\uff1f", "\u305a\u3044\u3076\u3093\u71b1\u5fc3\u306b\u805e\u3044\u3066\u3044\u3089\u3063\u3057\u3083\u3044\u307e\u3057\u305f\u308f\u306d\u3002\u30de\u30af\u30ed\u30d3\u306b\u8208\u5473\u304c\u304a\u3042\u308a\u306a\u3093\u3067\u3059\u304b\uff1f", "\u3048\u3048\u3002\u3068\u3066\u3082\u8208\u5473\u6df1\u3044\u304a\u8a71\u3092\u805e\u304b\u305b\u3066\u3044\u305f\u3060\u3051\u305f\u308f\u3002\u79c1\u3001\u4eca\u65e5\u6765\u3066\u826f\u304b\u3063\u305f", "\u8000\u7f8e\u3055\u3093\u3001\u3088\u304b\u3063\u305f\u3089\u304a\u8336\u3067\u3082\u3044\u304b\u304c\u3067\u3059\u304b\uff1f\u79c1\u306b\u3082\u305d\u306e\u304a\u8a71\u3001\u805e\u304b\u305b\u3066\u304f\u3060\u3055\u3044\u306a", "\u79c1\u3067\u3088\u3051\u308c\u3070\u559c\u3093\u3067", "\u5b9f\u306f\u79c1\u3001\u304a\u6599\u7406\u3092\u3059\u308b\u306e\u304c\u597d\u304d\u306a\u306e", "\u305d\u3046\u306a\u3093\u3067\u3059\u304b\uff1f", "\u3048\u3048\u3002\u5927\u5b66\u306e\u5c02\u653b\u3082\u305d\u3061\u3089\u3060\u3057\u3001\u5c06\u6765\u306f\u6599\u7406\u306b\u643a\u308f\u308b\u4ed5\u4e8b\u306b\u5c31\u3051\u305f\u3089\u306a\u3068\u601d\u3063\u3066\u2026", "\u307e\u3041", "\u305d\u308c\u306f\u81ea\u5206\u3067\u304a\u5e97\u3092\u958b\u304d\u305f\u3044\u3068\u3044\u3046\u3053\u3068\u3067\u3059\u304b\uff1f", "\u3046\u3046\u3093\u3002\u79c1\u306b\u305d\u3053\u307e\u3067\u306e\u624d\u899a\u306f\u306a\u3044\u304b\u3089\u2026\u3002\u3067\u3082\u3044\u3064\u304b\u304a\u6599\u7406\u6559\u5ba4\u306e\u5148\u751f\u306b\u306a\u308c\u305f\u3089\u306a\u3063\u3066\u2026", "\u3060\u304b\u3089\u3044\u308d\u3044\u308d\u306a\u304a\u6599\u7406\u3092\u5b66\u3073\u305f\u3044\u306e\u3002\u30de\u30af\u30ed\u30d3\u3082\u305d\u306e\u3072\u3068\u3064\u3002\u4eca\u3082\u3044\u304f\u3064\u304b\u306e\u304a\u6599\u7406\u6559\u5ba4\u306b\u901a\u3063\u3066\u3044\u308b\u306e\u3088\uff1f", "\u305d\u3046\u306a\u3093\u3067\u3059\u304b\uff01", "\u3053\u308c\u3060\u304b\u3089\u79c1\u3001\u592a\u3063\u3061\u3083\u3046\u306e\u306d\u2026", "\u305d\u3093\u306a\u3053\u3068\u3042\u308a\u307e\u305b\u3093\u308f\u3002\u79c1\u3082\u8000\u7f8e\u3055\u3093\u306e\u4f5c\u3063\u305f\u304a\u6599\u7406\u3001\u305c\u3072\u98df\u3079\u3066\u307f\u305f\u3044\u3067\u3059\u308f", "\u672c\u5f53\uff1f\u3067\u3082\u307e\u3060\u307e\u3060\u306a\u306e\u3088\uff1f", "\u9e97\u83ef\u3055\u3093\u3001\u96c5\u54c9\u3055\u3093\u3068\u306f\u304a\u89aa\u3057\u3044\u306e\uff1f", "\u3044\u3048\u3001\u305d\u308c\u307b\u3069\u3067\u306f", "\u3042\u3089\u3001\u5642\u306f\u805e\u3044\u3066\u3044\u307e\u3057\u3066\u3088\uff1f\u9e97\u83ef\u3055\u3093\u306f\u96c5\u54c9\u3055\u3093\u3068\u4ef2\u304c\u3044\u3044\u3063\u3066", "\u7d20\u6575\u3088\u306d\uff5e\u3001\u96c5\u54c9\u3055\u3093\u3002\u79c1\u304c\u3082\u3046\u5c11\u3057\u82e5\u304b\u3063\u305f\u3089", "\u3084\u3060\u308f\u3001\u5965\u69d8\u3063\u305f\u3089\u3002\u3067\u3082\u79c1\u3082", "\u306d\uff5e\u3047\uff01\u3046\u3075\u3075\u3001\u79c1\u9054\u3001\u96c5\u54c9\u69d8\u306e\u30d5\u30a1\u30f3\u306a\u306e", "\u9e97\u83ef\u3055\u3093\u3082\u305d\u3046\u601d\u3046\u3067\u3057\u3087\u3046\uff1f", "\u9e97\u83ef\u3055\u3093\u3068\u96c5\u54c9\u3055\u3093\u3060\u3063\u305f\u3089\u304a\u4f3c\u5408\u3044\u306d"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/133/", "subtitle": "133", "discourse": ["\u304a\u306f\u3088\u3046\u3054\u3056\u3044\u307e\u3059\uff01\u9e97\u83ef\u304a\u5b22\u69d8\uff01\u3055\u3041\uff01\u4eca\u65e5\u304b\u3089\u9811\u5f35\u308a\u307e\u3057\u3087\u3046\uff01", "\u304a\u306f\u3088\u3046\u3054\u3056\u3044\u307e\u3059\u2026\u3002\u3088\u308d\u3057\u304f\u304a\u9858\u3044\u3044\u305f\u3057\u307e\u3059\u2026", "\u4eca\u65e5\u306f\u30bf\u30a4\u30e0\u3092\u7af6\u3063\u3066\u3044\u308b\u308f\u3051\u3067\u306f\u306a\u3044\u306e\u3067\u3001\u81ea\u5206\u306e\u30da\u30fc\u30b9\u3067\u3086\u3063\u304f\u308a\u8d70\u308a\u307e\u3057\u3087\u3046\uff01", "\u306f\u3044", "\u3048\u3063\uff01\u9e97\u83ef\u304a\u5b22\u69d8\u3001\u3069\u3046\u3057\u307e\u3057\u305f\uff1f\uff01", "\u2026\u82e6\u3057\u3044\u3067\u3059", "\u307e\u3060\uff15\uff10\uff10\u30e1\u30fc\u30c8\u30eb\u3082\u8d70\u3063\u3066\u3044\u307e\u305b\u3093\u3088\uff1f\uff01", "\u9e97\u83ef\u304a\u5b22\u69d8\uff01\u305d\u308c\u3058\u3083\u3042\u6b69\u304f\u3088\u308a\u3082\u9045\u3044\u3067\u3059\u3088\uff1f\uff01\u3057\u3063\u304b\u308a\uff01", "\u9e97\u83ef\u304a\u5b22\u69d8\uff01\u3082\u3063\u3068\u8155\u3092\u632f\u3063\u3066\uff01\u307b\u3089\u3001\u3044\u3063\u3061\u306b\uff01\u3044\u3063\u3061\u306b\uff01", "\u9e97\u83ef\u304a\u5b22\u69d8\uff01\u81ea\u5206\u306b\u8ca0\u3051\u308b\u306a\uff01", "\u9e97\u83ef\u304a\u5b22\u69d8\uff01\u307e\u3060\u534a\u5206\u3082\u8d70\u3063\u3066\u307e\u305b\u3093\u3088\uff01\u5143\u6c17\u51fa\u3057\u3066\uff01", "\u307b\u3089\u9854\u3092\u4e0a\u3052\u3066\u3057\u3063\u304b\u308a\u524d\u3092\u5411\u304f\uff01\u306f\u3044\uff01\u306f\u3044\uff01\u306f\u3044\uff01", "\u9e97\u83ef\u304a\u5b22\u69d8\uff01\u30b8\u30e7\u30ae\u30f3\u30b0\u5f8c\u306e\u30b9\u30c8\u30ec\u30c3\u30c1\u304c\u6b8b\u3063\u3066\u3044\u307e\u3059\u3088\uff01", "\u9e97\u83ef\u3001\u5927\u4e08\u592b\u304b\u2026\uff1f", "\u4e09\u539f\u3055\u3093\u3001\u3069\u306e\u304f\u3089\u3044\u8d70\u3063\u305f\u306e\uff1f", "\u7d04\uff13\u30ad\u30ed\u3067\u3059\u304c", "\u3046\uff5e\u3093\u3001\uff13\u30ad\u30ed\u3067\u3053\u308c\u304b\u3041\u2026", "\u3067\u3082\u5927\u4e08\u592b\u3067\u3059\u3088\uff01\u6bce\u65e5\u8d70\u308a\u7d9a\u3051\u308c\u3070\u4f53\u529b\u3082\u3064\u3044\u3066\u304d\u307e\u3059\u304b\u3089\uff01", "\u3042\u3089\u3001\u9e97\u83ef\u3055\u3093\u3002\u8cb4\u5973\u3082\u6765\u305f\u306e", "\u3048\u3048\u3002\u821e\u6d5c\u3055\u3093\u3082\u3044\u3089\u3057\u3066\u305f\u306e\u306d", "\u65ad\u98df\u306b\u7d9a\u304d\u3001\u4eca\u5e74\u306f\u30c7\u30c8\u30c3\u30af\u30b9\uff1f\u8cb4\u5973\u3082\u5fc5\u6b7b\u306d\u3047\u3002\u305d\u3053\u307e\u3067\u3057\u3066\u96c5\u54c9\u69d8\u306e\u304a\u6bcd\u69d8\u306e\u6c17\u3092\u5f15\u304d\u305f\u3044\u306a\u3093\u3066", "\u8aa4\u89e3\u306a\u3055\u3063\u3066\u3044\u308b\u3088\u3046\u3067\u3059\u3051\u3069\u3001\u79c1\u306f\u3042\u304f\u307e\u3067\u3082\u6bcd\u306e\u4ed8\u304d\u6dfb\u3044\u3067\u3059\u308f\u3002\u821e\u6d5c\u3055\u3093\u3053\u305d\u3001\u65ad\u98df\u3092\u3042\u308c\u307b\u3069\u5c0f\u30d0\u30ab\u306b\u306a\u3055\u3063\u3066\u305f\u306e\u306b\u4eca\u5e74\u306f\u53c2\u52a0\u3055\u308c\u308b\u306a\u3093\u3066\u3001\u3069\u3046\u3044\u3063\u305f\u98a8\u306e\u5439\u304d\u56de\u3057\u304b\u3057\u3089\u3002\u5c06\u3092\u5c04\u3093\u3068\u6b32\u3059\u308c\u3070\u307e\u305a\u99ac\u3092\u5c04\u3088\u3002\u3054\u81ea\u5206\u304c\u305d\u3046\u3060\u304b\u3089\u3068\u8a00\u3063\u3066\u3001\u79c1\u307e\u3067\u540c\u3058\u3060\u3068\u601d\u308f\u308c\u308b\u306e\u306f\u5fc3\u5916\u3067\u3059\u308f", "\u306a\u3093\u3067\u3059\u3063\u3066"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/132/", "subtitle": "132", "discourse": ["\u30d9\u30a2\u30c8\u30ea\u30fc\u30c1\u30a7\u306e\u306c\u3044\u3050\u308b\u307f\u304b\u3041\u3002\u7d20\u6674\u3089\u3057\u3044\u767a\u60f3\u3060\u3088\u3001\u5409\u7965\u9662\u3055\u3093\uff01", "\u305d\u304a\uff1f", "\u3046\u3093\uff01\u3042\u306e\u5b50\u306e\u611b\u3089\u3057\u3055\u3092\u306c\u3044\u3050\u308b\u307f\u306b\u3057\u3066\u6c38\u9060\u306b\u7559\u3081\u305f\u3044\u3063\u3066\u3001\u305d\u306e\u6c17\u6301\u3061\u306f\u51c4\u304f\u3088\u304f\u308f\u304b\u308b\u306a\u3041\u3002\u30d9\u30a2\u305f\u3093\u306e\u53ef\u611b\u3055\u306f\u4e16\u754c\u4e00\u3060\u304b\u3089\u306d\uff01\u3057\u304b\u3057\u56f0\u3063\u305f\u306a\u3041\u3001\u3053\u308c\u3067\u745e\u9e1e\u306b\u3082\u30d9\u30a2\u305f\u3093\u30d5\u30a1\u30f3\u304c\u5897\u3048\u3061\u3083\u3046\u304b\u3082\uff1f\uff01", "\u305d\u308c\u3067\u3067\u3059\u306d\u3001\u6885\u82e5\u541b\u3002\u30e2\u30c7\u30eb\u3092\u5f15\u304d\u53d7\u3051\u3066\u3082\u3089\u3046\u306b\u3042\u305f\u3063\u3066\u3001\u30d9\u30a2\u30c8\u30ea\u30fc\u30c1\u30a7\u306e\u30b5\u30a4\u30ba\u3068\u5168\u4f53\u5199\u771f\u3068\u90e8\u5206\u5199\u771f\u304c\u6b32\u3057\u3044\u306e\u3067\u3059\u304c\u3001\u4eca\u5ea6\u6301\u3063\u3066\u304d\u3066\u3044\u305f\u3060\u3051\u307e\u3059\u304b\uff1f", "\u30b5\u30a4\u30ba\uff1f\u30d9\u30a2\u30c8\u30ea\u30fc\u30c1\u30a7\u306e\u4f53\u9577\u306f\uff13\uff12\u30bb\u30f3\u30c1\u3060\u3088\u3002\u4f53\u91cd\u306f\u2026\u3001\u30c0\u30e1\u30c0\u30e1\uff01\u5973\u306e\u5b50\u3060\u304b\u3089\u79d8\u5bc6\u306d\uff01\u30d0\u30e9\u3057\u305f\u3089\u30d9\u30a2\u305f\u3093\u306b\u6012\u3089\u308c\u3061\u3083\u3046\u304b\u3089\uff01", "\u3048\u3048\u2026", "\u4f53\u91cd\u306f\u3088\u308d\u3057\u3044\u3067\u3059\u3051\u3069\u3001\u80f4\u56de\u308a\u3068\u304b\u7d30\u304b\u3044\u30b5\u30a4\u30ba\u306f\u304a\u9858\u3044\u3067\u304d\u307e\u3059\u304b\uff1f\u51fa\u6765\u308c\u3070\u7b49\u8eab\u5927\u3067\u4f5c\u308a\u305f\u3044\u306e\u3067", "\u3048\u3063\uff01\u305d\u3046\u306a\u306e\uff1f\uff01\u7b49\u8eab\u5927\u306e\u30d9\u30a2\u305f\u3093\u306c\u3044\u3050\u308b\u307f\u304b\u3041\u3002\u5b8c\u6210\u3057\u305f\u3089\u3081\u3061\u3083\u304f\u3061\u3083\u53ef\u611b\u3044\u3060\u308d\u3046\u306a\u3041\u3002\u3042\uff01\u3060\u3063\u305f\u3089\u3055\u3001\u5409\u7965\u9662\u3055\u3093\u76f4\u63a5\u6e2c\u308c\u3070\uff1f\u81ea\u5206\u3067\u597d\u304d\u306a\u3068\u3053\u308d\u3092\u6e2c\u3063\u305f\u307b\u3046\u304c\u308f\u304b\u308a\u3084\u3059\u304f\u306a\u3044\uff1f\u305d\u308c\u306b\u672c\u7269\u306b\u4f1a\u3063\u305f\u65b9\u304c\u30a4\u30e1\u30fc\u30b8\u304c\u308f\u304f\u3068\u601d\u3046\u3057", "\u3044\u3044\u3093\u3067\u3059\u304b\uff1f\uff01", "\u3046\u3093\u3002\u4eca\u5ea6\u516c\u5712\u304b\u3069\u3053\u304b\u3067\u4f1a\u3046\u6a5f\u4f1a\u3092\u8a2d\u3051\u308b\u3088\u3002\u305d\u308c\u3067\u3069\u3046\uff1f", "\u305c\u3072\uff01", "\u3067\u306f\u3044\u3064\u306b\u3057\u307e\u3057\u3087\u3046\u304b\u3002\u6885\u82e5\u541b\u306e\u30b9\u30b1\u30b8\u30e5\u30fc\u30eb\u306b\u5408\u308f\u305b\u307e\u3059\u308f\u3002\u5834\u6240\u3068\u6642\u9593\u3092\u6307\u5b9a\u3057\u3066\u304f\u308c\u308c\u3070\u3001\u79c1\u304c\u305d\u3053\u306b\u51fa\u5411\u304d\u307e\u3059", "\u305d\u3046\u3060\u306a\u3041\u2026\u3002\u3042\uff01\u30b5\u30a4\u30ba\u6e2c\u308b\u306e\u3001\u3061\u3087\u3063\u3068\u3060\u3051\u5f85\u3063\u3066\uff01\u5b9f\u306f\u4eca\u30d9\u30a2\u305f\u3093\u3001\u904b\u52d5\u4e0d\u8db3\u3068\u98df\u3079\u3059\u304e\u3067\u5c11\u3057\u592a\u3063\u3061\u3083\u3063\u3066\u308b\u3093\u3060\u3088\u3002\u4eca\u3001\u671d\u5915\u306e\u6563\u6b69\u3068\u30d8\u30eb\u30b7\u30fc\u30e1\u30cb\u30e5\u30fc\u3067\u623b\u3057\u3066\u308b\u6700\u4e2d\u3060\u304b\u3089\u3001\u3082\u3046\u5c11\u3057\u3060\u3051\u6642\u9593\u3061\u3087\u3046\u3060\u3044\uff01", "\u2026\u3082\u3061\u308d\u3093\uff01\u305d\u3046\u3044\u3046\u3053\u3068\u3067\u3057\u305f\u3089\u30d9\u30b9\u30c8\u4f53\u578b\u306b\u623b\u3059\u306e\u3092\u5f85\u3061\u307e\u3059\u308f\u3002\u5973\u306e\u5b50\u305f\u308b\u3082\u306e\u3001\u592a\u3063\u305f\u81ea\u5206\u3092\u5f62\u306b\u6b8b\u3055\u308c\u308b\u306a\u3093\u3066\u3001\u5c48\u8fb1\u4ee5\u5916\u306e\u306a\u306b\u3082\u306e\u3067\u3082\u3042\u308a\u307e\u305b\u3093\u304b\u3089\u306d\uff01", "\u3042\u308a\u304c\u3068\u3046\u3001\u5409\u7965\u9662\u3055\u3093\uff01\u5fc5\u305a\u6700\u9ad8\u306b\u53ef\u611b\u3044\u72b6\u614b\u306e\u30d9\u30a2\u30c8\u30ea\u30fc\u30c1\u30a7\u306b\u4f1a\u308f\u305b\u308b\u3053\u3068\u3092\u7d04\u675f\u3059\u308b\u3088\uff01", "\u9e97\u83ef\u304a\u5b22\u69d8\uff01\u79c1\u306f\u5b66\u751f\u6642\u4ee3\u306b\u9678\u4e0a\u3092\u7d4c\u9a13\u3057\u3066\u3044\u308b\u306e\u3067\u3001\u30d5\u30a9\u30fc\u30e0\u306e\u6307\u5c0e\u3082\u3057\u3063\u304b\u308a\u30b3\u30fc\u30c1\u3055\u305b\u3066\u3044\u305f\u3060\u304d\u307e\u3059\u304b\u3089\u3054\u5b89\u5fc3\u304f\u3060\u3055\u3044\uff01\u4e00\u7dd2\u306b\u8a18\u9332\u3092\u4f38\u3070\u305b\u308b\u3088\u3046\u306b\u9811\u5f35\u308a\u307e\u3057\u3087\u3046\uff01\u521d\u65e5\u306f\u8efd\u304f\uff13\u30ad\u30ed\u8d70\u308b\u3068\u3053\u308d\u304b\u3089\u59cb\u3081\u307e\u3057\u3087\u3046\u306d\uff01", "\u30b8\u30e7\u30ae\u30f3\u30b0\u3058\u3083\u306a\u304f\u3066\u30e9\u30b8\u30aa\u4f53\u64cd\u306b\u2026", "\u308f\u304b\u308a\u307e\u3057\u305f\uff01\u30b8\u30e7\u30ae\u30f3\u30b0\u306e\u307b\u304b\u306b\u30e9\u30b8\u30aa\u4f53\u64cd\u3082\u52a0\u3048\u307e\u3057\u3087\u3046\uff01"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/131/", "subtitle": "131", "discourse": ["\u3082\u3046\u6674\u6597\u5144\u69d8\u3068\u306f\u4e00\u7dd2\u306b\u98df\u4e8b\u306b\u884c\u304b\u306a\u3044\uff01", "\u6674\u6597\u5144\u69d8\u306e\u98df\u3079\u6b69\u304d\u306e\u8da3\u5473\u306b\u306f\u30a8\u30ea\u30ab\u3055\u3093\u304c\u4ed8\u304d\u5408\u3048\u3070\u3044\u3044\u306e\u3088\u3002\u3060\u3063\u3066\u5f7c\u5973\u306a\u3093\u3060\u304b\u3089\u3002\u305d\u3057\u3066\u592a\u3063\u3061\u3083\u3048\u2026", "\u6674\u6597\u5144\u69d8\u304c\u3044\u306a\u304f\u3066\u3082\u3001\u9e97\u83ef\u304a\u59c9\u69d8\u3001\u79c1\u3068\u4e00\u7dd2\u306b\u51fa\u639b\u3051\u3066\u304f\u308c\u307e\u3059\u304b\uff1f", "\u9ebb\u592e\u304c\u3082\u3046\u4e00\u7dd2\u306b\u3054\u306f\u3093\u98df\u3079\u306a\u3044\u3063\u3066\u6012\u3063\u3066\u308b\u3093\u3060\u3051\u3069\u2026", "\u3042\u3089\u3041", "\u3069\u3046\u3057\u305f\u3089\u3044\u3044\u304b\u306a\u3002\u9e97\u83ef\u3055\u3093\u3001\u9ebb\u592e\u3092\u8aac\u5f97\u3057\u3066\u304f\u308c\u306a\u3044\u304b\uff1f", "\u7533\u3057\u8a33\u3042\u308a\u307e\u305b\u3093\u3051\u3069\u79c1\u306f\u5e02\u4e4b\u5009\u69d8\u3088\u308a\u3082\u9ebb\u592e\u3061\u3083\u3093\u304c\u5927\u4e8b\u3067\u3059\u304b\u3089\u3001\u9ebb\u592e\u3061\u3083\u3093\u306e\u5473\u65b9\u306b\u306a\u308a\u307e\u3059\u308f", "\u9e97\u83ef\u3055\u3093\u2026", "\u5927\u4e08\u592b\u3067\u3059\u308f\u3002\u307b\u3068\u307c\u308a\u304c\u51b7\u3081\u308c\u3070\u9ebb\u592e\u3061\u3083\u3093\u3082\u8a31\u3057\u3066\u304f\u308c\u307e\u3059\u308f\u3088\u3002\u9ebb\u592e\u3061\u3083\u3093\u306f\u5e02\u4e4b\u5009\u69d8\u304c\u5927\u597d\u304d\u3067\u3059\u304b\u3089\u306d", "\u305d\u3046\u304b\u306a\u2026\u3002\u9ebb\u592e\u306b\u4f1a\u3044\u306b\u884c\u3063\u305f\u3089\u3001\u6674\u6597\u5144\u69d8\u306f\u304a\u6bcd\u69d8\u306e\u5f1f\u3060\u304b\u3089\u4eca\u65e5\u304b\u3089\u53d4\u7236\u3055\u3093\u3068\u547c\u3076\uff01\u3063\u3066\u8a00\u308f\u308c\u3061\u3083\u3063\u3066\u3001\u50d5\u3082\u3046\u30b7\u30e7\u30c3\u30af\u3067\u2026", "\u3084\u3063\u3071\u308a\u304a\u90aa\u9b54\u3058\u3083\u306a\u3044\u304b\u3057\u3089\u3002\u307b\u304b\u306e\u65b9\u3005\u304b\u3089\u8ff7\u60d1\u3060\u3068\u601d\u308f\u308c\u306a\u3044\u304b\u3057\u3089\u2026", "\u5e73\u6c17\u3067\u3059\u308f\u3002\u540c\u3058\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u306e\u30e1\u30f3\u30d0\u30fc\u306a\u306e\u3067\u3059\u304b\u3089\u3002\u305d\u308c\u306b\u524d\u306b\u3082\u6765\u305f\u3053\u3068\u304c\u3042\u308a\u307e\u305b\u3093\u3067\u3057\u305f\uff1f", "\u305d\u3046\u3067\u3059\u3051\u3069\u2026", "\u307e\u3041\uff01\u53ef\u611b\u3089\u3057\u3044\uff01\u30d7\u30c6\u30a3\u306e\u5b50\u9054\u306d\uff1f\u79c1\u306f\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u306e\u73fe\u4f1a\u9577\u306e\u6c96\u5cf6\u7464\u5b50\u3067\u3059\u308f\u3002\u3069\u3046\u305e\u3088\u308d\u3057\u304f", "\u306f\u3044\u3063\u3001\u3088\u308d\u3057\u304f\u304a\u9858\u3044\u3044\u305f\u3057\u307e\u3059\uff01", "\u672c\u5f53\u306b\u53ef\u611b\u3044\u308f\u306d\u3047", "\u3053\u3093\u306b\u3061\u306f\u3002\u30d7\u30c6\u30a3\u306e\u5b50\u9054\u304b\u306a\uff1f", "\u306f\u3044\u3063", "\u3058\u3083\u3042\u50d5\u306e\u5f1f\u306f\u77e5\u3063\u3066\u3044\u308b\u304b\u306a\uff1f\u5186\u57ce\u96ea\u91ce\u3068\u3044\u3046\u3093\u3060\u3051\u3069", "\u306f\u3044\uff01\u96ea\u91ce\u541b\u3068\u306f\u30d7\u30c6\u30a3\u3067\u3088\u304f\u304a\u3057\u3083\u3079\u308a\u3092\u3057\u3066\u3044\u307e\u3059\uff01", "\u305d\u3046\u3002\u4ef2\u826f\u304f\u3057\u3066\u304f\u308c\u3066\u3042\u308a\u304c\u3068\u3046\u3002\u3053\u308c\u304b\u3089\u3082\u5f1f\u306e\u3053\u3068\u3088\u308d\u3057\u304f\u306d\uff1f", "\u306f\u3044\u3063\uff01", "\u96ea\u91ce\u306f\u30d7\u30c6\u30a3\u3067\u306f\u3069\u3093\u306a\u69d8\u5b50\u304b\u306a", "\u3048\u3063\u3068\u2026", "\u3078\u3047\u3001\u96ea\u91ce\u306e\u53cb\u9054\uff1f", "\u96ea\u91ce\u306b\u8ff7\u60d1\u3092\u304b\u3051\u3089\u308c\u305f\u3089\u3001\u3044\u3064\u3067\u3082\u8a00\u3044\u306b\u6765\u3044", "\u306a\u3093\u3060\u304a\u524d\u3001\u59ac\u3044\u3066\u308b\u306e\u304b\uff1f", "\u306a\u3093\u3060\u3088\u304a\u524d\u30fc\u3001\u3057\u3063\u304b\u308a\u3057\u308d\u3088\u30fc\uff01\u79c0\u4ecb\u306a\u3093\u304b\u306b\u53d6\u3089\u308c\u3066\u308b\u3093\u3058\u3083\u306d\u30fc\u3088\uff01", "\u305d\u3093\u306a\u3093\u3058\u3083\u306a\u3044\u3067\u3059\uff01", "\u3088\u3057\uff01\u4ffa\u304c\u304a\u524d\u306e\u604b\u306e\u60a9\u307f\u3092\u805e\u3044\u3066\u3084\u308d\u3046", "\u305c\u3072\u307e\u305f\u3044\u3089\u3057\u3066\uff01", "\u5922\u306e\u3088\u3046\u306a\u6642\u9593\u3067\u3057\u305f\u2026", "\u6c34\u65cf\u9928\u3068\u52d5\u7269\u5712\u3001\u3069\u3063\u3061\u304c\u3044\u3044\uff1f", "\u6c34\u65cf\u9928", "\u4eca\u56de\u306f\u3042\u307e\u308a\u30c0\u30e1\u30fc\u30b8\u53d7\u3051\u3066\u3044\u306a\u3044\u304b\u3089\u65e5\u5e30\u308a\u304b\u306a\u3041\u2026"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/130/", "subtitle": "130", "discourse": ["\u306a\u306b\u304b\u3054\u7528\u3067\u3057\u3087\u3046\u304b\uff1f\u304a\u7236\u69d8", "\u2026\u9e97\u83ef\u3061\u3083\u3093\u3001\u306a\u306b\u304b\u3042\u3063\u305f\u306e\u304b\u306a\uff1f\u304a\u7236\u69d8\u306b\u8a71\u3057\u3066\u3054\u3089\u3093\u3002\u304a\u7236\u69d8\u3001\u53ef\u611b\u3044\u9e97\u83ef\u3061\u3083\u3093\u306e\u305f\u3081\u306a\u3089\u306a\u3093\u3060\u3063\u3066\u3057\u3066\u3042\u3052\u308b\u304b\u3089\u306d\uff1f\u3055\u3041\uff01\u60a9\u307f\u304c\u3042\u308b\u306a\u3089\u304a\u7236\u69d8\u306b\uff01", "\u306f\uff1f", "\u9e97\u83ef\u3055\u3093\u3001\u90e8\u5c4b\u3067\u306f\u9759\u304b\u306b\u306a\u3055\u3063\u3066\u3002\u306a\u306b\u3084\u3089\u7363\u306e\u3088\u3046\u306a\u58f0\u304c\u805e\u3053\u3048\u3066\u304d\u307e\u3057\u305f\u3088", "\u3042\u3063\u3001\u7533\u3057\u8a33\u3042\u308a\u307e\u305b\u3093", "\u306d\u3047\u3001\u304a\u6bcd\u69d8\u3001\u79c1\u592a\u3063\u305f\u3068\u601d\u3044\u307e\u305b\u3093\u304b\uff1f", "\u9e97\u83ef\u3055\u3093\u3082\u6c17\u304c\u4ed8\u3044\u305f\u306e\u306d\u2026", "\u304a\u6bcd\u69d8\uff01\u306a\u305c\u3082\u3063\u3068\u65e9\u304f\u8a00\u3063\u3066\u304f\u308c\u306a\u304b\u3063\u305f\u306e\u3067\u3059\u304b\uff01", "\u3054\u3081\u3093\u306a\u3055\u3044\u306d\u3002\u9e97\u83ef\u3055\u3093\u3002\u3067\u3082\u304a\u6bcd\u69d8\u8a00\u3048\u306a\u304b\u3063\u305f\u2026", "\u304a\u6bcd\u69d8\u2026", "\u304a\u6bcd\u69d8\u306f\u3044\u3064\u304b\u3089\u6c17\u304c\u4ed8\u3044\u3066\u3044\u305f\u3093\u3067\u3059\u304b\uff1f", "\u305d\u3046\u306d\u3047\u3001\u3053\u3053\uff11\u3001\uff12\u30f6\u6708\u3068\u3044\u3063\u305f\u3068\u3053\u308d\u304b\u3057\u3089\u2026", "\u305d\u3046\u3067\u3057\u305f\u304b\u2026", "\u3067\u3082\u306d\u3001\u9e97\u83ef\u3055\u3093\u3001\u3068\u3063\u3066\u3082\u3044\u3044\u304a\u8a71\u304c\u3042\u308b\u306e\u3088\uff01", "\u3053\u308c\u306f\u2026", "\u53bb\u5e74\u4e00\u7dd2\u306b\u65ad\u98df\u306b\u884c\u3063\u305f\u3067\u3057\u3087\u3046\uff1f\u4eca\u5e74\u3082\u304a\u8a98\u3044\u3092\u53d7\u3051\u305f\u306e\u3088\u3002\u3067\u3082\u4eca\u5e74\u306f\u65ad\u98df\u3067\u306f\u306a\u304f\u3066\u3001\u30c7\u30c8\u30c3\u30af\u30b9\u306a\u3093\u3067\u3059\u3063\u3066\u3002\u30a8\u30b9\u30c6\u3092\u53d7\u3051\u3066\u30de\u30af\u30ed\u30d3\u98df\u3092\u98df\u3079\u3066\u904b\u52d5\u3059\u308b\u3093\u3067\u3059\u3063\u3066\u3002\u306d\u3001\u9e97\u83ef\u3055\u3093\u3001\u304a\u6bcd\u69d8\u3068\u4e00\u7dd2\u306b\u884c\u304d\u307e\u3057\u3087\u3046\uff1f", "\u304a\u6bcd\u69d8\u2026", "\u304a\u8a00\u8449\u3067\u3059\u304c\u3001\u79c1\u306e\u8d05\u8089\u306f\u3053\u306e\u7a0b\u5ea6\u3067\u306f\u843d\u3061\u308b\u3068\u306f\u601d\u3048\u307e\u305b\u3093\u308f", "\u9e97\u83ef\u3055\u3093\uff01\u304a\u9858\u3044\u3088\u3001\u4e00\u7dd2\u306b\u884c\u3063\u3066\uff1f\u53bb\u5e74\u3060\u3063\u3066\u304a\u6bcd\u69d8\u3001\u3072\u3068\u308a\u3060\u3063\u305f\u3089\u3064\u3089\u304f\u3066\u8010\u3048\u3089\u308c\u306a\u304b\u3063\u305f\u3082\u306e\u3002\u306d\u3047\uff1f\u304a\u9858\u3044\u3001\u9e97\u83ef\u3055\u3093\uff01", "\u30a4\u30e4\u3067\u3059", "\u93d1\u6728\u5bb6\u306e\u5965\u69d8\u306b\u3082\u305c\u3072\u9e97\u83ef\u3055\u3093\u306b\u4e00\u7dd2\u306b\u53c2\u52a0\u3057\u3066\u6b32\u3057\u3044\u3063\u3066\u3001\u3053\u306e\u524d\u30d1\u30fc\u30c6\u30a3\u30fc\u3067\u304a\u4f1a\u3044\u3057\u305f\u6642\u3082\u8a00\u308f\u308c\u3066\u3057\u307e\u3063\u305f\u306e\u3088\u3002\u3060\u304b\u3089\u9e97\u83ef\u3055\u3093\u3001\u8cb4\u5973\u3082\u884c\u3063\u3066\u304f\u308c\u308b\u308f\u306d\uff1f", "\u3048\uff5e\u3063", "\u3060\u3063\u305f\u3089\u9e97\u83ef\u3055\u3093\uff01\u8cb4\u5973\u3053\u306e\u307e\u307e\u305a\u3063\u3068\u592a\u3063\u305f\u307e\u307e\u3067\u3044\u308b\u3068\u8a00\u3046\u306e\uff01\u590f\u306a\u306e\u306b\uff01\u8584\u7740\u306e\u5b63\u7bc0\u306a\u306e\u306b\uff01\u3053\u3093\u306a\u306b\u305f\u304f\u307e\u3057\u3044\u30a6\u30a8\u30b9\u30c8\u306b\u306a\u3063\u3061\u3083\u3063\u3066\uff01", "\u75db\u3044\u3063\uff01\u304a\u6bcd\u69d8\u3001\u75db\u3044\u3067\u3059\u3063\uff01", "\u9e97\u83ef\u3055\u3093\u306b\u306f\u7d76\u5bfe\u306b\u884c\u3063\u3066\u3082\u3089\u3044\u307e\u3059\u304b\u3089\u306d\uff01\u62d2\u5426\u306f\u8a8d\u3081\u307e\u305b\u3093\u3063\uff01", "\u9e97\u83ef\u3055\u3093\u3001\u8cb4\u5973\u592a\u308a\u307e\u3057\u305f\u308f\u3088\uff01", "\u5409\u7965\u9662\u3055\u3093\u3001\u3053\u3063\u3061\u534a\u5206\u6301\u3063\u3066\u304f\u308c\u308b\uff1f", "\u306f\uff5e\u3044", "\u79c1\u306b\u306a\u306b\u304b\uff1f", "\u2026\u524d\u306b\u3001\u6c7a\u3081\u3064\u3051\u308b\u3088\u3046\u306a\u3053\u3068\u3092\u8a00\u3063\u3066\u60aa\u304b\u3063\u305f", "\u306f\u3044\uff1f", "\u53cb\u67c4\u5148\u8f29\u304b\u3089\u3082\u4e00\u65b9\u7684\u306a\u6c7a\u3081\u3064\u3051\u306f\u826f\u304f\u306a\u3044\u3068\u6012\u3089\u308c\u305f", "\u3048\u3063\u3001\u53cb\u67c4\u5148\u8f29\uff1f\uff01", "\u3053\u306e\u524d\u53cb\u67c4\u5148\u8f29\u306b\u4f1a\u3063\u3066\u3001\u5409\u7965\u9662\u306e\u8a71\u304c\u51fa\u305f\u6642\u3001\u305d\u3046\u8a00\u308f\u308c\u305f\u2026", "\u5409\u7965\u9662\u3082\u4ffa\u306b\u306a\u306b\u304b\u8a00\u3044\u305f\u3044\u3053\u3068\u304c\u3042\u3063\u305f\u3089\u8a00\u3063\u3066\u3044\u3044\u304b\u3089", "\u6c34\u5d0e\u541b\u306b\u306f\u7279\u306b\u3042\u308a\u307e\u305b\u3093\u308f\u306d", "\u5409\u7965\u9662\u3055\u3093\u3001\u6c34\u5d0e\u541b\u3068\u306a\u306b\u304b\u3042\u3063\u305f\u306e\uff1f", "\u3055\u3041\u3002\u3088\u304f\u308f\u304b\u308a\u307e\u305b\u3093\u3051\u3069\u2026\u3002\u307e\u3041\u60aa\u3044\u3053\u3068\u3067\u306f\u306a\u3055\u305d\u3046\u3067\u3059\u304b\u3089\u3001\u3088\u308d\u3057\u3044\u3093\u3058\u3083\u306a\u3044\u304b\u3057\u3089", "\u305d\u3046\u306a\u3093\u3060", "\u590f\u4f11\u307f\u306e\u88dc\u7fd2\u306b\u672c\u7530\u3055\u3093\u9054\u3082\u53c2\u52a0\u3059\u308b\u3089\u3057\u3044\u304b\u3089\u3001\u50d5\u3082\u53c2\u52a0\u3057\u3088\u3046\u304b\u3068\u601d\u3046\u3093\u3060", "\u5e2b\u5320\u3001\u81ea\u5206\u304c\u6301\u3061\u307e\u3059\uff01", "\u5e2b\u5320\u3001\u3042\u3068\u3067\u76f8\u8ac7\u304c\u2026"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/129/", "subtitle": "129", "discourse": ["\u5f7c\u5973\u306f\u50d5\u306e\u5927\u5b66\u6642\u4ee3\u306e\u540c\u7d1a\u751f\u3067\u3001\u5f13\u5c71\u30a8\u30ea\u30ab\u3055\u3093", "\u3053\u3093\u306b\u3061\u306f", "\u30a8\u30ea\u30ab\u3001\u30aa\u30fc\u30c0\u30fc\u306f\uff1f", "\u79c1\u306f\u30b5\u30e9\u30c0\u3060\u3051\u3067\u3044\u3044\u308f\u3002\u3082\u3046\u3059\u3050\u64ae\u5f71\u304c\u3042\u308b\u304b\u3089", "\u6674\u6597\u304b\u3089\u3075\u305f\u308a\u306e\u8a71\u306f\u3044\u3064\u3082\u805e\u3044\u3066\u3044\u305f\u306e\u3088", "\u3068\u3063\u3066\u3082\u53ef\u611b\u304c\u3063\u3066\u3044\u308b\u59ea\u3063\u5b50\u3061\u3083\u3093\u3068\u305d\u306e\u304a\u53cb\u9054\u3063\u3066\u3002\u6674\u6597\u304c\u8a00\u3063\u3066\u305f\u901a\u308a\u3001\u672c\u5f53\u306b\u304a\u4eba\u5f62\u3055\u3093\u307f\u305f\u3044\u306b\u53ef\u611b\u3044\u3075\u305f\u308a\u306d\uff5e\uff01", "\u3067\u3057\u3087\uff1f\u9ebb\u592e\u3082\u9e97\u83ef\u3055\u3093\u3082\u59b9\u307f\u305f\u3044\u306b\u53ef\u611b\u3044\u3093\u3060\u3002\u3053\u306e\u5b50\u9054\u3068\u4e00\u7dd2\u306b\u3054\u306f\u3093\u3092\u98df\u3079\u3066\u3044\u308b\u3068\u3001\u30b9\u30c8\u30ec\u30b9\u3082\u5fd8\u308c\u3061\u3083\u3046\u3093\u3060\u3088", "\u6674\u6597\u306f\u98df\u9053\u697d\u3060\u3082\u306e\u306d\uff5e", "\u9ebb\u592e\u3061\u3083\u3093\u306e\u8a71\u306f\u524d\u304b\u3089\u805e\u3044\u3066\u3044\u305f\u3093\u3060\u3051\u3069\u3001\u6700\u8fd1\u65b0\u3057\u304f\u9e97\u83ef\u3055\u3093\u3066\u3044\u3046\u53ef\u611b\u3044\u5973\u306e\u5b50\u3068\u4ef2\u826f\u304f\u306a\u3063\u305f\u3063\u3066\u805e\u3044\u3066\u3001\u305c\u3072\u4f1a\u3044\u305f\u304f\u306a\u3063\u3061\u3083\u3063\u305f\u306e", "\u3042\u306e\u3001\u604b\u4eba\u306e\u3044\u308b\u304b\u305f\u3068\u983b\u7e41\u306b\u98df\u4e8b\u3092\u3057\u3066\u7533\u3057\u8a33\u3042\u308a\u307e\u305b\u3093\u3067\u3057\u305f\u3002\u79c1\u3001\u306a\u306b\u3082\u5b58\u3058\u4e0a\u3052\u3066\u3044\u306a\u304b\u3063\u305f\u3082\u306e\u3067\u3059\u304b\u3089\u2026\u3002\u3054\u4e0d\u5feb\u306a\u601d\u3044\u3092\u3055\u305b\u3066\u3044\u305f\u3089\u304a\u8a6b\u3073\u3044\u305f\u3057\u307e\u3059\u308f", "\u3072\u3068\u306e\u5f7c\u6c0f\u3068\u904a\u3073\u6b69\u3044\u3066\u308b\u3093\u3058\u3083\u306a\u3044\u308f\u3088", "\u3084\u3060\u3001\u8aa4\u89e3\u3057\u306a\u3044\u3067\uff01\u79c1\u306f\u5168\u7136\u4e0d\u6109\u5feb\u306a\u601d\u3044\u306a\u3093\u3066\u3057\u3066\u306a\u3044\u3093\u3060\u304b\u3089\uff01\u3080\u3057\u308d\u5927\u98df\u3044\u306e\u6674\u6597\u306b\u4ed8\u304d\u5408\u3063\u3066\u98df\u3079\u3066\u304f\u308c\u308b\u5b50\u304c\u3044\u3066\u3001\u559c\u3093\u3067\u3044\u308b\u304f\u3089\u3044\u3088\uff1f", "\u6674\u6597\u306f\u77e5\u3063\u3066\u306e\u901a\u308a\u98df\u9053\u697d\u3067\u3057\u3087\u3046\uff1f\u79c1\u3082\u306a\u308b\u3079\u304f\u305d\u308c\u306b\u4ed8\u304d\u5408\u3063\u3066\u3042\u3052\u305f\u3044\u3068\u306f\u601d\u3063\u3066\u308b\u3093\u3060\u3051\u3069\u3001\u306a\u304b\u306a\u304b\u305d\u3046\u3082\u3044\u304b\u306a\u304f\u3066\u3002\u305d\u3046\u3057\u305f\u3089\u6700\u8fd1\u3001\u9ebb\u592e\u3061\u3083\u3093\u3068\u9e97\u83ef\u3055\u3093\u304c\u305d\u308c\u306b\u4ed8\u304d\u5408\u3063\u3066\u304f\u308c\u3066\u308b\u3063\u3066\u805e\u3044\u3066\u3001\u826f\u304b\u3063\u305f\u306a\uff5e\u3063\u3066\u601d\u3063\u3066\u305f\u306e\u3002\u6674\u6597\u3082\u306d\u3001\u305f\u304f\u3055\u3093\u98df\u3079\u3066\u304f\u308c\u308b\u5b50\u3068\u4e00\u7dd2\u306b\u98df\u4e8b\u3092\u3059\u308b\u306e\u306f\u697d\u3057\u3044\u3063\u3066\u8a00\u3063\u3066\u305f\u3057", "\u30a8\u30ea\u30ab\u306f\u98df\u304c\u7d30\u3044\u304b\u3089\u306d\u3002\u7121\u7406\u3057\u3066\u4ed8\u304d\u5408\u3063\u3066\u304f\u308c\u306a\u304f\u3066\u3082\u3044\u3044\u3088\u3002\u9e97\u83ef\u3055\u3093\u3082\u6700\u521d\u306b\u4f1a\u3063\u305f\u6642\u306f\u98f2\u307f\u7269\u3057\u304b\u98f2\u307e\u306a\u3044\u3067\u4e00\u5207\u98df\u3079\u7269\u306b\u624b\u3092\u3064\u3051\u306a\u3044\u304b\u3089\u5fc3\u914d\u3057\u305f\u3093\u3060\u3051\u3069\u3001\u9ebb\u592e\u306e\u8a95\u751f\u65e5\u4f1a\u3067\u4f1a\u3063\u305f\u6642\u306f\u3001\u51c4\u304f\u304a\u3044\u3057\u305d\u3046\u306b\u98df\u4e8b\u3092\u3057\u3066\u3066\u30db\u30c3\u3068\u3057\u305f\u3093\u3060\u3002\u30a8\u30ea\u30ab\u306f\u5143\u3005\u98df\u304c\u7d30\u3044\u3057\u4ed5\u4e8b\u67c4\u3042\u307e\u308a\u98df\u3079\u3089\u308c\u306a\u3044\u3051\u3069\u3001\u3075\u305f\u308a\u306f\u6210\u9577\u671f\u306a\u3093\u3060\u304b\u3089\u3057\u3063\u304b\u308a\u98df\u3079\u306a\u3044\u3068\u30c0\u30e1\u3060\u3088\u3002\u3042\u3001\u30c7\u30b6\u30fc\u30c8\u306f\u306a\u306b\u304c\u3044\u3044\uff1f", "\u65e5\u5e30\u308a\u3067\u53f0\u6e7e\u3063\u3066\u75b2\u308c\u305f\u3067\u3057\u3087\u3046\u3002\u5c0f\u9f8d\u5305\u304a\u3044\u3057\u304b\u3063\u305f\u3088\u3046\u3067\u826f\u304b\u3063\u305f\u308f\u3002\u79c1\u3082\u6674\u6597\u304b\u3089\u304a\u8336\u3092\u304a\u571f\u7523\u306b\u3082\u3089\u3063\u3066\u6bce\u65e5\u98f2\u3093\u3067\u308b\u306e", "\u3055\u3066\u3001\u3053\u308c\u304b\u3089\u3069\u3046\u3057\u3088\u3046\u304b\uff1f\u4eca\u65e5\u306f\u30a8\u30ea\u30ab\u3082\u3075\u305f\u308a\u306e\u597d\u304d\u306a\u5834\u6240\u306b\u4ed8\u304d\u5408\u3046\u3063\u3066\u8a00\u3063\u3066\u308b\u3057", "\u3048\u3048\u3002\u30b7\u30e7\u30c3\u30d4\u30f3\u30b0\u306b\u884c\u304b\u306a\u3044\uff1f\u79c1\u3001\u3075\u305f\u308a\u306b\u4f3c\u5408\u3046\u6d0b\u670d\u3092\u9078\u3093\u3067\u3042\u3052\u305f\u3044\u308f\u3002\u3042\u3001\u305d\u308c\u3068\u3082\u307b\u304b\u306b\u4f55\u304b\u98df\u3079\u306b\u884c\u304f\uff1f", "\u3044\u3048\u3001\u79c1\u306f\u3053\u308c\u3067\u5931\u793c\u3044\u305f\u3057\u307e\u3059\u308f\u3002\u304a\u3075\u305f\u308a\u306e\u30c7\u30fc\u30c8\u306e\u304a\u90aa\u9b54\u306f\u3067\u304d\u307e\u305b\u3093\u3082\u306e", "\u79c1\u3082\uff01\u304a\u306a\u304b\u3044\u3063\u3071\u3044\u306b\u306a\u3063\u305f\u304b\u3089\u3082\u3046\u5e30\u308b\u308f\uff01", "\u3048\u3063\u3001\u305d\u3093\u306a\u3053\u3068\u6c17\u306b\u3057\u306a\u304f\u3066\u3044\u3044\u306e\u306b", "\u305d\u3046\u3088\u3001\u305b\u3063\u304b\u304f\u4f1a\u3048\u305f\u306e\u306b\u3082\u3046\u89e3\u6563\u306a\u3093\u3066\u5bc2\u3057\u3044\u308f", "\u3067\u3082\u3053\u308c\u4ee5\u4e0a\u306f\u99ac\u306b\u8e74\u3089\u308c\u3066\u3057\u307e\u3044\u307e\u3059\u3082\u306e\u306d", "\u305d\u3046\u3067\u3059\u3088\u306d\u3001\u9e97\u83ef\u304a\u59c9\u69d8\u3002\u6674\u6597\u5144\u69d8\u3053\u305d\u9060\u616e\u3057\u306a\u3044\u3067\u3001\u3069\u3046\u305e\u30c7\u30fc\u30c8\u3092\u697d\u3057\u3093\u3067\u304d\u3066\uff01", "\u3058\u3083\u3042\u3001\u305b\u3081\u3066\u9001\u308b\u3088", "\u5e73\u6c17\u3067\u3059\u308f\u3002\u5bb6\u306e\u8eca\u304c\u8fce\u3048\u306b\u6765\u307e\u3059\u304b\u3089\u3002\u3069\u3046\u305e\u304a\u6c17\u9063\u3044\u306a\u304f", "\u9e97\u83ef\u304a\u59c9\u69d8\u3001\u3060\u3063\u305f\u3089\u79c1\u3082\u4e57\u305b\u3066\u884c\u3063\u3066\u3044\u305f\u3060\u3051\u307e\u3059\u304b\uff1f", "\u3082\u3061\u308d\u3093\u3088\u3001\u9ebb\u592e\u3061\u3083\u3093", "\u3046\uff5e\u3093\u3001\u672c\u5f53\u306b\u5e73\u6c17\uff1f", "\u307e\u305f\u4eca\u5ea6\u7d76\u5bfe\u306b\u4f1a\u3044\u307e\u3057\u3087\u3046\u306d\uff1f\u9ebb\u592e\u3061\u3083\u3093\u3001\u79c1\u306e\u3053\u3068\u3082\u304a\u59c9\u3055\u3093\u3063\u3066\u547c\u3093\u3067\u304f\u308c\u308b\u3068\u5b09\u3057\u3044\u306a", "\u2026\u9ebb\u592e\u3061\u3083\u3093\u3001\u3069\u3053\u304b\u5165\u308a\u307e\u3057\u3087\u3046\u304b", "\u2026\u306f\u3044", "\u306a\u306b\u3042\u308c\uff01\u6674\u6597\u5144\u69d8\u306e\u5f7c\u5973\u3001\u30ac\u30ea\u30ac\u30ea\u3058\u3083\u306a\u3044\uff01", "\u30e2\u30c7\u30eb\u3055\u3093\u3067\u3059\u304b\u3089\u306d\u2026", "\u4fe1\u3058\u3089\u308c\u306a\u3044\uff01\u75e9\u305b\u3066\u3044\u308b\u5b50\u3088\u308a\u307d\u3063\u3061\u3083\u308a\u3057\u3066\u3044\u308b\u307b\u3046\u304c\u53ef\u611b\u3044\u306a\u3093\u3066\u6563\u3005\u8a00\u3063\u3066\u304a\u3044\u3066\u3001\u9078\u3093\u3060\u5f7c\u5973\u306f\u30ac\u30ea\u30ac\u30ea\u306e\u30e2\u30c7\u30eb\uff1f\uff01\u3042\u308a\u3048\u306a\u3044\u308f\uff01", "\u2026\u30b5\u30e9\u30c0\u3057\u304b\u98df\u3079\u307e\u305b\u3093\u3067\u3057\u305f\u308f\u306d", "\u305d\u3046\uff01\u79c1\u9054\u304c\u305f\u304f\u3055\u3093\u98df\u3079\u3066\u3044\u308b\u306e\u306b\u3072\u3068\u308a\u3060\u3051\u30b5\u30e9\u30c0\u3060\u3051\u3063\u3066\uff01\u307e\u308b\u3067\u3001\u3088\u304f\u98df\u3079\u308b\u308f\u306d\u3063\u3066\u30d0\u30ab\u306b\u3055\u308c\u3066\u3044\u308b\u307f\u305f\u3044\u306b\u601d\u3048\u305f\u308f\uff01", "\u79c1\u3001\u6674\u6597\u5144\u69d8\u304c\u9078\u3076\u4eba\u306f\u3001\u304d\u3063\u3068\u98df\u3079\u308b\u3053\u3068\u304c\u5927\u597d\u304d\u3067\u3001\u304a\u6599\u7406\u4e0a\u624b\u306a\u307d\u3063\u3061\u3083\u308a\u3057\u305f\u4eba\u3060\u3068\u601d\u3063\u3066\u3044\u307e\u3057\u305f\u3002\u306a\u306e\u306b\u3042\u306e\u4eba\u3063\u305f\u3089\u3001\u30b5\u30e9\u30c0\u3057\u304b\u98df\u3079\u306a\u3044\u30c0\u30a4\u30a8\u30c3\u30bf\u30fc\u3058\u3083\u306a\u3044\uff01", "\u2026\u2026\u79c1\u3001\u592a\u308a\u307e\u3057\u305f", "\u3048\u3063\uff01", "\u6674\u6597\u5144\u69d8\u3068\u9e97\u83ef\u304a\u59c9\u69d8\u306b\u306f\u3042\u3042\u8a00\u308f\u308c\u307e\u3057\u305f\u3051\u3069\u3001\u3084\u3063\u3071\u308a\u60a0\u7406\u306b\u8a00\u308f\u308c\u305f\u3053\u3068\u304c\u6c17\u306b\u306a\u3063\u3066\u2026\u3002\u4f53\u91cd\u8a08\u306b\u4e57\u3063\u305f\u3089\u592a\u3063\u3066\u305f\u3093\u3067\u3059\u3002\u3067\u3082\u304a\u6bcd\u69d8\u306b\u8a00\u3063\u3066\u3082\u5168\u7136\u592a\u3063\u3066\u898b\u3048\u306a\u3044\u3001\u53ef\u611b\u3044\u3063\u3066\u8a00\u3063\u3066\u304f\u308c\u305f\u304b\u3089\u3001\u5e73\u6c17\u304b\u306a\u3063\u3066\u601d\u3063\u3061\u3083\u3063\u305f\u3093\u3067\u3059\u2026", "\u60a0\u7406\u304c\u6b63\u3057\u304b\u3063\u305f\u3093\u3060\u2026\u3002\u6674\u6597\u5144\u69d8\u306a\u3093\u3066\u5927\u5618\u3064\u304d\u3088\uff01", "\u305d\u3063\u304b\u2026\u3002\u5b9f\u306f\u79c1\u3082\u3001\u592a\u3063\u3061\u3083\u3063\u305f\u306e\u2026", "\u9e97\u83ef\u304a\u59c9\u69d8\uff01", "\u79c1\u3082\u8584\u3005\u6c17\u3065\u3044\u3066\u3044\u305f\u3093\u3067\u3059\u308f\u3001\u6700\u8fd1\u4f53\u304c\u91cd\u3044\u306a\u3063\u3066\u3002\u3067\u3082\u73fe\u5b9f\u304b\u3089\u76ee\u3092\u80cc\u3051\u3066\u3044\u307e\u3057\u305f\u2026", "\u305d\u3093\u306a\u304a\u59c9\u69d8\uff01\u304a\u59c9\u69d8\u306f\u5168\u7136\u592a\u3063\u3066\u898b\u3048\u307e\u305b\u3093\u308f\uff01", "\u3046\u3046\u3093\u3002\u79c1\u8131\u3044\u3060\u3089\u30e9\u30fb\u30d5\u30e9\u30f3\u30b9\u306a\u306e\u2026", "\u2026\u2026\uff01", "\u9e97\u83ef\uff01\u9e97\u83ef\u306e\u5927\u597d\u304d\u306a\u30b1\u30fc\u30ad\u3092\u8cb7\u3063\u3066\u304d\u305f\u304b\u3089\u3001\u304a\u7236\u69d8\u3068\u4e00\u7dd2\u306b\u98df\u3079\u3088\u3046\uff01", "\u306c\u3049\u3049\u3049\u3049\u3049\u3049\u3063\u3063\uff01\uff01\uff01", "\u3046\u3049\u3049\u3049\u3049\u3049\u3063\uff01\uff01\uff01"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/128/", "subtitle": "128", "discourse": ["\u3042\u3089\u3063\uff01\u9e97\u83ef\u69d8\u3001\uff13\uff10\u4f4d\u3067\u3059\u308f\uff01", "\u9e97\u83ef\u69d8\u3001\u51c4\u3044\u308f\uff01", "\u307e\u3041\u672c\u5f53\u306d", "\u3055\u3059\u304c\u3067\u3059\u308f\u3001\u9e97\u83ef\u69d8\uff01", "\u3042\u308a\u304c\u3068\u3046\u3002\u3067\u3082\u3080\u3057\u308d\u524d\u56de\u3088\u308a\u9806\u4f4d\u306f\u4e0b\u304c\u3063\u3066\u3057\u307e\u3063\u305f\u306e\u3088\uff1f\u4eca\u56de\u306f\u3042\u307e\u308a\u8a66\u9a13\u52c9\u5f37\u3092\u3059\u308b\u6642\u9593\u304c\u306a\u304b\u3063\u305f\u304b\u3089\u2026", "\u305d\u3046\u3060\u3063\u305f\u3093\u3067\u3059\u304b\uff1f\uff01", "\uff13\uff10\u4f4d\u3067\u4e0b\u304c\u3063\u305f\u306a\u3093\u3066\u3001\u9e97\u83ef\u69d8\u306f\u3084\u306f\u308a\u9055\u3046\u308f", "\u52c9\u5f37\u3057\u306a\u3044\u3067\uff13\uff10\u4f4d\u3060\u306a\u3093\u3066\uff01", "\u307f\u306a\u3055\u3093\u3001\u3082\u3046\u304a\u3088\u3057\u306b\u306a\u3063\u3066\u3002\u79c1\u306e\u9806\u4f4d\u306a\u3093\u3066\u305f\u3044\u3057\u305f\u3082\u306e\u3067\u306f\u3042\u308a\u307e\u305b\u3093\u308f\u3002\u305d\u308c\u3088\u308a\u3082\u307b\u3089\u3001\u93d1\u6728\u69d8\u9054\u306e\u307b\u3046\u304c\u7d20\u6674\u3089\u3057\u3044\u306e\u3067\u306f\u306a\u304f\u3066\uff1f", "\u305d\u3046\u3067\u3059\u308f\u306d\uff01\u3055\u3059\u304c\u306f\u745e\u9e1e\u306e\u7687\u5e1d\u3067\u3059\u308f\uff01", "\u304a\u304a\u3063\uff01", "\u6240\u8a6e\u307e\u3050\u308c\u3060\u3063\u305f\u306e\u3088", "\u93d1\u6728\u69d8\u3068\u5186\u57ce\u69d8\u3088\uff01", "\u93d1\u6728\u69d8\u3001\uff11\u4f4d\u304a\u3081\u3067\u3068\u3046\u3054\u3056\u3044\u307e\u3059\uff01", "\u5186\u57ce\u69d8\u3001\u7d20\u6674\u3089\u3057\u3044\u3067\u3059\u308f\uff01", "\u9ad8\u9053\u3055\u3093\u306a\u3069\u306b\u8ca0\u3051\u308b\u308f\u3051\u304c\u306a\u3044\u306e\u3088", "\u307e\u3041\u6b21\u306f\u9811\u5f35\u308c", "\u79c1\u3001\u6674\u6597\u5144\u69d8\u306b\u604b\u4eba\u304c\u3044\u305f\u306a\u3093\u3066\u77e5\u308a\u307e\u305b\u3093\u3067\u3057\u305f\u308f\u2026", "\u305d\u3046\u306d\u3047\u3002\u79c1\u3082\u521d\u3081\u3066\u805e\u304d\u307e\u3057\u305f\u308f", "\u79c1\u3001\u6674\u6597\u5144\u69d8\u306b\u306f\u9e97\u83ef\u304a\u59c9\u69d8\u3068\u304a\u4ed8\u304d\u5408\u3044\u3057\u3066\u6b32\u3057\u304b\u3063\u305f\u306e\u306b\u2026", "\u3048\u3063\uff01", "\u9ebb\u592e\u3061\u3083\u3093\u3001\u5e02\u4e4b\u5009\u3055\u3093\u304c\u5e78\u305b\u306a\u3089\u3044\u3044\u3058\u3083\u306a\u3044\uff1f\u795d\u798f\u3057\u3066\u3042\u3052\u307e\u3057\u3087\u3046\uff1f", "\u2026\u306f\u3044", "\u3069\u3093\u306a\u304b\u305f\u306a\u306e\u304b\u3057\u3089\u306d\u3047\u3002\u5e02\u4e4b\u5009\u3055\u3093\u304c\u9078\u3093\u3060\u304b\u305f\u3067\u3059\u3082\u306e\u3002\u304d\u3063\u3068\u7d20\u6575\u306a\u304b\u305f\u306d", "\u6674\u6597\u5144\u69d8\u3068\u4e00\u7dd2\u3067\u3001\u304a\u3044\u3057\u3044\u3082\u306e\u304c\u5927\u597d\u304d\u306a\u304b\u305f\u3060\u3068\u601d\u3044\u307e\u3059\u308f\uff01", "\u6674\u6597\u5144\u69d8\u306e\u597d\u304d\u306a\u307d\u3063\u3061\u3083\u308a\u3055\u3093\u306d\u3001\u304d\u3063\u3068", "\u79c1\u3082\u305d\u3046\u601d\u3044\u307e\u3059\u308f", "\u9031\u672b\u306b\u4f1a\u3048\u308b\u306e\u304c\u697d\u3057\u307f\u3067\u3059\u308f\u306d"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/127/", "subtitle": "127\u624b\u82b8\u90e8\u306e\u4e01\u7a1a", "discourse": ["\u30d2\u2026\u30c3\uff01", "\u3069\u3046\u304b\u3057\u307e\u3057\u305f\uff1f", "\u3042\u306e\u2026\u3001\u3053\u306e\u4eba\u306f\u8ab0\u306a\u3093\u3067\u3059\u304b\u2026\uff1f", "\uff12\u5e74\u751f\u306e\u624b\u82b8\u90e8\u54e1\u3067\u3059\u3088", "\u3042\u308c\u306f\u3001\u306a\u306b\u3092\u3057\u3066\u3044\u308b\u3093\u3067\u3059\u304b\u2026\uff1f", "\u3042\u3041\u3001\u3042\u308c\u306f\u30cb\u30fc\u30c9\u30eb\u30d5\u30a7\u30eb\u30c8\u3068\u8a00\u3063\u3066\u3001\u6700\u8fd1\u6d41\u884c\u3063\u3066\u3044\u308b\u624b\u82b8\u3067\u3059\u3088\u3002\u7f8a\u6bdb\u306b\u91dd\u3092\u523a\u3057\u3066\u5f62\u3092\u4f5c\u308b\u3093\u3067\u3059", "\u9e97\u83ef\u69d8", "\u898b\u5b66\u306b\u6765\u305f\uff11\u5e74\u751f\u3067\u3059\u308f\u3002\u9e97\u83ef\u69d8\u304c\u306a\u3055\u3063\u3066\u3044\u308b\u30cb\u30fc\u30c9\u30eb\u30d5\u30a7\u30eb\u30c8\u306b\u8208\u5473\u304c\u3042\u308b\u307f\u305f\u3044\u3067\u3059\u306e", "\u3053\u308c\u306f\u30d5\u30a7\u30eb\u30c8\u306b\u91dd\u3092\u523a\u3057\u3066\u4f5c\u308b\u624b\u82b8\u3067\u3059\u308f\u3002\u7c21\u5358\u3067\u697d\u3057\u3044\u3093\u3067\u3059\u306e\u3088\uff1f\u305d\u308c\u3067\u4eca\u306f\u30ec\u30c3\u30b5\u30fc\u30d1\u30f3\u30c0\u3092\u4f5c\u3063\u3066\u3044\u308b\u3093\u3067\u3059\u3002\u307b\u3089\uff01", "\u305c\u3072\u5165\u90e8\u3057\u3066\u304f\u3060\u3055\u3044\u306d\uff01", "\u9e97\u83ef\u69d8\u306f\u4e00\u898b\u3068\u3063\u3064\u304d\u306b\u304f\u304f\u898b\u3048\u308b\u304b\u3082\u3057\u308c\u307e\u305b\u3093\u3051\u3069\u3001\u8a71\u3059\u3068\u3068\u3066\u3082\u512a\u3057\u304f\u3066\u3001\u624b\u82b8\u90e8\u304c\u5927\u597d\u304d\u306a\u65b9\u3067\u3059\u304b\u3089\u3001\u5b89\u5fc3\u3057\u3066\u304f\u3060\u3055\u3044\u306d\uff1f", "\u306f\u3044", "\u5357\u541b\u306f\u523a\u7e4d\u304c\u4e0a\u624b\u3067\u3059\u308f\u306d\u3047\u3002\u304d\u3063\u3068\u5b66\u5712\u796d\u306e\u5c55\u793a\u3067\u306f\u5927\u6d3b\u8e8d\u3057\u307e\u3059\u308f\u306d", "\u5b9f\u306f\u79c1\u3001\u624b\u82b8\u306f\u597d\u304d\u3067\u3059\u3051\u3069\u5f97\u610f\u3067\u306f\u306a\u3044\u3093\u3067\u3059\u306e\u2026", "\u4e00\u756a\u597d\u304d\u306a\u624b\u82b8\u306f\u306a\u3093\u3067\u3059\u304b\uff1f", "\u3060\u3063\u305f\u3089\u8ca7\u76f8\u306b\u898b\u3048\u306a\u3044\u304f\u3089\u3044\u5927\u304d\u304f\u3066\u7acb\u6d3e\u306a\u7269\u3092\u4f5c\u308c\u3070\u3044\u3044\u3058\u3083\u3042\u308a\u307e\u305b\u3093\u304b", "\u79c1\u3001\u90e8\u9577\u306b\u76f8\u8ac7\u3057\u3066\u304d\u307e\u3059\u308f\uff01", "\u3053\u3053\u306b\u72f8\u306e\u523a\u7e4d\u3092\u3057\u305f\u3044\u304b\u3089\u6559\u3048\u3066\u6b32\u3057\u3044", "\u5618\u3064\u304d\u72f8\u3078\u306e\u5acc\u304c\u3089\u305b", "\u5168\u90e8\u306b\u4ed8\u3051\u3066\u3084\u308b\u2026", "\u4e2d\u9014\u534a\u7aef\u306a\u9023\u4e2d\u306b\u6587\u53e5\u3092\u8a00\u308f\u308c\u308b\u7b4b\u5408\u3044\u306f\u306a\u3044\uff01\u5b66\u529b\u3082\u8ca1\u529b\u3082\u79c1\u3088\u308a\u52a3\u308b\u4e8c\u6d41\u3069\u3082\u304c\uff01", "\u9e97\u83ef\u3055\u3093\u306e\u5f8c\u8f29\u306a\u3089\u3057\u3087\u3046\u304c\u306a\u3044\u304b\u3089\u79c1\u304c\u5b88\u3063\u3066\u3042\u3052\u308b\u308f\u3002\u8cb4\u65b9\u3092\u82db\u3081\u308b\u4eba\u9593\u304c\u3044\u305f\u3089\u8a00\u3044\u306a\u3055\u3044\u3002\u79c1\u304c\u6f70\u3057\u3066\u3042\u3052\u308b\u304b\u3089\uff01", "\u6700\u8fd1\u9e97\u83ef\u3055\u3093\u304c\u592a\u3063\u3066\u304d\u3066\u3044\u308b\u308f\u306d\u3002\u4e01\u7a1a\u3001\u8cb4\u5973\u9e97\u83ef\u3055\u3093\u306b\u592a\u3063\u305f\u308f\u3088\u3068\u4f1d\u3048\u3066\u3061\u3087\u3046\u3060\u3044", "\u306a\u3089\u79c1\u304c\u8a00\u3063\u3066\u304f\u308b\uff01", "\u304a\u9858\u3044\u3084\u3081\u3066\u30fc\uff01", "\u3060\u3063\u3066\u9e97\u83ef\u3055\u3093\u304c\u2026", "\u8cb4\u65b9\u3001\u5357\u96f7\u592a\u3063\u3066\u8a00\u3046\u3093\u3067\u3057\u3087\u3002\u3060\u3063\u305f\u3089\u7565\u3057\u3066\u898b\u7fd2\u3044\u306d\u3002\u4eca\u65e5\u304b\u3089\u898b\u7fd2\u3044\u3068\u547c\u3076\u308f", "\u3058\u3083\u3042\u898b\u7fd2\u3044\u3060\u304b\u3089\u4e01\u7a1a", "\u5185\u90e8\u751f\u3084\u7279\u306b\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u3068\u30c8\u30e9\u30d6\u30eb\u306b\u306a\u3063\u305f\u3089\u3059\u3050\u306b\u76f8\u8ac7\u3057\u3066\u304f\u308c", "\u50d5\u306b\u306f\u5409\u7965\u9662\u5148\u8f29\u304c\u3044\u308b\u304b\u3089\u5927\u4e08\u592b\u3067\u3059\uff01"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/126/", "subtitle": "126", "discourse": ["\u50d5\u304c\u4e00\u756a\u304a\u3044\u3057\u3044\u3068\u601d\u3046\u304a\u5e97\u306b\u9023\u308c\u3066\u884c\u304f\u3088", "\u5973\u6027\u306b\u4eba\u6c17\u306e\u304a\u8336\u304c\u3042\u308a\u307e\u3059", "\u93d1\u6728\u69d8\u3001\u7d20\u6674\u3089\u3057\u3044\u6f14\u594f\u3067\u3057\u305f\u308f\uff01", "\u3053\u3046\u3057\u3066\u93d1\u6728\u69d8\u306e\u201c\u96e8\u3060\u308c\u201d\u304c\u8074\u3051\u308b\u306a\u3089\u3001\u6885\u96e8\u3082\u3044\u3044\u3082\u306e\u3067\u3059\u308f\u306d", "\u93d1\u6728\u69d8\u3001\u307b\u304b\u306e\u66f2\u3082\u5f3e\u3044\u3066\u304f\u3060\u3055\u3044\u307e\u305b\u3093\uff1f", "\u305b\u3063\u304b\u304f\u3060\u304b\u3089\u5409\u7965\u9662\u3055\u3093\u3001\u30ea\u30af\u30a8\u30b9\u30c8\u3057\u3066\u307f\u305f\u3089\uff1f", "\u96c5\u54c9\u3001\u5409\u7965\u9662\u3055\u3093\u306e\u597d\u304d\u306a\u66f2\u3092\u5f3e\u3044\u3066\u3042\u3052\u3066", "\u3048\u3063\uff1f\uff01", "\u96ea\u91ce\u306e\u304a\u793c", "\u5409\u7965\u9662\u3055\u3093\u306b\u306f\u3044\u3064\u3082\u304a\u4e16\u8a71\u306b\u306a\u3063\u3066\u3044\u308b\u304b\u3089\u306d\u3002\u8074\u304d\u305f\u3044\u66f2\u3001\u306a\u3093\u3067\u3082\u8a00\u3063\u3066\u3044\u3044\u3088", "\u304a\u3044\u3001\u8ab0\u304c\u5f3e\u304f\u3068\u601d\u3063\u3066\u308b\u3093\u3060", "\u3093\uff1f\u96c5\u54c9", "\u2026\u66f2\u76ee\u306f\uff1f", "\u3048\u3063\uff01", "\u3048\uff5e\u3063\u3068\u2026", "\u3067\u306f\u3001\u540c\u3058\u304f\u30b7\u30e7\u30d1\u30f3\u306e\u201c\u5e7b\u60f3\u5373\u8208\u66f2\u201d\u3092", "\u308f\u304b\u3063\u305f", "\u4eca\u65e5\u306f\u60a0\u7406\u541b\u306f\u4e00\u7dd2\u3067\u306f\u306a\u3044\u306e\u306d\uff1f", "\u60a0\u7406\u3068\u306f\u30b1\u30f3\u30ab\u3057\u307e\u3057\u305f", "\u30b1\u30f3\u30ab\uff1f\u3044\u3064\u3082\u3042\u3093\u306a\u306b\u4ef2\u304c\u826f\u304b\u3063\u305f\u306e\u306b", "\u3060\u3063\u3066\u3001\u60a0\u7406\u3063\u305f\u3089\u79c1\u306b\u3001\u6700\u8fd1\u592a\u3063\u305f\uff1f\u3063\u3066\u8a00\u3063\u305f\u3093\u3067\u3059\u3088\uff01\u592a\u3063\u305f\uff1f\u3063\u3066\uff01\u9177\u3044\u3068\u601d\u3044\u307e\u305b\u3093\uff1f\u3082\u3046\u60a0\u7406\u306a\u3093\u3066\u77e5\u3089\u306a\u3044\u3063\uff01", "\u5973\u306e\u5b50\u306b\u305d\u3093\u306a\u3053\u3068\u8a00\u3046\u306a\u3093\u3066\u30b5\u30a4\u30c6\u30fc\u3067\u3059\u308f\uff01\u9e97\u83ef\u304a\u59c9\u69d8\u3082\u305d\u3046\u601d\u3046\u3067\u3057\u3087\uff1f", "\u305d\u3046\u306d", "\u9ebb\u592e\u306f\u5168\u7136\u592a\u3063\u3066\u306a\u3044\u3088\u3002\u80b2\u3061\u76db\u308a\u306a\u3093\u3060\u304b\u3089\u6c17\u306b\u3059\u308b\u3053\u3068\u306f\u306a\u3044\u3002\u3080\u3057\u308d\u4eca\u306e\u307b\u3046\u304c\u305a\u3063\u3068\u53ef\u611b\u3044", "\u2026\u9e97\u83ef\u304a\u59c9\u69d8\u3001\u79c1\u592a\u308a\u307e\u3057\u305f\uff1f", "\u305d\u3093\u306a\u3053\u3068\u306a\u3044\u308f\u3088\uff01\u9ebb\u592e\u3061\u3083\u3093\u306f\u53ef\u611b\u3044\u308f", "\u30ac\u30ea\u30ac\u30ea\u306e\u5973\u306e\u5b50\u306a\u3093\u3066\u53ef\u611b\u304f\u306a\u3044\u3088\u3002\u9ebb\u592e\u306f\u3044\u3064\u3060\u3063\u3066\u53ef\u611b\u3044\u304b\u3089\u5fc3\u914d\u3057\u306a\u304f\u3066\u5927\u4e08\u592b"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/125/", "subtitle": "125", "discourse": ["\u96ea\u91ce\u541b\u3001\u9000\u9662\u304a\u3081\u3067\u3068\u3046\uff01", "\u9e97\u83ef\u304a\u59c9\u3055\u3093\uff01", "\u3042\u3041\u3063\uff01\u7acb\u305f\u306a\u304f\u3066\u3044\u3044\u304b\u3089\u5ea7\u3063\u3066\u96ea\u91ce\u541b\uff01", "\u3048\uff5e\u3001\u3053\u306e\u304f\u3089\u3044\u5168\u7136\u5e73\u6c17\u3067\u3059\u3088\uff1f", "\u30c0\u30e1\u30c0\u30e1\u3001\u9000\u9662\u3057\u305f\u3070\u3063\u304b\u308a\u306a\u3093\u3067\u3059\u304b\u3089\u3002\u307b\u3089\u5ea7\u3063\u3066", "\u306f\u3041\u3044", "\u96ea\u91ce\u541b\u306f\u75c5\u307f\u4e0a\u304c\u308a\u306a\u3093\u3067\u3059\u304b\u3089\u3001\u30e0\u30ea\u3057\u3066\u306f\u3044\u3051\u307e\u305b\u3093\u308f", "\u30e0\u30ea\u306a\u3093\u3066\u3057\u3066\u306a\u3044\u3067\u3059\u3088\uff1f\u3082\u3046\u5927\u4e08\u592b\u3067\u3059", "\u3067\u3082\u2026", "\u672c\u5f53\u306b\u5e73\u6c17\u3067\u3059\u3063\u3066\u3070\u3002\u9e97\u83ef\u304a\u59c9\u3055\u3093\u3001\u5fc3\u914d\u3057\u3066\u304f\u308c\u3066\u3042\u308a\u304c\u3068\u3046", "\u305d\u3046\u3060\uff01\u304a\u898b\u821e\u3044\u306e\u672c\u3001\u3042\u308a\u304c\u3068\u3046\u3054\u3056\u3044\u307e\u3057\u305f\u3002\u51c4\u304f\u9762\u767d\u304b\u3063\u305f\u3067\u3059", "\u305d\u304a\uff1f\u826f\u304b\u3063\u305f\u3067\u3059\u308f\u3002\u96ea\u91ce\u541b\u306e\u304a\u5144\u69d8\u304b\u3089\u96ea\u91ce\u541b\u304c\u9000\u5c48\u3057\u3066\u3044\u308b\u3063\u3066\u805e\u3044\u3066\u3001\u306a\u306b\u304b\u9000\u5c48\u3057\u306e\u304e\u306b\u306a\u308b\u7269\u3092\u3068\u8003\u3048\u307e\u3057\u305f\u306e", "\u3046\u3093\u3001\u9762\u767d\u304f\u3063\u3066\uff12\u56de\u3082\u8aad\u307f\u8fd4\u3057\u3061\u3083\u3063\u305f", "\u6c17\u306b\u5165\u3063\u3066\u304f\u308c\u305f\u306a\u3089\u5b09\u3057\u3044\u3067\u3059\u308f\u3002\u2026\u5165\u9662\u751f\u6d3b\u306f\u5927\u5909\u3067\u3057\u305f\uff1f", "\u3046\u3046\u3093\u3002\u3044\u3064\u3082\u306e\u3053\u3068\u3060\u304b\u3089", "\u3044\u3064\u3082\u3063\u3066\u2026\u3001\u4eca\u307e\u3067\u4f55\u56de\u5165\u9662\u3057\u305f\u3053\u3068\u304c\u3042\u308b\u306e\uff1f", "\u3048\uff5e\u3001\u899a\u3048\u3066\u306a\u3044\u306a\u3041\u2026\u3001\u4f55\u56de\u3060\u308d\u3046\uff1f", "\u5c0f\u3055\u3044\u9803\u304b\u3089\u5927\u304d\u306a\u767a\u4f5c\u306e\u305f\u3073\u306b\u5165\u9662\u3057\u3066\u3044\u308b\u3093\u3067\u3059\u3002\u3067\u3082\u540c\u3044\u5e74\u304f\u3089\u3044\u306e\u5b50\u9054\u304c\u305f\u304f\u3055\u3093\u3044\u3066\u697d\u3057\u3044\u3067\u3059\u3088\uff1f\u30af\u30ea\u30b9\u30de\u30b9\u306b\u306f\u5148\u751f\u304c\u30b5\u30f3\u30bf\u306e\u683c\u597d\u3092\u3057\u3066\u3001\u307f\u3093\u306a\u3067\u30af\u30ea\u30b9\u30de\u30b9\u4f1a\u3092\u3059\u308b\u3093\u3067\u3059", "\u3048\u3063\uff01\u30af\u30ea\u30b9\u30de\u30b9\u306b\u3082\u5165\u9662\u3057\u3066\u305f\u306e\uff1f\uff01", "\u306f\u3044\u3001\u4e00\u5ea6\u3060\u3051\u3067\u3059\u3051\u3069", "\u9e97\u83ef\u304a\u59c9\u3055\u3093\u3001\u305d\u3093\u306a\u9854\u3057\u306a\u3044\u3067\u3002\u305d\u308c\u306b\u5165\u9662\u3057\u3066\u3066\u3082\u5bb6\u65cf\u3082\u9762\u4f1a\u306b\u6765\u3066\u304f\u308c\u3066\u3001\u30d7\u30ec\u30bc\u30f3\u30c8\u3092\u305f\u304f\u3055\u3093\u3082\u3089\u3044\u307e\u3057\u305f\u3002\u3042\u3068\u53bb\u5e74\u306f\u5143\u6c17\u3060\u3063\u305f\u304b\u3089\u3061\u3083\u3093\u3068\u5bb6\u3067\u304a\u795d\u3044\u3067\u304d\u307e\u3057\u305f\u3088\uff1f", "\u305d\u3046\u306a\u306e\u3002\u3058\u3083\u3042\u53bb\u5e74\u306f\u5bb6\u65cf\u5168\u54e1\u3067\u697d\u3057\u3044\u30af\u30ea\u30b9\u30de\u30b9\u3092\u904e\u3054\u305b\u305f\u306e\u306d\uff1f", "\u306f\u3044\u3002\u3042\u3001\u3067\u3082\u5144\u69d8\u306f\u3044\u307e\u305b\u3093\u3067\u3057\u305f", "\u307e\u3041\uff01\u305b\u3063\u304b\u304f\u96ea\u91ce\u541b\u304c\u3044\u308b\u30af\u30ea\u30b9\u30de\u30b9\u306a\u306e\u306b\uff01", "\u5144\u69d8\u306f\u96c5\u54c9\u5144\u69d8\u3068\u65e5\u672c\u6d77\u306b\u884c\u3063\u3066\u3044\u307e\u3057\u305f\u3002\u304a\u571f\u7523\u306b\u87f9\u3092\u8cb7\u3063\u3066\u304d\u3066\u304f\u308c\u307e\u3057\u305f\u3088\u3002\u5439\u96ea\u3067\u6d77\u304c\u6697\u304f\u3066\u8352\u308c\u3066\u3044\u305f\u3063\u3066\u8a00\u3063\u3066\u307e\u3057\u305f", "\u2026\u2026", "\u3067\u3082\u79c1\u3001\u96ea\u91ce\u541b\u304c\u5165\u9000\u9662\u3092\u7e70\u308a\u8fd4\u3059\u307b\u3069\u3001\u60aa\u304b\u3063\u305f\u3068\u306f\u601d\u3063\u3066\u3082\u307f\u307e\u305b\u3093\u3067\u3057\u305f\u308f\u2026\u3002\u6cbb\u7642\u3063\u3066\u5927\u5909\uff1f", "\u5438\u5165\u3068\u70b9\u6ef4\u304f\u3089\u3044\u3060\u304b\u3089\u5e73\u6c17\u3067\u3059\u3088", "\u70b9\u6ef4\u3059\u308b\u3093\u3060\u2026", "\u4eca\u56de\u306f\u3082\u3046\u8155\u306e\u8840\u7ba1\u306f\u4f7f\u3048\u306a\u304b\u3063\u305f\u304b\u3089\u3001\u624b\u306e\u7532\u306b\u523a\u3055\u308c\u3061\u3083\u3044\u307e\u3057\u305f\u3002\u307b\u3089", "\u3048\u3063\u3001\u624b\u306e\u7532\uff1f\uff01", "\u624b\u306f\u305d\u308c\u307b\u3069\u75db\u304f\u306a\u3044\u3067\u3059\u3088\u3002\u8db3\u306e\u7532\u306f\u75db\u304b\u3063\u305f\u306a\u3041\u3002\u3042\u308c\u306f\u3082\u3046\u30e4\u3060", "\u8db3\uff1f\uff01", "\u96ea\u91ce\u541b\u3001\u79c1\u306b\u51fa\u6765\u308b\u3053\u3068\u304c\u3042\u308c\u3070\u306a\u3093\u3067\u3082\u8a00\u3063\u3066\u306d\uff01", "\u3042\u308a\u304c\u3068\u3046\uff01\u9e97\u83ef\u304a\u59c9\u3055\u3093", "\u5357\u3001\u7537\u306e\u304f\u305b\u306b\u624b\u82b8\u90e8\u5165\u3063\u3066\u308b\u3093\u3060\u308d\uff1f", "\u304a\u524d\u304a\u304b\u307e\u304b\u3088", "\u7de8\u307f\u7269\u3068\u304b\u3084\u3063\u3061\u3083\u3063\u3066\u3093\u306e\uff1f\u8d85\u30c0\u30bb\u30fc\uff01", "\u304a\u3044\u5357\u3001\u306a\u306b\u4f5c\u3063\u3066\u3093\u306e\u304b\u898b\u305b\u3066\u307f\u308d\u3088\uff01", "\u3084\u3081\u308d\u3088\uff01", "\u8cb4\u65b9\u9054\u3001\u306a\u306b\u3092\u3057\u3066\u3044\u308b\u306e\uff1f", "\u8ab0\u3060\u3088", "\u30d0\u30ab\uff01\uff12\u5e74\u306e\u5409\u7965\u9662\u9e97\u83ef\u3001\u3055\u3093\u3060\uff01\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u306e\uff01", "\u3048\u3063\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\uff1f\uff01", "\u5357\u541b\u306f\u79c1\u306e\u624b\u82b8\u90e8\u306e\u5f8c\u8f29\u3067\u3059\u3051\u3069\u3001\u306a\u306b\u304b\u3054\u7528\u304b\u3057\u3089\uff1f", "\u3048\u3063\u3068\u2026", "\u3044\u3084\u3001\u4ffa\u9054\u306f\u5225\u306b\u2026", "\u3055\u304d\u307b\u3069\u624b\u82b8\u90e8\u3092\u30d0\u30ab\u306b\u3059\u308b\u3088\u3046\u306a\u767a\u8a00\u304c\u805e\u3053\u3048\u307e\u3057\u305f\u3051\u3069\u3001\u6c17\u306e\u305b\u3044\u304b\u3057\u3089\uff1f", "\u3048\u3063\uff01", "\u3044\u3048\u3001\u50d5\u9054\u305d\u3093\u306a\u3064\u3082\u308a\u3067\u306f\u2026\u3002\u306a\u3042\uff1f", "\u3046\u3093", "\u306f\u3044\u2026", "\u305d\u3046\uff1f\u5357\u541b\u306f\u79c1\u306e\u90e8\u6d3b\u52d5\u306e\u5927\u4e8b\u306a\u5f8c\u8f29\u3067\u3059\u306e\u3002\u3067\u3059\u304b\u3089\u5357\u541b\u306b\u5999\u306a\u771f\u4f3c\u3092\u3057\u305f\u3089\u3001\u79c1\u304c\u6575\u306b\u306a\u308b\u3068\u601d\u3063\u3066\u306d\uff1f", "\u3042\u306e\uff01\u5409\u7965\u9662\u5148\u8f29\u3001\u3042\u308a\u304c\u3068\u3046\u3054\u3056\u3044\u307e\u3057\u305f\uff01", "\u3044\u3044\u306e\u3088\u3002\u5f8c\u8f29\u3092\u5b88\u308b\u306e\u306f\u624b\u82b8\u90e8\u306e\u5148\u8f29\u3068\u3057\u3066\u5f53\u7136\u306e\u3053\u3068\u3067\u3059\u3082\u306e\u3002\u307e\u305f\u306a\u306b\u304b\u3042\u3063\u305f\u3089\u3001\u3044\u3064\u3067\u3082\u304a\u3063\u3057\u3083\u3063\u3066\u306d\u3002\u3055\u3001\u624b\u82b8\u90e8\u306b\u53c2\u308a\u307e\u3057\u3087\u3046", "\u306f\u3044\u3063\uff01", "\u9e97\u83ef\u3055\u3093\u306e\u5f8c\u8f29\u306a\u3089\u3057\u3087\u3046\u304c\u306a\u3044\u304b\u3089\u79c1\u304c\u5b88\u3063\u3066\u3042\u3052\u308b\u308f\u3088"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/124/", "subtitle": "124", "discourse": ["\u9e97\u83ef\u304a\u59c9\u3055\u3093", "\u3042\u306e\u3001\u5186\u57ce\u69d8\u3001\u96ea\u91ce\u541b\u304c\u4f53\u8abf\u3092\u5d29\u3057\u3066\u5b66\u9662\u3092\u304a\u4f11\u307f\u3057\u3066\u3044\u308b\u3068\u805e\u3044\u305f\u306e\u3067\u3059\u304c\u2026", "\u3093\uff1f\u8033\u304c\u65e9\u3044\u306d\u3002\u305d\u3046\u306a\u3093\u3060\u3002\u5598\u606f\u306e\u767a\u4f5c\u3067\u4eca\u5165\u9662\u3057\u3066\u3044\u308b\u3093\u3060\u3088", "\u3048\u3063\uff01\u5165\u9662\uff1f\uff01", "\u3042\u3041\u3001\u4eca\u56de\u306f\u305d\u3093\u306a\u306b\u5927\u304d\u306a\u767a\u4f5c\u3058\u3083\u306a\u3044\u304b\u3089\u3002\u6885\u96e8\u306e\u6642\u671f\u306b\u306f\u6bce\u5e74\u306a\u308b\u3093\u3060\u3002\u4e00\u5fdc\u5927\u4e8b\u3092\u53d6\u3063\u3066\u306e\u5165\u9662", "\u3067\u3082\u5165\u9662\u3059\u308b\u304f\u3089\u3044\u3067\u3059\u304b\u3089\u3001\u5927\u5909\u306a\u3053\u3068\u306a\u306e\u3067\u306f\u3042\u308a\u307e\u305b\u3093\u304b\uff1f", "\u305d\u3046\u3067\u3082\u306a\u3044\u3088\u3002\u75c5\u9662\u306b\u5165\u9662\u3057\u3066\u3044\u305f\u307b\u3046\u304c\u3001\u767a\u4f5c\u3092\u8d77\u3053\u3057\u305f\u6642\u306b\u5bfe\u51e6\u304c\u65e9\u3044\u304b\u3089\u3063\u3066\u3060\u3051\u3060\u3057\u3002\u305d\u308c\u306b\u5f1f\u306f\u4eca\u307e\u3067\u4f55\u5ea6\u3082\u5165\u9000\u9662\u3092\u7e70\u308a\u8fd4\u3057\u3066\u3044\u308b\u304b\u3089\u306d", "\u3048\u3048\u3063\uff01", "\u5927\u4e08\u592b\u3060\u3088\u3002\u672c\u4eba\u306f\u75c5\u5ba4\u3067\u5bdd\u3066\u3044\u308b\u3060\u3051\u3060\u304b\u3089\u305a\u3044\u3076\u3093\u9000\u5c48\u3057\u3066\u3044\u308b\u307f\u305f\u3044\u3002\u50d5\u3082\u6bce\u65e5\u5b66\u6821\u5e30\u308a\u306b\u75c5\u9662\u306b\u5bc4\u3063\u3066\u308b\u3093\u3060\u3051\u3069\u3001\u3064\u307e\u3089\u306a\u3044\u65e9\u304f\u5e30\u308a\u305f\u3044\u3063\u3066\u6587\u53e5\u8a00\u3063\u3066\u308b\u3088", "\u305d\u3046\u3067\u3059\u304b\u2026", "\u5186\u57ce\u69d8\uff01\u3053\u3093\u306a\u3068\u3053\u308d\u3067\u306e\u3093\u3073\u308a\u304a\u8336\u306a\u3093\u3066\u98f2\u3093\u3067\u306a\u3044\u3067\u3001\u65e9\u304f\u96ea\u91ce\u541b\u306e\u5143\u306b\u884c\u3063\u3066\u3042\u3052\u305f\u307b\u3046\u304c\u3088\u308d\u3057\u3044\u3093\u3058\u3083\u3042\u308a\u307e\u305b\u3093\u304b\uff1f\u304d\u3063\u3068\u5bc2\u3057\u304c\u3063\u3066\u3044\u307e\u3059\u308f\uff01", "\u305d\u3093\u306a\u306b\u6025\u304b\u3055\u306a\u304f\u3066\u3082\u3002\u3042\u306e\u5b50\u306f\u3082\u3046\u5165\u9662\u306b\u6163\u308c\u3066\u308b\u304b\u3089\u5e73\u6c17\u3060\u3088", "\u306a\u3093\u3066\u51b7\u305f\u3044\uff01", "\u304d\u3063\u3068\u4eca\u9803\u96ea\u91ce\u541b\u306f\u3001\u96e8\u306e\u964d\u308b\u7a93\u306e\u5916\u3092\u773a\u3081\u3066\u3001\u304a\u5144\u69d8\u304c\u6765\u3066\u304f\u308c\u308b\u306e\u3092\u3058\u3063\u3068\u5f85\u3063\u3066\u3044\u308b\u3093\u3067\u3059\u308f\u3002\u304a\u5144\u69d8\u307e\u3060\u304b\u306a\u3001\u5bc2\u3057\u3044\u306a\u3001\u307e\u3060\u6765\u3066\u304f\u308c\u306a\u3044\u306e\u304b\u306a\u3001\u3063\u3066\u2026\u3002\u3046\u3063\u3001\u96ea\u91ce\u541b\u3063\u2026", "\u78ba\u5b9f\u306b\uff24\uff36\uff24\u304b\u30b2\u30fc\u30e0\u3057\u3066\u3044\u308b\u3068\u601d\u3046\u3051\u3069", "\u2026\u2026", "\u306f\u306f\u3063\u3001\u3054\u3081\u3093\u3002\u5fc3\u914d\u3057\u306a\u304f\u3066\u3082\u4eca\u65e5\u306f\u96c5\u54c9\u3082\u4e00\u7dd2\u306b\u75c5\u9662\u306b\u884c\u304f\u304b\u3089\u3001\u96c5\u54c9\u304c\u6765\u305f\u3089\u3059\u3050\u306b\u5e30\u308b\u3088", "\u307e\u3041\u3001\u93d1\u6728\u69d8\u3082\u884c\u304b\u308c\u308b\u3093\u3067\u3059\u304b", "\u9045\u3044\u3067\u3059\u308f\u306d\u3002\u93d1\u6728\u69d8\u306e\u30af\u30e9\u30b9\u306f\u3044\u3063\u305f\u3044\u4f55\u3092\u3057\u3066\u3044\u308b\u306e\u3067\u3057\u3087\u3046", "\u3082\u3046\u3059\u3050\u6765\u308b\u3068\u601d\u3046\u3051\u3069\u2026\u3001\u307b\u3089\u3001\u5642\u3092\u3059\u308c\u3070\u5f71\u3060\u3088", "\u79c0\u4ecb\u2026\u3063\u3066\u3001\u304a\u524d\u3001\u306a\u306b\u7709\u9593\u306b\u30b7\u30ef\u5bc4\u305b\u3066\u308b\u3093\u3060\uff1f", "\u5409\u7965\u9662\u3055\u3093\u306f\u96c5\u54c9\u304c\u65e9\u304f\u6765\u306a\u3044\u304b\u3089\u30a4\u30e9\u30a4\u30e9\u3057\u3066\u305f\u3093\u3060\u3088\u3002\u306d\u3001\u5409\u7965\u9662\u3055\u3093", "\u4ffa\u304c\uff1f\u306a\u3093\u3067\u3060\u3088\u3002\u306a\u306b\u304b\u7528\u304c\u3042\u3063\u305f\u306e\u304b\uff1f", "\u3044\u3048\u3001\u306a\u3093\u3067\u3082\u3042\u308a\u307e\u305b\u3093\u308f\u3002\u305d\u308c\u3088\u308a\u3082\u65e9\u304f\u884c\u304b\u306a\u3044\u3068\u3002\u9762\u4f1a\u6642\u9593\u304c\u904e\u304e\u3066\u3057\u307e\u3044\u307e\u3059\u308f", "\u306f\uff1f", "\u93d1\u6728\u69d8\u2015\uff01\u3053\u3061\u3089\u306b\u5ea7\u3063\u3066\u304a\u8336\u3092\u3044\u304b\u304c\u3067\u3059\u304b\uff1f", "\u5186\u57ce\u69d8\uff01", "\u5409\u7965\u9662\u3055\u3093\uff1f", "\u3069\u3046\u3057\u305f\u306e\uff1f", "\u3053\u308c\u3092\u96ea\u91ce\u541b\u306b\u6e21\u3057\u3066\u3082\u3089\u3048\u307e\u305b\u3093\u304b\uff1f", "\u3053\u308c\u306f\uff1f", "\u672c\u306a\u3093\u3067\u3059\u3002\u5165\u9662\u3057\u3066\u3044\u308b\u96ea\u91ce\u541b\u306e\u9000\u5c48\u3092\u7d1b\u3089\u308f\u305b\u308b\u3053\u3068\u304c\u51fa\u6765\u305f\u3089\u3068\u601d\u3063\u3066", "\u3078\u3048\u3001\u3042\u308a\u304c\u3068\u3046\u3002\u3061\u306a\u307f\u306b\u306a\u3093\u306e\u672c\u304b\u805e\u3044\u3066\u3044\u3044\uff1f", "\u30b7\u30e5\u30da\u30c3\u30b5\u30eb\u30c8\u306e\u68ee\u306e\u5bbf\u5c4b\u3063\u3066\u3044\u3046\u7ae5\u8a71\u3067\u3059\u3002\u79c1\u304c\u5b50\u4f9b\u306e\u9803\u5927\u597d\u304d\u3060\u3063\u305f\u672c\u3067", "\u7ae5\u8a71\uff1f", "\u306f\u3044\u3002\u3048\u3063\uff01\u3082\u3057\u304b\u3057\u3066\u96ea\u91ce\u541b\u306f\u7ae5\u8a71\u306a\u3093\u3066\u8aad\u307e\u306a\u3044\u3093\u3067\u3059\u304b\uff1f\uff01", "\u3044\u3084\u3001\u305d\u3093\u306a\u3053\u3068\u306f\u306a\u3044\u3088\u3002\u96ea\u91ce\u3082\u672c\u306f\u597d\u304d\u3060\u304b\u3089\u304d\u3063\u3068\u559c\u3076\u3002\u3069\u3046\u3082\u3042\u308a\u304c\u3068\u3046", "\u305d\u308c\u3058\u3083\u3001\u3053\u308c\u306f\u78ba\u304b\u306b\u96ea\u91ce\u306b\u6e21\u3057\u3066\u304a\u304f\u304b\u3089", "\u306f\u3044\u3002\u5186\u57ce\u69d8\u3001\u5f15\u304d\u6b62\u3081\u3066\u3057\u307e\u3063\u3066\u7533\u3057\u8a33\u3042\u308a\u307e\u305b\u3093\u3067\u3057\u305f\u3002\u79c1\u3082\u3046\u623b\u308a\u307e\u3059\u306d\u3002\u3054\u304d\u3052\u3093\u3088\u3046", "\u3046\u3093\u3001\u307e\u305f\u660e\u65e5\u306d\u3002\u3042\u3001\u3053\u306e\u5098\u4f7f\u3063\u3066", "\u5e73\u6c17\u3067\u3059\u308f\u3002\u8d70\u308c\u3070\u3059\u3050\u3067\u3059\u304b\u3089\u3002\u3067\u306f\uff01"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/123/", "subtitle": "123", "discourse": ["\u3053\u306e\u624b\u7d19\u306a\u306b\uff5e", "\u3053\u306e\u624b\u7d19\u306a\u306b\uff5e", "\u306a\u3093\u3060\u304b\u306d\u3001\u8fd1\u9803\u672c\u7530\u3055\u3093\u3068\u3088\u304f\u8a71\u305b\u308b\u3088\u3046\u306b\u306a\u3063\u305f\u3093\u3060\u3088\u3002\u307b\u3089\u91ce\u3005\u702c\u3055\u3093\u304c\u3046\u3061\u306e\u30af\u30e9\u30b9\u306b\u3044\u308b\u304b\u3089\u3001\u6642\u3005\u904a\u3073\u306b\u6765\u3066\u308b\u3067\u3057\u3087\u3002\u305d\u306e\u6642\u306b\u91ce\u3005\u702c\u3055\u3093\u3068\u4e00\u7dd2\u306b\u8a71\u3057\u304b\u3051\u3066\u304f\u308c\u308b\u3093\u3060", "\u307e\u3041\u3001\u305d\u308c\u306f\u826f\u304b\u3063\u305f\u3067\u3059\u306d", "\u3078\u3078\u3063\u3002\u6700\u521d\u306f\u306d\u3001\u9060\u8db3\u306e\u6642\u306b\u5409\u7965\u9662\u3055\u3093\u306e\u4ee3\u308f\u308a\u306b\u91ce\u3005\u702c\u3055\u3093\u304c\u30af\u30e9\u30b9\u59d4\u54e1\u306e\u4ed5\u4e8b\u3092\u3057\u3066\u304f\u308c\u305f\u3067\u3057\u3087\u3002\u3042\u306e\u6642\u306b\u672c\u7530\u3055\u3093\u3082\u624b\u4f1d\u3063\u3066\u304f\u308c\u3066\u3044\u308d\u3044\u308d\u3057\u3083\u3079\u3063\u305f\u3093\u3060\u3002\u305d\u308c\u304b\u3089\u4ef2\u826f\u304f\u306a\u3063\u3061\u3083\u3063\u3066\u306d\u3002\u307b\u3089\u3001\u672c\u7530\u3055\u3093\u3068\u4ef2\u304c\u3044\u3044\u7537\u5b50\u304c\u3044\u308b\u3063\u3066\u8a71\u3057\u305f\u3067\u3057\u3087\u3002\u3042\u308c\u3082\u5225\u306b\u597d\u304d\u306a\u4eba\u3063\u3066\u308f\u3051\u3058\u3083\u306a\u3044\u307f\u305f\u3044\u3002\u3046\u3093\u3001\u306f\u3063\u304d\u308a\u3068\u805e\u3044\u305f\u308f\u3051\u3058\u3083\u306a\u3044\u3093\u3060\u3051\u3069\u306d", "\u307b\u3049\uff5e", "\u672c\u7530\u3055\u3093\u306f\u307e\u3060\u5186\u57ce\u541b\u306b\u61a7\u308c\u3066\u3044\u308b\u307f\u305f\u3044\u3060\u3057\u306d\u3002\u3067\u3082\u604b\u611b\u611f\u60c5\u3068\u306f\u5225\u307f\u305f\u3044\u3002\u672c\u7530\u3055\u3093\u306f\u3001\u5186\u57ce\u541b\u306f\u5409\u7965\u9662\u3055\u3093\u3068\u304a\u4f3c\u5408\u3044\u3063\u3066\u8a00\u3063\u3066\u305f\u3057", "\u306f\u3042\u3063\uff1f\uff01\u306a\u3093\u3067\u3059\u306e\u3001\u305d\u308c\uff1f\uff01", "\u3048\u3063\u3001\u3044\u3084\u3001\u3048\uff5e\u3063\u3068\u2026\u3002\u91ce\u3005\u702c\u3055\u3093\u306f\u5409\u7965\u9662\u3055\u3093\u306f\u93d1\u6728\u541b\u3068\u304a\u4f3c\u5408\u3044\u3063\u3066\u8a00\u3063\u3066\u305f\u3051\u3069\u3001\u50d5\u306f\u4e2d\u7acb\u3063\u3066\u8a00\u3063\u3066\u304a\u3044\u305f", "\u3061\u3087\u3063\u3068\uff01\u305d\u3053\u306f\u306f\u3063\u304d\u308a\u3068\u5426\u5b9a\u3057\u3066\u304a\u3044\u3066\u304f\u308c\u306a\u3044\u3068\uff01", "\u59d4\u54e1\u9577\u306f\u79c1\u306e\u5473\u65b9\u306a\u306e\u3067\u306f\u3042\u308a\u307e\u305b\u3093\u306e\uff01", "\u3046\u3093\u3001\u3082\u3061\u308d\u3093\u50d5\u306f\u5409\u7965\u9662\u3055\u3093\u306e\u5473\u65b9\u306b\u306a\u308a\u305f\u3044\u3068\u601d\u3063\u3066\u308b\u3088\u3002\u4eca\u56de\u3001\u672c\u7530\u3055\u3093\u3068\u4ef2\u826f\u304f\u306a\u308c\u305f\u306e\u3082\u3001\u5409\u7965\u9662\u3055\u3093\u304c\u9060\u8db3\u3067\u30b4\u30fc\u30eb\u3059\u308b\u306e\u304c\u9045\u304b\u3063\u305f\u304a\u304b\u3052\u3060\u3057\u3002\u3055\u3059\u304c\u306f\u604b\u611b\u306e\u5e2b\u5320\u3060\u306d\uff01", "\u3069\u3046\u3044\u305f\u3057\u307e\u3057\u3066\u3002\u3063\u3066\u305d\u3093\u306a\u3053\u3068\u3088\u308a\u3082\u3001\u79c1\u3068\u93d1\u6728\u69d8\u3084\u5186\u57ce\u69d8\u3068\u306e\u5999\u306a\u5642\u3092\u6255\u62ed\u3057\u3066\u304f\u308c\u306a\u3044\u3068\uff01", "\u3046\uff5e\u3093\u2026", "\u59d4\u54e1\u9577\uff01", "\u3044\u3084\u3041\u3001\u3060\u3063\u3066\u7d50\u69cb\u3053\u306e\u5642\u8a71\u3063\u3066\u6614\u304b\u3089\u3060\u3057\u6839\u5f37\u3044\u304b\u3089\u3001\u305d\u308c\u3092\u6d88\u3059\u306e\u3063\u3066\u96e3\u3057\u3044\u3093\u3058\u3083\u306a\u3044\u304b\u306a\u3041", "\u305d\u3046\u306a\u306e\uff1f\uff01", "\u3042\u308c\u3001\u5409\u7965\u9662\u3055\u3093\u3001\u3053\u3053\u3060\u3051\u9aea\u306e\u5dfb\u304d\u304b\u305f\u304c\u53cd\u5bfe\u3060\u3088\uff1f", "\u3048\uff1f", "\u307b\u3089\u3001\u3053\u306e\u4e00\u90e8\u5206\u3060\u3051\u9055\u3046", "\u306a\u306b\u3053\u308c\uff01", "\u3067\u3082\u5185\u5074\u3060\u304b\u3089\u5168\u7136\u6c17\u3065\u304b\u306a\u3044\u3088\u3002\u4eca\u3082\u898b\u3064\u3051\u305f\u306e\u306f\u5076\u7136\u3060\u3057", "\u306a\u3093\u3060\u304b\u73cd\u3057\u3044\u3082\u306e\u3092\u767a\u898b\u3057\u3061\u3083\u3063\u305f\u304b\u3089\u3001\u3044\u3044\u3053\u3068\u304c\u3042\u308a\u305d\u3046", "\u4eca\u65e5\u3001\u9580\u306e\u524d\u3067\u672c\u7530\u3055\u3093\u3068\u5076\u7136\u4f1a\u3063\u3066\u3001\u6559\u5ba4\u307e\u3067\u6388\u696d\u306e\u3053\u3068\u3068\u304b\u8a71\u3057\u306a\u304c\u3089\u6765\u308c\u305f\u3093\u3060\u3002\u5e2b\u5320\u306e\u3054\u5229\u76ca\u304b\u3082\uff01", "\u3078\u30fc\u2026", "\u305d\u306e\u53cd\u5bfe\u306e\u5dfb\u304d\u9aea\u3063\u3066\u3001\u56db\u3064\u8449\u306e\u30af\u30ed\u30fc\u30d0\u30fc\u307f\u305f\u3044\u3060\u3088\u306d\u3047", "\u2026\u2026", "\u4eca\u5ea6\u3001\u672c\u7530\u3055\u3093\u3084\u91ce\u3005\u702c\u3055\u3093\u9054\u3068\u3001\u8a66\u9a13\u304c\u8fd1\u304f\u306a\u3063\u305f\u3089\u4e00\u7dd2\u306b\u52c9\u5f37\u3057\u3088\u3046\u304b\u3063\u3066\u8a71\u3082\u51fa\u305f\u3093\u3060\u3088\u3002\u50d5\u3001\u6628\u65e5\u305d\u306e\u53cd\u5bfe\u306e\u5dfb\u304d\u9aea\u898b\u3064\u3051\u3066\u30e9\u30c3\u30ad\u30fc\u3060\u3063\u305f\u306a\u3041", "\u826f\u304b\u3063\u305f\u308f\u306d\u2026"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/122/", "subtitle": "122", "discourse": ["\u9ad8\u9053\u3055\u3093", "\u306f\u3044\uff1f\u3042\u3063\uff01", "\u9ad8\u9053\u3055\u3093\u3001\u8cb4\u5973\u3044\u3044\u52a0\u6e1b\u306b\u3057\u3066\u304f\u3060\u3055\u3089\u306a\u3044\uff1f\u3069\u3053\u307e\u3067\u745e\u9e1e\u306e\u540d\u3092\u8cb6\u3081\u308c\u3070\u6c17\u304c\u6e08\u3080\u306e\uff1f", "\u3048\u2026\u3001\u79c1\u304c\u307e\u305f\u306a\u306b\u304b\u3057\u307e\u3057\u305f\u304b\u2026\uff1f", "\u306a\u306b\u304b\u3001\u3058\u3083\u306a\u3044\u308f\uff01\u8cb4\u5973\u3001\u4eca\u65e5\u81ea\u8ee2\u8eca\u3067\u767b\u6821\u3057\u305f\u305d\u3046\u306d\u3002\u745e\u9e1e\u306e\u751f\u5f92\u304c\u81ea\u8ee2\u8eca\u3067\u767b\u6821\u306a\u3093\u3066\u3001\u3044\u3063\u305f\u3044\u306a\u306b\u3092\u8003\u3048\u3066\u3044\u308b\u306e\uff1f\uff01", "\u3059\u307f\u307e\u305b\u3093\u2026\u3002\u3042\u306e\u2026\u81ea\u8ee2\u8eca\u901a\u5b66\u306f\u7279\u306b\u6821\u5247\u9055\u53cd\u3067\u306f\u306a\u3044\u306e\u3067\u5927\u4e08\u592b\u304b\u306a\u3063\u3066\u601d\u3063\u3066\u2026", "\u6821\u5247\uff1f\uff01\u81ea\u8ee2\u8eca\u3067\u901a\u5b66\u3059\u308b\u751f\u5f92\u306a\u3069\u745e\u9e1e\u306b\u306f\u3044\u306a\u3044\u304b\u3089\u3001\u4eca\u307e\u3067\u306f\u898f\u5247\u306b\u3059\u308b\u5fc5\u8981\u304c\u306a\u304b\u3063\u305f\u3060\u3051\u3088\uff01\u306a\u3093\u3066\u307f\u3063\u3068\u3082\u306a\u3044\u3053\u3068\u3092\u3057\u3066\u304f\u308c\u305f\u306e\uff01", "\u3059\u307f\u307e\u305b\u3093\u3067\u3057\u305f\u2026", "\u81ea\u8ee2\u8eca\u2026\uff1f\uff01", "\u9ad8\u9053\u3055\u3093\u3001\u8cb4\u5973\u307e\u3055\u304b\u6bce\u65e5\u81ea\u8ee2\u8eca\u3067\u767b\u6821\u3057\u3066\u3044\u305f\u3093\u3058\u3083\u306a\u3044\u3067\u3057\u3087\u3046\u306d", "\u9055\u3044\u307e\u3059\uff01\u305f\u3060\u4eca\u65e5\u306f\u305f\u307e\u305f\u307e\u671d\u304b\u3089\u96fb\u8eca\u304c\u4e8b\u6545\u3067\u6b62\u307e\u3063\u3066\u3057\u307e\u3063\u3066\u3044\u3066\u3001\u3053\u306e\u307e\u307e\u3058\u3083\u9045\u523b\u3057\u3061\u3083\u3046\u3068\u601d\u3063\u3066\u3001\u3060\u3063\u305f\u3089\u81ea\u8ee2\u8eca\u3067\u884c\u3053\u3046\u3068\u2026\u2026\u3002\u3044\u3048\u3001\u672c\u5f53\u306b\u3059\u307f\u307e\u305b\u3093\u2026", "\u9ad8\u9053\u3055\u3093\u3001\u8cb4\u5973\u3001\u5165\u308b\u5b66\u6821\u3092\u9593\u9055\u3048\u305f\u3088\u3046\u306d\uff1f\u81ea\u8ee2\u8eca\u3067\u901a\u5b66\u306a\u3055\u308a\u305f\u3051\u308c\u3070\u3001\u3054\u81ea\u5b85\u306e\u8fd1\u304f\u306e\u516c\u7acb\u9ad8\u6821\u306b\u3067\u3082\u5165\u308c\u3070\u3088\u308d\u3057\u304b\u3063\u305f\u306e\u3067\u306f\u306a\u304f\u3066\uff1f", "\u7533\u3057\u8a33\u3042\u308a\u307e\u305b\u3093\u3067\u3057\u305f\u2026", "\u3068\u306b\u304b\u304f\u3001\u4e8c\u5ea6\u3068\u3053\u3093\u306a\u307f\u3063\u3068\u3082\u306a\u3044\u771f\u4f3c\u306f\u3057\u306a\u3044\u3067\u3061\u3087\u3046\u3060\u3044\u3002\u8cb4\u5973\u306e\u884c\u52d5\u306b\u306f\u76ee\u306b\u4f59\u308b\u3082\u306e\u304c\u3042\u308b\u308f\u3002\u3053\u308c\u4ee5\u4e0a\u745e\u9e1e\u306e\u540d\u306b\u6ce5\u3092\u5857\u308b\u3088\u3046\u306a\u3053\u3068\u3092\u3059\u308b\u306a\u3089\u3001\u899a\u609f\u3057\u3066\u304a\u304f\u306e\u306d", "\u672c\u5f53\u306b\u3059\u307f\u307e\u305b\u3093\u3067\u3057\u305f\uff01", "\u6210\u7e3e\u304c\u3044\u3044\u304b\u3089\u3063\u3066\u5929\u72d7\u306b\u306a\u3063\u3066\u3044\u308b\u3093\u3058\u3083\u306a\u3044\u306e\uff1f", "\u7acb\u5834\u3092\u5f01\u3048\u306a\u3055\u3044\u3088", "\u9ad8\u9053\u3001\u3069\u3046\u3057\u305f\uff1f\uff01", "\u3042\u3089\u3001\u8cb4\u65b9\u306f\u306a\u306b\uff1f", "\u4ffa\u306f\u751f\u5f92\u4f1a\u5f79\u54e1\u306e\u6c34\u5d0e\u3067\u3059", "\u305d\u3093\u306a\u3053\u3068\u306f\u3082\u3061\u308d\u3093\u77e5\u3063\u3066\u3044\u308b\u308f\u3088\u3002\u305d\u306e\u751f\u5f92\u4f1a\u306e\u5f79\u54e1\u304c\u4f55\u3092\u3057\u306b\u6765\u305f\u3068\u8a00\u3063\u3066\u3044\u308b\u306e\u3088", "\u5b66\u6821\u306e\u554f\u984c\u3092\u89e3\u6c7a\u3059\u308b\u306e\u304c\u3001\u751f\u5f92\u4f1a\u306e\u4ed5\u4e8b\u3067\u3059\u304b\u3089", "\u307e\u3041\u3001\u305a\u3044\u3076\u3093\u3068\u81ea\u4fe1\u304c\u304a\u3042\u308a\u306b\u306a\u308b\u306e\u306d\u3047\u3002\u305f\u304b\u304c\u751f\u5f92\u4f1a\u306e\u5206\u969b\u3067\u3002\u8abf\u5b50\u306b\u4e57\u308b\u306e\u3082\u3044\u3044\u52a0\u6e1b\u306b\u306a\u3055\u3044\uff01", "\u306a\u3063\u2026\uff01", "\u3084\u3081\u3066\u3084\u3081\u3066\u6c34\u5d0e\u541b\u3063\u3001\u79c1\u304c\u60aa\u304b\u3063\u305f\u3093\u3060\u3088\u3002\u672c\u5f53\u306b\u3084\u3081\u3066", "\u3059\u307f\u307e\u305b\u3093\u3067\u3057\u305f\u3002\u4eca\u5f8c\u4e00\u5207\u81ea\u8ee2\u8eca\u306b\u4e57\u3063\u3066\u304f\u308b\u3088\u3046\u306a\u3053\u3068\u306f\u3057\u307e\u305b\u3093\u3002\u4eca\u65e5\u306f\u672c\u5f53\u306b\u7533\u3057\u8a33\u3042\u308a\u307e\u305b\u3093\u3067\u3057\u305f", "\u4e8c\u5ea6\u76ee\u306f\u3042\u308a\u307e\u305b\u3093\u308f\u3088", "\u2026\u308f\u304b\u308a\u307e\u3057\u305f", "\u307e\u3041\u9e97\u83ef\u69d8\uff01\u3054\u304d\u3052\u3093\u3088\u3046", "\u3054\u304d\u3052\u3093\u3088\u3046\u3001\u7464\u5b50(\u3088\u3046\u3053)\u69d8", "\u9e97\u83ef\u69d8\u3082\u91ce\u826f\u72ac\u304c\u7d1b\u308c\u8fbc\u3093\u3067\u5927\u5909\u3060\u3068\u601d\u3044\u307e\u3059\u3051\u3069\u3001\u306a\u306b\u304b\u3042\u3063\u305f\u3089\u79c1\u9054\u306b\u3059\u3050\u306b\u304a\u3063\u3057\u3083\u3063\u3066\u306d", "\u307e\u3041\u3001\u307b\u307b\u307b\u2026", "\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u306b\u4eca\u65e5\u3001\u65b0\u3057\u3044\u8336\u8449\u304c\u5165\u3063\u305f\u305d\u3046\u306a\u306e\u3002\u653e\u8ab2\u5f8c\u306f\u305c\u3072\u30b5\u30ed\u30f3\u306b\u3044\u3089\u3057\u3066\u306d", "\u306f\u3044\u3001\u697d\u3057\u307f\u306b\u3057\u3066\u304a\u308a\u307e\u3059\u308f", "\u6065\u6652\u3057", "\u5b9f\u306f\u4eca\u65e5\u3001\u81ea\u8ee2\u8eca\u3067\u767b\u6821\u3057\u3061\u3083\u3063\u3066\u2026", "\u81ea\u8ee2\u8eca\uff1f\uff01\u306a\u3093\u3060\u3063\u3066\u305d\u3093\u306a\u3053\u3068\u3092", "\u671d\u96fb\u8eca\u304c\u6b62\u307e\u3063\u3066\u3066\u3001\u632f\u308a\u66ff\u3048\u8f38\u9001\u3067\u3082\u9593\u306b\u5408\u3044\u305d\u3046\u306b\u306a\u304b\u3063\u305f\u306e\u3002\u3067\u3001\u7686\u52e4\u8cde\u9003\u3057\u305f\u304f\u306a\u304f\u3066\u3001\u3064\u3044\u2026", "\u2026\u7686\u52e4\u8cde\u306f\u9045\u5ef6\u8a3c\u660e\u3082\u3089\u3048\u3070\u5e73\u6c17\u3060\u3063\u305f\u3093\u3058\u3083\u306a\u3044\u304b\uff1f", "\u3042\u3063\uff01", "\u304a\u4ed5\u4e8b\u4e2d\u306b\u7533\u3057\u8a33\u3042\u308a\u307e\u305b\u3093\u3002\u4eca\u306e\u5b50\u306f\u3001\u3053\u3053\u3067\u306a\u306b\u3092\u3057\u3066\u3044\u305f\u306e\u3067\u3059\u304b\uff1f", "\u3048\u3063\uff01", "\u3042\u3041\u9a5a\u3044\u305f\u3002\u3053\u3093\u306a\u5834\u6240\u306b\u751f\u5f92\u304c\u6765\u308b\u3068\u306f\u601d\u308f\u306a\u304b\u3063\u305f\u3002\u4eca\u306e\u5b50\u3063\u3066\u3001\u82e5\u8449\u3061\u3083\u3093\u304b\u3044\uff1f\u5c71\u83dc\u6458\u307f\u306b\u6765\u305f\u3060\u3051\u3060\u3088", "\u2026\u5c71\u83dc\uff1f", "\u3053\u306e\u68ee\u306b\u306f\u3044\u3044\u91ce\u8349\u304c\u305f\u304f\u3055\u3093\u751f\u3048\u3066\u3044\u3066\u306d\u3002\u305d\u308c\u3092\u6642\u3005\u63a1\u308a\u306b\u6765\u3066\u308b\u3093\u3060\u3088\u3002\u305f\u3089\u306e\u82bd\u3001\u305c\u3093\u307e\u3044\u3001\u8317\u8377\u3001\u308f\u3089\u3073\u306a\u3093\u304b\u3092\u306d", "\u305d\u3093\u306a\u3082\u306e\u304c\u751f\u3048\u3066\u3044\u308b\u3093\u3067\u3059\u304b\uff1f\uff01", "\u305f\u304f\u3055\u3093\u751f\u3048\u3066\u3044\u308b\u3088\u3002\u307f\u3093\u306a\u8208\u5473\u304c\u306a\u3044\u306e\u304b\u6c17\u3065\u304b\u306a\u3044\u306e\u304b\u3001\u308f\u3056\u308f\u3056\u63a1\u308a\u306b\u6765\u308b\u751f\u5f92\u306f\u521d\u3081\u3066\u3060\u3051\u3069\u306d\u3002\u53bb\u5e74\u304b\u3089\u3088\u304f\u82e5\u8449\u3061\u3083\u3093\u304c\u91ce\u8349\u56f3\u9451\u3092\u624b\u306b\u3046\u308d\u3046\u308d\u3057\u3066\u305f\u304b\u3089\u3001\u58f0\u3092\u639b\u3051\u3066\u307f\u305f\u3089\u98df\u5353\u306e\u5f69\u3092\u63a2\u3057\u306b\u3063\u3066\u8a00\u308f\u308c\u3066\u306d\uff5e\u3002\u9762\u767d\u304b\u3063\u305f\u304b\u3089\u304a\u3044\u3057\u3044\u91ce\u8349\u304c\u751f\u3048\u3066\u3044\u308b\u5834\u6240\u3092\u6559\u3048\u3066\u3042\u3052\u308b\u3088\u3046\u306b\u306a\u3063\u305f\u3093\u3060", "\u305d\u308c\u306f\u2026\u554f\u984c\u306b\u306f\u306a\u3089\u306a\u3044\u306e\u3067\u3057\u3087\u3046\u304b\uff1f", "\u6240\u8a6e\u306f\u52dd\u624b\u306b\u751f\u3048\u3066\u3044\u308b\u91ce\u8349\u3060\u304b\u3089\u306d\u3002\u624b\u5165\u308c\u3055\u308c\u3066\u3044\u308b\u82b1\u3092\u63a1\u308b\u306e\u306f\u30c0\u30e1\u3060\u3051\u3069\u3001\u91ce\u8349\u306a\u3089\u5b66\u6821\u5074\u3082\u7279\u306b\u6587\u53e5\u306f\u306a\u3044\u3088\u3046\u3060\u3088", "\u307e\u3041\u3001\u305d\u3046\u3067\u3059\u304b", "\u4eca\u65e5\u306f\u3066\u3093\u3077\u3089\u306e\u6750\u6599\u3092\u63a1\u308a\u306b\u6765\u305f\u3093\u3060\u3063\u3066\u3055\u3002\u672c\u5f53\u306f\u8317\u8377\u304c\u4e00\u756a\u306e\u76ee\u5f53\u3066\u3060\u3063\u305f\u3089\u3057\u3044\u3051\u3069\u5c11\u3057\u6642\u671f\u304c\u65e9\u304b\u3063\u305f\u306d\u3002\u8317\u8377\u3092\u85ac\u5473\u306b\u51b7\u5974\u3092\u98df\u3079\u305f\u304b\u3063\u305f\u3063\u3066\u8a00\u3063\u3066\u305f\u3088\u3002\u3057\u3087\u3046\u304c\u306a\u3044\u304b\u3089\u8317\u8377\u306f\u6765\u9031\u3082\u3046\u4e00\u5ea6\u69d8\u5b50\u3092\u898b\u306b\u6765\u308b\u3063\u3066\u3055\u3002\u30ad\u30af\u30e9\u30b2\u3082\u63a2\u3057\u3066\u305f\u306a\u3041\u3002\u6625\u96e8\u30b5\u30e9\u30c0\u306b\u3057\u3066\u98df\u3079\u305f\u3044\u3063\u3066", "\u2026\u2026\u2026\u2026", "\u82e5\u8449\u3061\u3083\u3093\u306f\u81ea\u5206\u3067\u3082\u30cf\u30fc\u30d6\u3084\u30d7\u30c1\u91ce\u83dc\u3092\u80b2\u3066\u3066\u308b\u304b\u3089\u3001\u305d\u306e\u683d\u57f9\u65b9\u6cd5\u306a\u3093\u304b\u3082\u3088\u304f\u805e\u304d\u306b\u6765\u308b\u3093\u3060\u3002\u3053\u306e\u524d\u306f\u30df\u30f3\u30c8\u304c\u5927\u91cf\u7e41\u6b96\u3057\u3066\u5927\u5909\u306a\u3053\u3068\u306b\u306a\u3063\u305f\u3089\u3057\u3044\u3088\u3002\u305d\u308c\u3067\u30df\u30f3\u30c8\u30c6\u30a3\u30fc\u3092\u4f5c\u3063\u3066\u307f\u305f\u3063\u3066\u3001\u6c34\u7b52\u306b\u5165\u308c\u3066\u6301\u3063\u3066\u304d\u3066\u304f\u308c\u305f\u3088\u3002\u3044\u3084\u3041\u9762\u767d\u3044\u306d\u3047\u3001\u3042\u306e\u5b50\u306f", "\u3042\u306e\u2026\u3001\u5f7c\u5973\u304c\u5c71\u83dc\u6458\u307f\u306b\u6765\u308b\u306e\u306f\u3044\u3044\u3068\u601d\u3044\u307e\u3059\u3051\u3069\u3001\u51fa\u6765\u308c\u3070\u3053\u306e\u3053\u3068\u306f\u307b\u304b\u306e\u751f\u5f92\u306b\u805e\u304b\u308c\u3066\u3082\u5185\u7dd2\u306b\u3057\u3066\u3042\u3052\u3066\u3082\u3089\u3048\u307e\u3059\u304b\uff1f\u554f\u984c\u8996\u3059\u308b\u4eba\u3082\u3044\u308b\u304b\u3082\u3057\u308c\u307e\u305b\u3093\u304b\u3089\u2026", "\u3093\uff1f\u305d\u3046\u304b\u3044\uff1f\u307e\u3041\u3082\u3057\u304b\u3057\u305f\u3089\u826f\u304f\u601d\u308f\u306a\u3044\u4eba\u9593\u3082\u3044\u308b\u304b\u3082\u3057\u308c\u306a\u3044\u306d\u3002\u3058\u3083\u3042\u3053\u308c\u306f\u3053\u3053\u3060\u3051\u306e\u8a71\u306b\u3057\u3066\u304a\u3053\u3046\u304b\u3002\u305b\u3063\u304b\u304f\u306e\u82e5\u8449\u3061\u3083\u3093\u306e\u697d\u3057\u307f\u3092\u53f0\u7121\u3057\u306b\u3057\u3061\u3083\u53ef\u54c0\u60f3\u3060\u304b\u3089\u306d\u3002\u305d\u3046\u3044\u3084\u82e5\u8449\u3061\u3083\u3093\u3001\u51ac\u306b\u306f\u5bd2\u3044\u4e2d\u3001\u4e03\u8349\u7ca5\u3092\u4f5c\u308b\u304b\u3089\u3063\u3066\u6625\u306e\u4e03\u8349\u3092\u63a1\u308a\u306b\u6765\u3066\u305f\u3053\u3068\u3082\u3042\u3063\u305f\u306a\u3041\u3002\u65b0\u9bae\u3067\u304a\u3044\u3057\u3044\u4e03\u8349\u7ca5\u304c\u51fa\u6765\u305f\u3063\u3066\u559c\u3093\u3067\u3044\u305f\u3088\u3001\u3053\u308c\u3067\u4e00\u5e74\u7121\u75c5\u606f\u707d\u3063\u3066\u306d\u3002\u98a8\u6d41\u3060\u306d", "\u4e03\u8349\u7ca5\u3067\u3059\u304b\u2026"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/121/", "subtitle": "121", "discourse": ["\u9ebb\u592e\u3001\u30c7\u30b6\u30fc\u30c8\u306f\u6c7a\u307e\u3063\u305f\u306e\uff1f", "\u3046\uff5e\u3093\u3001\u3069\u3061\u3089\u306b\u3057\u3088\u3046\u304b\u8ff7\u3063\u3066\u308b\u306e", "\u3069\u308c\u3067\u8ff7\u3063\u3066\u308b\u306e\uff1f", "\u3053\u306e\u30b7\u30d5\u30a9\u30f3\u30b1\u30fc\u30ad\u306b\u679c\u7269\u306e\u30a2\u30a4\u30b9\u6dfb\u3048\u304b\u3001\u30d6\u30eb\u30fc\u30d9\u30ea\u30fc\u306e\u30c1\u30fc\u30ba\u30b1\u30fc\u30ad\u30bf\u30eb\u30c8", "\u3060\u3063\u305f\u3089\u50d5\u304c\u7247\u65b9\u983c\u3080\u304b\u3089\u3001\u534a\u5206\u3053\u3057\u3088\u3046", "\u3046\u3093\uff01\u3042\u308a\u304c\u3068\u3046\u60a0\u7406\uff01", "\u9e97\u83ef\u3055\u3093\u3082\u30c7\u30b6\u30fc\u30c8\u306f\u6c7a\u307e\u3063\u305f\uff1f", "\u3069\u3046\u3057\u305f\u306e\uff1f", "\u30c6\u30a3\u30e9\u30df\u30b9\u304b\u82fa\u306e\u30bf\u30eb\u30c8\u304b\u3067\u8ff7\u3063\u3066\u3044\u308b\u3093\u3067\u3059", "\u306a\u3093\u3060\u3002\u3060\u3063\u305f\u3089\u4e21\u65b9\u98df\u3079\u308c\u3070\u3044\u3044\u3088\u3002\u9e97\u83ef\u3055\u3093\u306a\u3089\u5927\u4e08\u592b\uff01", "\u3044\u3048\u3001\u3084\u306f\u308a\u82fa\u306e\u30bf\u30eb\u30c8\u3060\u3051\u306b\u3057\u3066\u304a\u304d\u307e\u3059\u308f\u2026", "\u305b\u3063\u304b\u304f\u3060\u304b\u3089\u98df\u3079\u308c\u3070\u3044\u3044\u306e\u306b", "\u3044\u3048\u3001\u82fa\u306e\u30bf\u30eb\u30c8\u3060\u3051\u3067", "\u3075\u3075\u3063\u3001\u3053\u306e\u30c1\u30fc\u30ba\u30b1\u30fc\u30ad\u3082\u304a\u3044\u3057\u3044\u3063\u3002\u3042\u308a\u304c\u3068\u3046\u3001\u60a0\u7406", "\u3046\u3093", "\u3042\u3041\u3001\u3053\u3093\u306a\u306b\u98df\u3079\u305f\u3089\u592a\u3063\u3061\u3083\u3046", "\u306a\u306b\u8a00\u3063\u3066\u308b\u3093\u3060\u3001\u9ebb\u592e\u3002\u5b50\u4f9b\u304c\u305d\u3093\u306a\u3053\u3068\u6c17\u306b\u3057\u3066\u30c0\u30a4\u30a8\u30c3\u30c8\u306a\u3093\u3066\u3057\u305f\u3089\u30c0\u30e1\u3060\u305e\u3002\u80b2\u3061\u76db\u308a\u306a\u3093\u3060\u304b\u3089\u3057\u3063\u304b\u308a\u98df\u3079\u306a\u3044\u3068", "\u3060\u3063\u3066\u3001\u592a\u308a\u305f\u304f\u306a\u3044\u3082\u3093\u3002\u5973\u306e\u5b50\u306a\u3089\u307f\u3093\u306a\u305d\u3046\u601d\u3044\u307e\u3059\u3088\u306d\u3001\u9e97\u83ef\u304a\u59c9\u69d8\uff1f", "\u3048\u3063\uff01\u305d\u3046\u306d\u2026", "\u307b\u3089\uff01\u9e97\u83ef\u304a\u59c9\u69d8\u3082\u3053\u3046\u8a00\u3063\u3066\u308b\u308f\u3002\u6674\u6597\u5144\u69d8\u306f\u75e9\u305b\u3066\u308b\u304b\u3089\u308f\u304b\u3089\u306a\u3044\u306e\u3088", "\u306a\u3093\u306e\u305f\u3081\u306b\u75e9\u305b\u305f\u3044\u306e\u304b\u308f\u304b\u3089\u306a\u3044\u3051\u3069\u3001\u30ac\u30ea\u30ac\u30ea\u306b\u75e9\u305b\u3066\u3044\u308b\u3088\u308a\u3001\u3080\u3057\u308d\u3061\u3087\u3063\u3068\u3075\u3063\u304f\u3089\u3057\u3066\u3044\u308b\u307b\u3046\u304c\u5973\u306e\u5b50\u306f\u7d76\u5bfe\u306b\u53ef\u611b\u3044\u3068\u601d\u3046\u3088\u3002\u4e16\u306e\u7537\u306f\u307f\u3093\u306a\u305d\u3046\u601d\u3063\u3066\u308b\u3088", "\u3048\uff5e\u3001\u305d\u3046\u304b\u306a\u3041\u2026", "\u60a0\u7406\u541b\u3082\u305d\u3046\u601d\u3046\u3088\u306d\uff1f", "\u672c\u5f53\uff1f\u60a0\u7406\uff01", "\u3042\u2026\u50d5\u306f\u2026\u9ebb\u592e\u306f\u4eca\u306e\u307e\u307e\u3067\u3044\u3044\u3068\u601d\u3046\u2026", "\u9e97\u83ef\u3055\u3093\u3082\u3001\u7121\u7406\u306a\u30c0\u30a4\u30a8\u30c3\u30c8\u306a\u3093\u3066\u3057\u3061\u3083\u30c0\u30e1\u3060\u3088\u3002\u305d\u308c\u3067\u306a\u304f\u3066\u3082\u9e97\u83ef\u3055\u3093\u306f\u75e9\u305b\u3066\u3044\u308b\u3093\u3060\u304b\u3089", "\u305d\u3046\u3044\u3048\u3070\u3001\u96ea\u91ce\u541b\u306b\u9e97\u83ef\u304a\u59c9\u69d8\u3068\u4e00\u7dd2\u306b\u304a\u98df\u4e8b\u306b\u884c\u3063\u3066\u308b\u3063\u3066\u8a71\u3092\u3057\u305f\u3089\u3001\u697d\u3057\u305d\u3046\u3067\u3044\u3044\u306a\u3063\u3066\u8a00\u3063\u3066\u307e\u3057\u305f\u308f", "\u3048\u3063\u3001\u96ea\u91ce\u541b\u304c\uff1f\uff01", "\u96ea\u91ce\u541b\u3001\u3053\u306e\u524d\u9e97\u83ef\u304a\u59c9\u69d8\u306b\u6559\u308f\u3063\u305f\u624b\u82b8\u306b\u8208\u5473\u3092\u6301\u3063\u305f\u3093\u3067\u3059\u3063\u3066\u3002\u307e\u305f\u6559\u3048\u3066\u304f\u3060\u3055\u3044\u3063\u3066\u9e97\u83ef\u304a\u59c9\u69d8\u306b\u4f1d\u3048\u3066\u6b32\u3057\u3044\u3063\u3066\u8a00\u308f\u308c\u307e\u3057\u305f", "\u307e\u3041\u2026\uff01", "\u3069\u3046\u304b\u3057\u305f\uff1f", "\u3044\u3048\u3001\u5e02\u4e4b\u5009\u69d8\u306e\u80a9\u306b\u3053\u308c\u304c\u4ed8\u3044\u3066\u3044\u305f\u3082\u306e\u3067\u3059\u304b\u3089", "\u306a\u306b\uff1f\u3042\u3041\u305d\u308c\u3001\u3082\u3057\u304b\u3057\u305f\u3089\u3046\u3061\u306e\u732b\u306e\u6bdb\u304b\u306a", "\u732b\uff1f\u5e02\u4e4b\u5009\u69d8\u306e\u5bb6\u3067\u306f\u732b\u3092\u98fc\u3063\u3066\u3089\u3063\u3057\u3083\u308b\u306e\u3067\u3059\u304b\uff1f", "\u3046\u3093", "\u3061\u306a\u307f\u306b\u732b\u306e\u7a2e\u985e\u306f\uff1f", "\u30d2\u30de\u30e9\u30e4\u30f3\u3060\u3088\u3002\u540d\u524d\u306f\u30a2\u30ea\u30b9\u3063\u3066\u3044\u3046\u3093\u3060", "\u30d2\u30de\u30e9\u30e4\u30f3\u2026", "\u9e97\u83ef\u69d8\u3001\u9e97\u83ef\u69d8", "\u6674\u6597\u5144\u69d8\u306e\u5bb6\u306e\u732b\u306f\u3001\u3061\u3087\u3063\u3068\u304a\u3067\u3076\u3055\u3093\u306a\u3093\u3067\u3059\u3088", "\u9ebb\u592e\u3001\u805e\u3053\u3048\u3066\u308b\u3088", "\u3075\u3075\u3075\u3063\u3001\u3060\u3063\u3066\u672c\u5f53\u306e\u3053\u3068\u3060\u3082\uff5e\u3093", "\u30a2\u30ea\u30b9\u306f\u3067\u3076\u3058\u3083\u306a\u3044\u3002\u6bdb\u304c\u9577\u3044\u304b\u3089\u592a\u3063\u3066\u898b\u3048\u308b\u3060\u3051\u306a\u306e"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/120/", "subtitle": "120", "discourse": ["\u306d\u3047\u5409\u7965\u9662\u3055\u3093\u3001\u3055\u3063\u304d\u805e\u3044\u305f\u3093\u3060\u3051\u3069\u3001\u3042\u306e\u30c9\u30a2\u5074\u306e\u524d\u304b\u3089\uff12\u756a\u76ee\u306b\u5ea7\u3063\u3066\u308b\u7537\u3001\u745e\u9e1e\u751f\u306a\u3093\u3060\u3063\u3066\u3002\u77e5\u308a\u5408\u3044\uff1f", "\u3048\u3063", "\u3055\u3041\u2026\u3002\u305f\u3076\u3093\u540c\u3058\u30af\u30e9\u30b9\u306b\u306f\u306a\u3063\u305f\u3053\u3068\u306f\u306a\u3044\u3068\u601d\u3044\u307e\u3059\u3051\u3069\u2026", "\u306a\u3093\u3060\u304b\u3042\u3063\u3061\u306f\u5409\u7965\u9662\u3055\u3093\u306e\u3053\u3068\u3092\u77e5\u3063\u3066\u308b\u307f\u305f\u3044\u3060\u3088\uff1f", "\u305d\u3046\u3067\u3059\u304b", "\u3053\u3053\u306b\u306f\u4eca\u5e74\u304b\u3089\u901a\u3044\u59cb\u3081\u305f\u3093\u3060\u3063\u3066\u3055\u3002\u540c\u3058\u5b66\u6821\u306a\u3089\u8a71\u3057\u304b\u3051\u3066\u307f\u305f\u3089\uff1f", "\u305d\u3046\u3067\u3059\u306d\u3047", "\u745e\u9e1e\u306e\u751f\u5f92\u3060\u3068\u4f3a\u3063\u305f\u3093\u3067\u3059\u3051\u3069\u3001\u305d\u3046\u306a\u3093\u3067\u3059\u304b\uff1f", "\u3042\u3063\u3001\u306f\u3044\u3063\u2026", "\u79c1\u306f\u5409\u7965\u9662\u9e97\u83ef\u3068\u8a00\u3044\u307e\u3059\u306e\u3088\u3002\u3088\u308d\u3057\u304f\u304a\u9858\u3044\u3057\u307e\u3059\u306d", "\u306f\u3044\u3063\u3001\u3082\u3061\u308d\u3093\u77e5\u3063\u3066\u307e\u3059\u2026", "\u591a\u57a3\u541b\u306f\u745e\u9e1e\u3067\u306f\u4f55\u7d44\u3067\u3059\u304b\uff1f\u3069\u306a\u305f\u3068\u540c\u3058\u30af\u30e9\u30b9\u306a\u306e\u304b\u3057\u3089", "\u3048\u3063\u3068\u2026\u50d5\u306f\u5186\u57ce\u3055\u3093\u3068\u304b\u3068\u540c\u3058\u30af\u30e9\u30b9\u3067\u2026", "\u5186\u57ce\u69d8\uff1f", "\u3067\u306f\u9ad8\u9053\u3055\u3093\u3068\u3082\u4e00\u7dd2\u306d\u3002\u9ad8\u9053\u3055\u3093\u3068\u306f\u89aa\u3057\u3044\u306e\uff1f", "\u3048\u3063\uff01\u3044\u3084\u3001\u7279\u306b\u306f\u2026", "\u305d\u3046\u3002\u9ad8\u9053\u3055\u3093\u3063\u3066\u30af\u30e9\u30b9\u3067\u306f\u3069\u3093\u306a\u611f\u3058\uff1f\u304a\u53cb\u9054\u306f\u3044\u3089\u3063\u3057\u3083\u308b\u306e\uff1f", "\u3042\u2026\u4e00\u5fdc\u3044\u308b\u307f\u305f\u3044\u3067\u3059\u3051\u3069\u2026", "\u745e\u9e1e\u751f\u3060\u3063\u305f\u3067\u3057\u3087\uff1f\u3069\u3046\u3060\u3063\u305f\uff1f", "\u3048\u3048\u3002\u3067\u3082\u30af\u30e9\u30b9\u3082\u9055\u3046\u306e\u3067\u307b\u307c\u521d\u5bfe\u9762\u3067\u3057\u305f\u308f\u3002\u3042\u3061\u3089\u306f\u79c1\u306e\u3053\u3068\u3092\u3054\u5b58\u3058\u306e\u3088\u3046\u3067\u3057\u305f\u3051\u3069", "\u306a\u306b\u5409\u7965\u9662\u3055\u3093\u3063\u3066\u5b66\u6821\u3067\u6709\u540d\u4eba\u306a\u306e\uff1f", "\u5409\u7965\u9662\u3055\u3093\u3001\u76ee\u7acb\u3064\u3082\u3093\u306d\u30fc", "\u305d\u3093\u306a\u3053\u3068\u306f\u306a\u3044\u3067\u3059\u3051\u3069\u3001\u79c1\u306f\u521d\u7b49\u79d1\u304b\u3089\u745e\u9e1e\u306a\u306e\u3067\u3001\u5916\u90e8\u751f\u3088\u308a\u540d\u524d\u304c\u6d78\u900f\u3057\u3066\u3044\u308b\u306e\u3067\u3057\u3087\u3046", "\u3042\u308c\uff1f\u5409\u7965\u9662\u3055\u3093\u3001\u3082\u3057\u304b\u3057\u3066\u30d3\u30d3\u3089\u308c\u3066\u308b\uff1f", "\u304a\u306f\u3088\u3046\u3001\u5409\u7965\u9662\u3055\u3093", "\u304a\u306f\u3088\u3046\u3054\u3056\u3044\u307e\u3059\u3001\u5186\u57ce\u69d8", "\u3069\u3046\u304b\u3057\u305f\uff1f\u5409\u7965\u9662\u3055\u3093", "\u3044\u3048\u306a\u306b\u3082", "\u4eca\u671d\u306f\u93d1\u6728\u69d8\u3068\u3054\u4e00\u7dd2\u3067\u306f\u3042\u308a\u307e\u305b\u3093\u306e\uff1f", "\u5225\u306b\u96c5\u54c9\u3068\u50d5\u306f\u4e00\u7dd2\u306b\u767b\u6821\u3057\u3066\u308b\u308f\u3051\u3058\u3083\u306a\u3044\u304b\u3089\u3002\u3048\u3063\u306a\u306b\u3001\u3082\u3057\u304b\u3057\u3066\u5409\u7965\u9662\u3055\u3093\u3001\u50d5\u9054\u304c\u6bce\u65e5\u4ef2\u826f\u304f\u540c\u3058\u8eca\u3067\u767b\u6821\u3057\u3066\u308b\u3068\u3067\u3082\u601d\u3063\u3066\u305f\uff1f\u9ad8\u6821\u751f\u306e\u7537\u9054\u304c\u305d\u308c\u3092\u3084\u3063\u3066\u305f\u3089\u3001\u306a\u304b\u306a\u304b\u4e0d\u6c17\u5473\u3067\u3057\u3087\u3002\u305f\u3060\u6765\u308b\u6642\u9593\u5e2f\u304c\u540c\u3058\u3060\u304b\u3089\u304b\u3061\u5408\u3046\u3053\u3068\u306f\u3088\u304f\u3042\u308b\u3051\u3069", "\u3042\u3089\u3001\u305d\u3046\u3060\u3063\u305f\u3093\u3067\u3059\u304b\uff1f", "\u305d\u3046\u3044\u3048\u3070\u78ba\u304b\u306b\u4e00\u6642\u671f\u3001\u671d\u8fce\u3048\u306b\u884c\u3063\u3066\u305f\u6642\u304c\u3042\u3063\u305f\u306d", "\u3042\u306e\u6642\u306f\u96c5\u54c9\u304b\u3089\u76ee\u304c\u96e2\u305b\u306a\u304b\u3063\u305f\u6642\u671f\u3060\u3063\u305f\u304b\u3089\u306d\u3047\u3002\u305d\u3046\u305d\u3046\u3001\u3042\u306e\u6642\u306f\u5409\u7965\u9662\u3055\u3093\u306b\u3082\u304a\u4e16\u8a71\u306b\u306a\u3063\u3061\u3083\u3063\u3066", "\u5409\u7965\u9662\u3055\u3093\u306e\u6368\u3066\u8eab\u306e\u52b1\u307e\u3057\u3067\u96c5\u54c9\u3082\u306a\u3093\u3068\u304b\u5143\u6c17\u306b\u306a\u3063\u305f\u3088\u3002\u3042\u306e\u6642\u306f\u3069\u3046\u3082\u3042\u308a\u304c\u3068\u3046", "\u3069\u3046\u3044\u305f\u3057\u307e\u3057\u3066\u2026", "\u5f1f\u3082\u304a\u4e16\u8a71\u306b\u306a\u3063\u3066\u3044\u308b\u307f\u305f\u3044\u3060\u306d\u3002\u3053\u306e\u524d\u5409\u7965\u9662\u3055\u3093\u306b\u7fd2\u3063\u305f\u3068\u304b\u3044\u3046\u30de\u30b9\u30b3\u30c3\u30c8\u3092\u6301\u3063\u3066\u5e30\u3063\u3066\u304d\u305f\u3088", "\u3042\u3041\u96ea\u3046\u3055\u304e\u3067\u3059\u306d\uff01", "\u30cb\u30fc\u30c9\u30eb\u30d5\u30a7\u30eb\u30c8\u3063\u3066\u3044\u3046\u3093\u3060\u3063\u3066\u306d\u3002\u5409\u7965\u9662\u3055\u3093\u306b\u305d\u3093\u306a\u8da3\u5473\u304c\u3042\u3063\u305f\u3068\u306f\u77e5\u3089\u306a\u304b\u3063\u305f\u3088\u3002\u5f1f\u3082\u697d\u3057\u304b\u3063\u305f\u3068\u559c\u3093\u3067\u305f", "\u305d\u3046\u3067\u3059\u304b\u3002\u96ea\u91ce\u541b\u306b\u559c\u3093\u3067\u3082\u3089\u3048\u305f\u3089\u3001\u79c1\u3082\u5b09\u3057\u3044\u3067\u3059\u308f", "\u3042\u306e\u96ea\u3046\u3055\u304e\u306f\u5186\u57ce\u69d8\u3068\u306e\u601d\u3044\u51fa\u306e\u54c1\u3060\u305d\u3046\u3067\u3059\u306d\u3002\u93d1\u6728\u69d8\u3068\u3075\u305f\u308a\u3067\u96ea\u3060\u308b\u307e\u3092\u4f5c\u3063\u3066\u3082\u3089\u3063\u305f\u8a71\u3082\u805e\u304b\u305b\u3066\u3082\u3089\u3044\u307e\u3057\u305f\u308f\u3002\u5186\u57ce\u69d8\u306f\u96ea\u91ce\u541b\u3092\u3068\u3066\u3082\u53ef\u611b\u304c\u3063\u3066\u3044\u3089\u3063\u3057\u3083\u308b\u306e\u3067\u3059\u306d", "\u307e\u3041\u6b73\u306e\u96e2\u308c\u305f\u5f1f\u3060\u304b\u3089\u306d\u3002\u4f53\u3082\u5f31\u3044\u3057\u3002\u5409\u7965\u9662\u3055\u3093\u3060\u3063\u3066\u304a\u5144\u3055\u3093\u306b\u305a\u3044\u3076\u3093\u53ef\u611b\u304c\u3089\u308c\u3066\u3044\u308b\u305d\u3046\u3058\u3083\u306a\u3044\u304b\u3002\u5642\u3067\u805e\u3044\u3066\u3044\u308b\u3088\u3001\u5409\u7965\u9662\u3055\u3093\u306e\u30d6\u30e9\u30b3\u30f3\u3076\u308a\u306f\u3002\u6700\u8fd1\u3067\u306f\u305d\u308c\u306b\u30d5\u30a1\u30b6\u30b3\u30f3\u7591\u60d1\u306a\u3093\u3066\u8a71\u3082\u6301\u3061\u4e0a\u304c\u3063\u3066\u308b\u3051\u3069", "\u78ba\u304b\u306b\u5144\u3068\u306f\u6614\u304b\u3089\u4ef2\u304c\u3044\u3044\u3067\u3059\u308f\u306d\u3002\u30d5\u30a1\u30b6\u30b3\u30f3\u7591\u60d1\u3068\u3044\u3046\u306e\u306f\u3044\u3063\u305f\u3044\u3069\u3053\u304b\u3089\u51fa\u3066\u304d\u305f\u306e\u304b\u3001\u5fc3\u5f53\u305f\u308a\u304c\u5168\u304f\u3042\u308a\u307e\u305b\u3093\u308f", "\u3078\u3047\u3001\u305d\u3046\u306a\u3093\u3060", "\u5e74\u306e\u96e2\u308c\u305f\u5144\u541b\u304c\u5927\u597d\u304d\u3060\u304b\u3089\u3001\u5409\u7965\u9662\u3055\u3093\u306f\u5e74\u4e0a\u306e\u7537\u6027\u304c\u597d\u304d\u306a\u306e\u304b\u306a\uff1f", "\u3048\u3063", "\u3084\u3063\u3071\u308a\u59eb\u306b\u306f\u4f8d\u5f93\u3058\u3083\u30c0\u30e1\u3060\u3063\u305f\u306e\u3088\u3002\u59eb\u306b\u306f\u738b\u5b50\uff01", "\u4f8d\u5f93\u3001\u5fdc\u63f4\u3057\u3066\u305f\u3093\u3060\u3051\u3069\u306a", "\u6170\u3081\u3066\u307f\u308b\uff1f"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/119/", "subtitle": "119", "discourse": ["\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u306e\u7d50\u675f\u3092\u5f37\u3081\u308b\u305f\u3081\u306b\u3082\u3001\u5168\u54e1\u3067\u98df\u4e8b\u3082\u3044\u3044\u3082\u306e\u3088\u306d\uff1f\u305d\u3082\u305d\u3082\u30e9\u30f3\u30c1\u306f\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u5c02\u7528\u5e2d\u3067\u3001\u30e1\u30f3\u30d0\u30fc\u3068\u98df\u3079\u308b\u306e\u304c\u4e00\u756a\u826f\u3044\u3068\u601d\u3046\u306e\u3088", "\u3084\u3063\u3071\u308a\u521d\u7b49\u79d1\u304b\u3089\u745e\u9e1e\u3067\u80b2\u3063\u3066\u3044\u308b\u4eba\u9054\u3068\u3044\u308b\u306e\u306f\u843d\u3061\u7740\u304f\u308f\u306d\u3002\u9ad8\u7b49\u79d1\u3068\u3082\u306a\u308b\u3068\u3001\u5916\u90e8\u304b\u3089\u745e\u9e1e\u306e\u6821\u98a8\u306b\u99b4\u67d3\u307e\u306a\u3044\u4eba\u3082\u305a\u3044\u3076\u3093\u3068\u6df7\u3058\u3063\u3066\u304d\u3066\u3057\u307e\u3046\u304b\u3089", "\u305d\u3046\u3060\u306d\u3002\u745e\u9e1e\u306e\u540d\u524d\u3092\u6c5a\u3059\u3088\u3046\u306a\u771f\u4f3c\u3060\u3051\u306f\u3084\u3081\u3066\u3082\u3089\u3044\u305f\u3044\u3082\u306e\u3060\u3088", "\u4eca\u5e74\u306e\uff11\u5e74\u751f\u306f\u3069\u3046\uff1f", "\u307e\u3060\u5165\u5b66\u3057\u3066\u65e5\u304c\u6d45\u3044\u306e\u3067\u554f\u984c\u3092\u8d77\u3053\u3059\u5916\u90e8\u751f\u306f\u3044\u307e\u305b\u3093", "\u305d\u3046\u3002\u306a\u306b\u304b\u3042\u308c\u3070\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u306e\u540d\u306e\u5143\u306b\u3001\u3057\u3063\u304b\u308a\u3068\u7acb\u5834\u3092\u6559\u3048\u3066\u5dee\u3057\u4e0a\u3052\u3066", "\u308f\u304b\u308a\u307e\u3057\u305f\u3002\u50d5\u9054\u306e\u745e\u9e1e\u3067\u597d\u304d\u52dd\u624b\u306a\u3053\u3068\u306f\u3055\u305b\u307e\u305b\u3093", "\u5168\u304f\u3001\u745e\u9e1e\u306b\u61a7\u308c\u308b\u306e\u306f\u7d50\u69cb\u3060\u3051\u3069\u3001\u8eab\u306e\u7a0b\u3092\u77e5\u3089\u305a\u306b\u5165\u5b66\u3057\u3066\u304d\u3066\u82e6\u52b4\u3059\u308b\u306e\u306f\u3054\u81ea\u5206\u9054\u306a\u306e\u306b\u306d\u3047", "\u3068\u3053\u308d\u3067\u3001\uff12\u5e74\u751f\u306b\u306f\u554f\u984c\u884c\u52d5\u3092\u8d77\u3053\u3059\u5916\u90e8\u751f\u304c\u3044\u308b\u3051\u308c\u3069\u2026", "\u3042\u3041\u3001\u9ad8\u9053\u3068\u304b\u3044\u3046\u5973\u5b50\u751f\u5f92\u3060\u308d\u3046", "\u745e\u9e1e\u306e\u5236\u670d\u306b\u9577\u9774\u3092\u5c65\u3044\u3066\u3044\u308b\u306e\u3092\u898b\u305f\u6642\u306f\u3001\u79c1\u306f\u30b7\u30e7\u30c3\u30af\u3067\u76ee\u7729\u304c\u3057\u307e\u3057\u305f\u308f", "\u3042\u308c\u306f\u9a5a\u304d\u307e\u3057\u305f\u306d\u3002\u5e38\u8b58\u304c\u306a\u3044\u306b\u3082\u7a0b\u304c\u3042\u308b\u2026", "\u672c\u5f53\u3060\u306a\u3002\u3088\u304f\u3082\u5916\u3067\u745e\u9e1e\u306e\u6065\u3092\u6652\u3057\u3066\u304f\u308c\u305f\u3082\u306e\u3060\u3002\u3042\u308c\u304c\u745e\u9e1e\u751f\u3060\u3068\u601d\u308f\u308c\u308b\u306a\u3069\u8a31\u3057\u304c\u305f\u3044\u3088", "\u3053\u306e\u524d\u3082\u5eca\u4e0b\u3067\u98db\u3073\u8df3\u306d\u3066\u3044\u305f\u308a\u3001\u3068\u306b\u304b\u304f\u30ac\u30b5\u30c4\u3067\u898b\u3066\u3044\u308b\u3060\u3051\u3067\u8179\u7acb\u305f\u3057\u3044\u308f", "\u9aea\u3082\u6642\u3005\u306f\u306d\u3066\u3044\u305f\u308a\u3057\u306a\u3044\uff1f\u8eab\u3060\u3057\u306a\u307f\u3082\u304d\u3061\u3093\u3068\u51fa\u6765\u306a\u3044\u306e\u304b\u3057\u3089", "\u5e30\u308a\u3082\u99c5\u307e\u3067\u8d70\u3063\u3066\u3044\u308b\u306e\u3092\u898b\u305f\u3053\u3068\u304c\u3042\u308b\u305e\u3002\u745e\u9e1e\u306e\u5236\u670d\u3092\u7740\u3066\u9053\u3092\u8d70\u308b\u306a\u3069\u3001\u307f\u3063\u3068\u3082\u306a\u304f\u3066\u601d\u308f\u305a\u8eca\u3092\u964d\u308a\u3066\u6ce8\u610f\u3057\u3088\u3046\u304b\u3068\u601d\u3063\u305f\u3088", "\u307e\u3041\u3001\u305d\u3093\u306a\u3053\u3068\u304c\u2026\u3002\u3068\u306b\u304b\u304f\u3042\u307e\u308a\u306b\u3082\u745e\u9e1e\u306b\u4f3c\u3064\u304b\u308f\u3057\u304f\u306a\u3044\u5b50\u3067\u3059\u308f\u306d\u3002\u3060\u3044\u305f\u3044\u3042\u306e\u6210\u7e3e\u3060\u3063\u3066\u5b9f\u529b\u304b\u3069\u3046\u304b\u2026", "\u93d1\u6728\u69d8\u3068\u5186\u57ce\u69d8\u3092\u629c\u304f\u306a\u3093\u3066\u2026\u3002\u3042\u308a\u3048\u306a\u3044\u306e\u3088\u2026", "\u3082\u3063\u3068\u3057\u3063\u304b\u308a\u52c9\u5f37\u3057\u3066\u9996\u4f4d\u30ad\u30fc\u30d7\u3057\u308d\u3084\uff01", "\u3068\u306b\u304b\u304f\u3001\u3042\u306e\u5b50\u306f\u8981\u6ce8\u610f\u306d\u3002\u7279\u306b\uff12\u5e74\u751f\u306e\u30e1\u30f3\u30d0\u30fc\u306f\u5f7c\u5973\u306b\u76ee\u3092\u5149\u3089\u305b\u3066\u304f\u3060\u3055\u3044\u306a\u3002\u9e97\u83ef\u69d8", "\u2026\u3048\u3063\uff01", "\u9e97\u83ef\u69d8\u3082\u3088\u308d\u3057\u304f\u304a\u9858\u3044\u3057\u307e\u3059\u308f\u306d\u3002\u76ee\u306b\u4f59\u308b\u8f29\u306f\u7c9b\u6e05\u306a\u3055\u3063\u3066", "\u624b\u4f1d\u3046\u3088", "\u3048\u3001\u3067\u3082\u9055\u3046\u30af\u30e9\u30b9\u3060\u3057", "\u3044\u3044\u304b\u3089\u3002\u3053\u308c\u8077\u54e1\u5ba4\uff1f", "\u3044\u3048\u3001\u6e96\u5099\u5ba4\u306e\u307b\u3046\u3067\u2026", "\u308f\u304b\u3063\u305f", "\u306a\u306b\u3042\u308c\u3001\u7537\u5b50\u306b\u5a9a\u3073\u3061\u3083\u3063\u3066", "\u305d\u308c\u3001\u306a\u306b\u3092\u3084\u3063\u3066\u3044\u308b\u3093\u3067\u3059\u304b\uff1f", "\u3053\u308c\u306f\u30cb\u30fc\u30c9\u30eb\u30d5\u30a7\u30eb\u30c8\u3068\u3044\u3063\u3066\u3001\u3053\u3046\u3057\u3066\u5c02\u7528\u306e\u91dd\u3067\u523a\u3057\u3066\u5f62\u3092\u4f5c\u3063\u3066\u3044\u304f\u3082\u306e\u306a\u306e\u3067\u3059\u308f", "\u3078\u3047\u3001\u9762\u767d\u305d\u3046\u2026", "\u6700\u521d\u306f\u4e38\u3092\u4f5c\u3063\u3066\u307f\u3066\u304f\u3060\u3055\u3044\u306d", "\u306f\u3044", "\u51fa\u6765\u305f\uff01", "\u3053\u308c\u306f\u3001\u3046\u3055\u304e\u3055\u3093\u304b\u3057\u3089\uff1f", "\u305d\u3046\u3067\u3059\u3002\u96ea\u3046\u3055\u304e\u3002\u50d5\u306f\u5c0f\u3055\u3044\u9803\u304b\u3089\u4f53\u304c\u5f31\u3044\u304b\u3089\u3001\u96ea\u304c\u964d\u3063\u3066\u3082\u3042\u3093\u307e\u308a\u5916\u3067\u904a\u3079\u306a\u3044\u3093\u3060\u3051\u3069\u3001\u305d\u3046\u3059\u308b\u3068\u5144\u69d8\u304c\u5bdd\u8fbc\u3093\u3067\u3044\u308b\u50d5\u306b\u96ea\u3046\u3055\u304e\u3092\u4f5c\u3063\u3066\u6301\u3063\u3066\u304d\u3066\u304f\u308c\u308b\u3093\u3067\u3059", "\u307e\u3041", "\u50d5\u306e\u90e8\u5c4b\u306e\u7a93\u304b\u3089\u898b\u3048\u308b\u5834\u6240\u306b\u3001\u5144\u69d8\u3068\u304a\u53cb\u9054\u306e\u96c5\u54c9\u5144\u69d8\u304c\u5927\u304d\u306a\u96ea\u3060\u308b\u307e\u3092\u4f5c\u3063\u3066\u304f\u308c\u308b\u3093\u3067\u3059\u3088\u3002\u96ea\u3060\u308b\u307e\u306f\u8d64\u3044\u30d0\u30b1\u30c4\u3092\u88ab\u3063\u3066\u3044\u306a\u3044\u3068\u30c0\u30e1\u3060\u3063\u3066\u8a00\u3063\u3066\u3001\u96c5\u54c9\u5144\u69d8\u304c\u308f\u3056\u308f\u3056\u5c0f\u3055\u306a\u8d64\u3044\u30d0\u30b1\u30c4\u3092\u8cb7\u3063\u3066\u304d\u3066\u88ab\u305b\u3066\u307e\u3057\u305f", "\u96ea\u91ce\u541b\u306f\u304a\u5144\u69d8\u3068\u4ef2\u304c\u3088\u308d\u3057\u3044\u306e\u306d\uff1f", "\u306f\u3044", "\u3053\u306e\u96ea\u3046\u3055\u304e\u3001\u3082\u3089\u3063\u3066\u3082\u3044\u3044\u3067\u3059\u304b\uff1f\u5144\u306b\u50d5\u304c\u4f5c\u3063\u305f\u3093\u3060\u3063\u3066\u898b\u305b\u305f\u3044\u3093\u3067\u3059"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/118/", "subtitle": "118", "discourse": ["\u6700\u8fd1\u3001\uff12\u5e74\u751f\u306b\u8abf\u5b50\u306b\u4e57\u3063\u3066\u3044\u308b\u5b50\u304c\u3044\u308b\u3088\u3046\u306d", "\u3042\u3093\u306a\u5b50\u304c\u306a\u3093\u3067", "\u30ab\u30f3\u30cb\u30f3\u30b0\u3058\u3083\u306a\u3044\u306e\u304b\uff1f", "\u9e97\u83ef\u69d8\u3001\u9811\u5f35\u3063\u3066\uff01", "\u304a\u304b\u3048\u308a\u306a\u3055\u3044\u304a\u5144\u69d8\uff01", "\u305f\u3060\u3044\u307e\u3002\u3069\u3046\u3057\u305f\u306e\u9e97\u83ef\u3001\u30c6\u30fc\u30d6\u30eb\u306b\u304a\u5e97\u5e83\u3052\u3061\u3083\u3063\u3066", "\u3061\u3087\u3063\u3068\u63a2\u3057\u7269\u3092\u3057\u3066\u3044\u3066\u2026", "\u3093\uff1f\u3053\u308c\u3001\u9e97\u83ef\u306e\u30c6\u30b9\u30c8\u306e\u6210\u7e3e\u8868\uff1f", "\u3084\u3060\u3041\uff5e\u3001\u304a\u5144\u69d8\uff01\u6065\u305a\u304b\u3057\u3044\u304b\u3089\u898b\u306a\u3044\u3067\uff5e", "\u3078\u3048\u3063\uff01\u9e97\u83ef\u3001\u4eca\u56de\u306e\u30c6\u30b9\u30c8\uff12\uff19\u4f4d\u3058\u3083\u306a\u3044\u304b\u3002\u51c4\u3044\u3001\u3088\u304f\u9811\u5f35\u3063\u305f\u306d\uff01", "\u3048\uff5e\u3063\u3001\u305d\u3093\u306a\u3053\u3068\u3082\u306a\u3044\u3051\u3069\u3049\uff5e", "\uff12\uff19\u4f4d\u3060\u3068\u9ad8\u7b49\u79d1\u3067\u306f\u540d\u524d\u304c\u8cbc\u308a\u51fa\u3055\u308c\u305f\u3060\u308d\u3046\u3002\u51c4\u3044\u51c4\u3044\u3002\u3053\u308c\u306f\u9811\u5f35\u3063\u305f\u3054\u8912\u7f8e\u306b\u306a\u306b\u304b\u30d7\u30ec\u30bc\u30f3\u30c8\u3057\u306a\u3044\u3068\u3044\u3051\u306a\u3044\u304b\u306a", "\u3048\u3047\u3063\u3001\uff12\uff19\u4f4d\u306a\u3093\u3066\u305f\u3044\u3057\u305f\u3053\u3068\u306a\u3044\u304b\u3089\u3044\u3044\u306e\u306b\uff5e", "\u305d\u3093\u306a\u3053\u3068\u306a\u3044\u3088\u3002\u9e97\u83ef\u306e\u52aa\u529b\u306e\u8cdc\u7269\u3060\u3088", "\u30d6\u30eb\u30b9\u30b1\u30c3\u30bf\u3082\u98df\u3079\u308b\u3067\u3057\u3087", "\u305d\u3046\u3067\u3059\u306d", "\u307b\u304b\u306b\u306f\u30b5\u30e9\u30c0\u304b\u306a\u3002\u9e97\u83ef\u3055\u3093\u3001\u30b5\u30e9\u30c0\u306f\u306a\u306b\u304c\u597d\u304d\uff1f", "\u79c1\u306f\u30ab\u30d7\u30ec\u30fc\u30bc\u304c\u597d\u304d\u3067\u3059\u308f", "\u3058\u3083\u3042\u305d\u308c\u306b\u3057\u3088\u3046\u3002\u3042\u3001\u3067\u3082\u305d\u3057\u305f\u3089\u30c8\u30de\u30c8\u5c3d\u304f\u3057\u306b\u306a\u3063\u3061\u3083\u3046\u306a\u3002\u50d5\u30d1\u30b9\u30bf\u3092\u5909\u66f4\u3059\u308b\u3088\u3002\u306a\u306b\u306b\u3057\u3088\u3046\u304b\u306a\u3041\u2026", "\u9ebb\u592e\u306e\u8a71\u3060\u3068\u3001\u9e97\u83ef\u3055\u3093\u306f\u305a\u3044\u3076\u3093\u512a\u79c0\u3089\u3057\u3044\u3088\u306d\uff1f", "\u305d\u3093\u306a\u3053\u3068\u3042\u308a\u307e\u305b\u3093\u308f\u3002\u9ebb\u592e\u3061\u3083\u3093\u305f\u3089\u8cb7\u3044\u88ab\u308a\u3059\u304e\u306a\u3093\u3067\u3059\u3082\u306e", "\u4eca\u5ea6\u9ebb\u592e\u306b\u52c9\u5f37\u6559\u3048\u3066\u3042\u3052\u3066", "\u30b9\u30a4\u30fc\u30c4\u3001\u3042\u308a\u304c\u3068\u3046\u3002\u9ebb\u592e\u306b\u9e97\u83ef\u3055\u3093\u304b\u3089\u3060\u3088\u3063\u3066\u6e21\u3059\u306d", "\u3053\u3061\u3089\u3053\u305d\u3001\u3044\u3064\u3082\u3054\u99b3\u8d70\u3057\u3066\u3044\u3060\u305f\u3044\u3066\u3042\u308a\u304c\u3068\u3046\u3054\u3056\u3044\u307e\u3059\u3002\u4eca\u65e5\u3082\u3068\u3063\u3066\u3082\u304a\u3044\u3057\u304b\u3063\u305f\u3067\u3059\u308f\u3002\u7279\u306b\u30ab\u30d7\u30ec\u30fc\u30bc\u306f\u79c1\u304c\u4eca\u307e\u3067\u98df\u3079\u305f\u4e2d\u3067\u3082\uff13\u672c\u306e\u6307\u306b\u5165\u308a\u307e\u3059\u308f\u306d", "\u305d\u3046\uff1f\u6c17\u306b\u5165\u3063\u3066\u3082\u3089\u3048\u3066\u826f\u304b\u3063\u305f\u3002\u3042\u306e\u5e97\u306f\u30c8\u30de\u30c8\u306b\u3053\u3060\u308f\u308a\u304c\u3042\u308b\u3093\u3060", "\u9053\u7406\u3067\u304a\u3044\u3057\u3044\u306f\u305a\u3067\u3059\u308f\u306d\u3002\u3067\u3082\u30c1\u30fc\u30ba\u3082\u304a\u3044\u3057\u304b\u3063\u305f\u3067\u3059", "\u30c1\u30fc\u30ba\u304b\u3041\u3002\u306a\u3089\u4eca\u5ea6\u306f\u30ea\u30be\u30c3\u30c8\u306e\u5c02\u9580\u5e97\u306b\u884c\u304b\u306a\u3044\uff1f\u30d6\u30eb\u30fc\u30c1\u30fc\u30ba\u306e\u30ea\u30be\u30c3\u30c8\u304c\u304a\u85a6\u3081\u306a\u3093\u3060", "\u30ea\u30be\u30c3\u30c8\u3067\u3059\u304b\uff01\u79c1\u5927\u597d\u304d\u3067\u3059\u308f", "\u3058\u3083\u3042\u6c7a\u307e\u308a\u3060\u306d"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/117/", "subtitle": "117", "discourse": ["\u308f\u3041\u3063\uff01\u304a\u3044\u3057\u305d\u3046\u3063\uff01", "\u9e97\u83ef\u304a\u59c9\u69d8\u306e\u30d1\u30f3\u30b1\u30fc\u30ad\u3082\u304a\u3044\u3057\u305d\u3046\u3067\u3059\u306d\uff01", "\u9ebb\u592e\u3061\u3083\u3093\u3001\u3053\u3063\u3061\u3082\u5c11\u3057\u98df\u3079\u3066\u307f\u306a\u3044\uff1f", "\u3044\u3044\u3093\u3067\u3059\u304b\uff01\u3060\u3063\u305f\u3089\u79c1\u306e\u3082\u98df\u3079\u3066\u304f\u3060\u3055\u3044\uff01", "\u5e02\u4e4b\u5009\u69d8\u3082\u30c1\u30e7\u30b3\u30d0\u30ca\u30ca\u3044\u304b\u304c\u3067\u3059\u304b\uff1f\u304a\u3044\u3057\u3044\u3067\u3059\u3088\uff1f", "\u79c1\u306e\u30b9\u30c8\u30ed\u30d9\u30ea\u30fc\u3082\u98df\u3079\u3066\u3001\u6674\u6597\u5144\u69d8\uff01", "\u3046\uff5e\u3093\u3001\u50d5\u306f\u3044\u3044\u3084", "\u5e02\u4e4b\u5009\u69d8\u3001\u3082\u3057\u304b\u3057\u3066\u7518\u3044\u3082\u306e\u306f\u3042\u307e\u308a\u304a\u597d\u304d\u3067\u306f\u306a\u3044\u306e\u3067\u3059\u304b\uff1f", "\u305d\u3046\u3044\u3046\u308f\u3051\u3067\u306f\u306a\u3044\u3088\u3002\u7518\u3044\u3082\u306e\u306f\u5272\u3068\u597d\u304d\u3060\u3051\u3069\u3001\u305f\u3060\u305d\u306e\u30af\u30ea\u30fc\u30e0\u306e\u91cf\u306b\u5727\u5012\u3055\u308c\u3061\u3083\u3063\u3066", "\u6674\u6597\u5144\u69d8\u3001\u30d1\u30f3\u30b1\u30fc\u30ad\u306f\u30a4\u30e4\u3060\u3063\u305f\uff1f", "\u305d\u3093\u306a\u3053\u3068\u306a\u3044\u3088\u3001\u9ebb\u592e\u3002\u3058\u3083\u3042\u5c11\u3057\u3082\u3089\u304a\u3046\u304b\u306a\uff1f", "\u672c\u5f53\uff1f", "\u672c\u5f53\u3060\u3088", "\u3046\u3093\u3001\u304a\u3044\u3057\u3044\u3002\u9ebb\u592e\u3042\u308a\u304c\u3068\u3046", "\u3069\u3046\u3044\u305f\u3057\u307e\u3057\u3066\u3002\u9060\u616e\u3057\u306a\u3044\u3067\u3082\u3063\u3068\u98df\u3079\u3066\u3082\u3044\u3044\u306e\u3088\uff1f", "\u305d\u3057\u305f\u3089\u9ebb\u592e\u306e\u3076\u3093\u304c\u306a\u304f\u306a\u3063\u3061\u3083\u3046\u3088\u3002\u50d5\u306f\u81ea\u5206\u306e\u304c\u3042\u308b\u304b\u3089\u5927\u4e08\u592b\u3002\u9ebb\u592e\u3053\u305d\u50d5\u306e\u30d1\u30f3\u30b1\u30fc\u30ad\u3001\u5c11\u3057\u98df\u3079\u3066\u307f\u308b\uff1f", "\u3046\u3093\uff01", "\u304a\u3044\u3057\u3044\uff01\u3042\u3001\u3067\u3082\u30d5\u30eb\u30fc\u30c4\u3092\u53d6\u308a\u904e\u304e\u3061\u3083\u3063\u305f\u3001\u3054\u3081\u3093\u306a\u3055\u3044\u3002\u304a\u8a6b\u3073\u306b\u79c1\u306e\u30af\u30ea\u30fc\u30e0\u3092\u5206\u3051\u3066\u3042\u3052\u308b\uff01", "\u5927\u597d\u304d\u306a\u6674\u6597\u5144\u69d8\u3068\u9e97\u83ef\u304a\u59c9\u69d8\u3068\u4e00\u7dd2\u306b\u3001\u98df\u3079\u305f\u304b\u3063\u305f\u30d1\u30f3\u30b1\u30fc\u30ad\u3092\u98df\u3079\u306b\u6765\u3089\u308c\u3066\u3001\u79c1\u3068\u3063\u3066\u3082\u5b09\u3057\u3044\u308f", "\u9ebb\u592e\u306f\u9e97\u83ef\u3055\u3093\u304c\u61a7\u308c\u3060\u3063\u3066\u8a00\u3063\u3066\u305f\u3082\u3093\u306d", "\u3046\u3093\u3001\u9e97\u83ef\u304a\u59c9\u69d8\u306f\u304d\u308c\u3044\u3067\u512a\u3057\u304f\u3066\u982d\u3082\u3044\u3044\u306e\u3088\u3002\u51c4\u3044\u3093\u3060\u304b\u3089", "\u305d\u3046\u8a00\u3063\u3066\u3082\u3089\u3048\u308b\u306e\u306f\u5b09\u3057\u3044\u3067\u3059\u3051\u3069\u3001\u5b9f\u969b\u306e\u79c1\u306f\u9ebb\u592e\u3061\u3083\u3093\u304c\u8003\u3048\u308b\u3088\u3046\u306a\u4eba\u9593\u3067\u306f\u3042\u308a\u307e\u305b\u3093\u308f\u3088\u3002\u982d\u3082\u826f\u304f\u3042\u308a\u307e\u305b\u3093\u3057\u2026", "\u305d\u3093\u306a\u3053\u3068\u3042\u308a\u307e\u305b\u3093\uff01\u5148\u751f\u3060\u3063\u3066\u9e97\u83ef\u304a\u59c9\u69d8\u306f\u6614\u304b\u3089\u6210\u7e3e\u512a\u79c0\u3060\u3063\u305f\u3063\u3066\u304a\u3063\u3057\u3083\u3063\u3066\u305f\u3082\u306e\uff01\u305d\u308c\u306b\u30af\u30e9\u30b9\u306e\u305f\u3081\u306b\u7387\u5148\u3057\u3066\u50cd\u3044\u3066\u3044\u305f\u3063\u3066", "\u5148\u751f\uff1f", "\u5c0f\u3055\u3044\u9803\u304b\u3089\u5148\u751f\u306b\u3082\u4fe1\u983c\u3055\u308c\u3066\u3044\u3066\u6210\u7e3e\u3082\u30c8\u30c3\u30d7\u30af\u30e9\u30b9\u3002\u3055\u3059\u304c\u9e97\u83ef\u304a\u59c9\u69d8\u3067\u3059\u308f\uff01", "\u3048\u3063\u3068\u3001\u9ebb\u592e\u3061\u3083\u3093\u2026", "\u3078\u3048\u3002\u9e97\u83ef\u3055\u3093\u306f\u512a\u79c0\u306a\u3093\u3060\u306d", "\u305d\u3046\u306a\u306e\uff01\u79c1\u3082\u9e97\u83ef\u304a\u59c9\u69d8\u307f\u305f\u3044\u306b\u306a\u308a\u305f\u3044\u306e\uff01", "\u305d\u3046\u304b\u3002\u3060\u3063\u305f\u3089\u9ebb\u592e\u3082\u52c9\u5f37\u9811\u5f35\u3089\u306a\u3044\u3068\u306a", "\u306f\u3041\uff5e\u3044", "\u2026\u2026", "\u305d\u3046\u3044\u3048\u3070\u9e97\u83ef\u304a\u59c9\u69d8\u306e\u3053\u3068\u306f\u3001\u96ea\u91ce\u541b\u3082\u512a\u3057\u3044\u304a\u59c9\u3055\u3093\u3063\u3066\u8a00\u3063\u3066\u307e\u3057\u305f\u308f", "\u3048\u3063\u3001\u96ea\u91ce\u541b\u304c\uff1f\uff01", "\u96ea\u91ce\u541b\u306f\u521d\u7b49\u79d1\u3067\u3082\u5927\u4eba\u6c17\u306a\u3093\u3067\u3059\u3088\u3002\u5c0f\u3055\u306a\u738b\u5b50\u69d8\u3063\u3066\u8a00\u308f\u308c\u3066\u4e2d\u7b49\u79d1\u3084\u9ad8\u7b49\u79d1\u304b\u3089\u3082\u898b\u306b\u304f\u308b\u4eba\u304c\u3044\u305f\u304f\u3089\u3044\u3002\u3067\u3082\u96ea\u91ce\u541b\u306f\u4f53\u304c\u5f31\u3044\u304b\u3089\u3001\u305d\u3046\u3084\u3063\u3066\u7fa4\u304c\u3063\u3066\u304f\u308b\u5973\u306e\u5b50\u9054\u304b\u3089\u96ea\u91ce\u541b\u3092\u5b88\u308b\u3088\u3046\u306b\u3063\u3066\u3001\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u306e\u4f1a\u9577\u69d8\u304b\u3089\u901a\u9054\u304c\u3042\u308a\u307e\u3057\u305f", "\u305d\u3046\u3067\u3057\u305f\u306e", "\u305d\u306e\u96ea\u91ce\u541b\u3068\u3044\u3046\u5b50\u306f\u305d\u3093\u306a\u306b\u304b\u3063\u3053\u3044\u3044\u306e\uff1f", "\u304b\u3063\u3053\u3044\u3044\u3068\u3044\u3046\u3088\u308a\u3001\u53ef\u611b\u3044\u3063\u3066\u611f\u3058\u3067\u3059\u3088\u306d\uff1f\u9e97\u83ef\u304a\u59c9\u69d8", "\u305d\u3046\u306d", "\u79c1\u9054\u306e\u9593\u3067\u306f\u5929\u4f7f\u307f\u305f\u3044\u3063\u3066\u8a00\u308f\u308c\u3066\u308b\u3093\u3067\u3059\u3088", "\u5929\u4f7f\u304b\u3041\u3002\u50d5\u3082\u4e00\u5ea6\u4f1a\u3063\u3066\u307f\u305f\u3044\u306a", "\u96ea\u91ce\u541b\u306e\u304a\u5144\u69d8\u3082\u3068\u3063\u3066\u3082\u7d20\u6575\u306a\u306e\u3088\u3002\u524d\u306b\u96ea\u91ce\u541b\u3092\u8fce\u3048\u306b\u30b5\u30ed\u30f3\u307e\u3067\u3044\u3089\u3057\u305f\u3053\u3068\u304c\u3042\u308b\u3093\u3060\u3051\u3069\u3001\u79c1\u9054\u306b\u201c\u5f1f\u3092\u3088\u308d\u3057\u304f\u306d\u201d\u3063\u3066\u5fae\u7b11\u3093\u3067\u304f\u308c\u305f\u306e\uff01\u4e0a\u7d1a\u751f\u306e\u304a\u59c9\u69d8\u65b9\u3082\u3046\u3063\u3068\u308a\u3057\u3066\u3044\u305f\u308f\u3002\u96ea\u91ce\u541b\u306e\u304a\u5144\u69d8\u3082\u738b\u5b50\u69d8\u307f\u305f\u3044\u306a\u306e\u3088", "\u305d\u308c\u306f\u51c4\u3044\u306a", "\u305d\u308c\u306b\u738b\u5b50\u69d8\u306e\u89aa\u53cb\u306e\u65b9\u3082\u3068\u3063\u3066\u3082\u304b\u3063\u3053\u3044\u3044\u306e\u3002\u9e97\u83ef\u304a\u59c9\u69d8\u306f\u3042\u306e\u304a\u3075\u305f\u308a\u3068\u540c\u7d1a\u751f\u306a\u3093\u3067\u3059\u3088\u306d\uff1f", "\u3042\u30fc\u2026\u307e\u3041", "\u3044\u3044\u306a\u3041\u3002\u304d\u3063\u3068\u304a\u89aa\u3057\u3044\u3093\u3067\u3059\u3088\u306d\uff1f\u96ea\u91ce\u541b\u3082\u9e97\u83ef\u304a\u59c9\u3055\u3093\u304c\u50d5\u306e\u5144\u3068\u4ef2\u304c\u826f\u3044\u307f\u305f\u3044\u3067\u5b09\u3057\u3044\u3063\u3066\u8a00\u3063\u3066\u307e\u3057\u305f\u3082\u306e", "\u3048\u3063\uff01\uff1f", "\u2026\u3053\u306e\u9ad8\u9053\u82e5\u8449\u3063\u3066\u79c0\u4ecb\u306e\u30af\u30e9\u30b9\u3060\u3063\u305f\u304b\uff1f", "\u305d\u3046\u3060\u3088\u3002\u5143\u6c17\u306a\u5b50", "\u3075\uff5e\u3093", "\u3042\u3001\u5642\u3092\u3059\u308c\u3070\u3002\u9ad8\u9053\u3055\u3093\u3001\uff11\u4f4d\u304a\u3081\u3067\u3068\u3046", "\u3048\u3063\uff11\u4f4d\uff1f\uff01\u3042\u2026\u3001\u3042\u308a\u304c\u3068\u3046\u3054\u3056\u3044\u307e\u3059\u2026", "\u304a\u524d\u304c\u9ad8\u9053\u304b", "\u3048\u3001\u306f\u3044\u2026", "\u3058\u3083\u3042\u306d"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/116/", "subtitle": "116", "discourse": ["\u3084\u3063\u3068\u7537\u5150\u304c\u751f\u307e\u308c\u3066\u304f\u308c\u305f", "\u6b21\u3082\u5973\u3060\u3063\u305f\u3089\u3069\u3046\u3057\u3088\u3046\u304b\u3068\u601d\u3063\u305f", "\u9ebb\u592e\u306f\u9e97\u83ef\u3055\u3093\u3092\u305a\u3044\u3076\u3093\u6155\u3063\u3066\u3044\u308b\u3088\u3046\u306a\u3093\u3060\u3002\u30b5\u30de\u30fc\u30d1\u30fc\u30c6\u30a3\u30fc\u3067\u521d\u3081\u3066\u9e97\u83ef\u3055\u3093\u3068\u4f1a\u3063\u305f\u5f8c\u3001\u5e30\u3063\u3066\u304d\u3066\u304b\u3089\u697d\u3057\u304b\u3063\u305f\u3068\u5b09\u3057\u305d\u3046\u306b\u3057\u3083\u3079\u3063\u3066\u3044\u3066\u306d\u3002\u3068\u3066\u3082\u7d20\u6575\u306a\u304a\u59c9\u69d8\u306b\u4f1a\u3063\u305f\u3093\u3060\u3063\u3066\u6559\u3048\u3066\u304f\u308c\u305f\u3002\u60a0\u7406\u541b\u3068\u4e00\u7dd2\u306b\u64ae\u3063\u305f\u5199\u771f\u3092\u304d\u308c\u3044\u306a\u5199\u771f\u7acb\u3066\u306b\u5165\u308c\u3066\u30d7\u30ec\u30bc\u30f3\u30c8\u3057\u3066\u304f\u308c\u305f\u3060\u308d\u3046\uff1f\u9ebb\u592e\u3001\u5927\u559c\u3073\u3057\u3066\u305f\u3088\u3002\u305d\u308c\u304b\u3089\u3082\u5ea6\u3005\u3001\u9ebb\u592e\u304b\u3089\u9e97\u83ef\u3055\u3093\u306e\u8a71\u304c\u51fa\u305f\u3088\u3002\u512a\u3057\u304f\u3057\u3066\u3082\u3089\u3063\u3066\u308b\u3063\u3066\u3002\u9ebb\u592e\u306b\u306f\u5f1f\u3088\u308a\u81ea\u5206\u3092\u53ef\u611b\u304c\u3063\u3066\u304f\u308c\u308b\u304a\u59c9\u3055\u3093\u306e\u5b58\u5728\u304c\u5b09\u3057\u304b\u3063\u305f\u3093\u3060\u3068\u601d\u3046\u3002\u3060\u304b\u3089\u56f3\u3005\u3057\u3044\u304a\u9858\u3044\u306a\u3093\u3060\u3051\u3069\u3001\u9ebb\u592e\u304c\u61a7\u308c\u3066\u61d0\u3044\u3066\u3044\u308b\u9e97\u83ef\u3055\u3093\u306b\u3001\u9ebb\u592e\u3092\u5c11\u3057\u3067\u3044\u3044\u304b\u3089\u304b\u307e\u3063\u3066\u3084\u3063\u3066\u3082\u3089\u3044\u305f\u3044\u3093\u3060\u3002\u3042\u306e\u5b50\u304c\u5bc2\u3057\u304f\u306a\u3044\u3088\u3046\u306b\u2026\u3002\u9e97\u83ef\u3055\u3093\u3001\u304a\u9858\u3044\u3057\u307e\u3059", "\u3086\u3063\u304f\u308a\u884c\u304d\u307e\u3057\u3087\u3046\u306d", "\u5186\u57ce\u69d8\u3068\u8a71\u3057\u3066\u3044\u308b\u5b50\u306f\u8ab0\uff1f", "\u78ba\u304b\u7279\u5f85\u751f\u306e\u9ad8\u9053\u3055\u3093\u3068\u3044\u3046\u5b50\u3088\u3002\u307b\u3089\u30c6\u30b9\u30c8\u3067\u3044\u3064\u3082\u4e0a\u4f4d\u306e", "\u306a\u3093\u3067\u3042\u3093\u306a\u306b\u5186\u57ce\u69d8\u3068\u89aa\u3057\u3052\u306a\u306e\uff1f\uff01", "\u3053\u308c\u3001\u85a6\u3081\u3066\u304f\u308c\u3066\u3042\u308a\u304c\u3068\u3046\u3054\u3056\u3044\u307e\u3057\u305f\uff01", "\u305d\u306e\u571f\u7523\u2026", "\u50d5\u306f\u98df\u3079\u3066\u3044\u306a\u3044\u3093\u3060\u3051\u3069\u3001\u3042\u3052\u305f\u4eba\u306b\u306f\u304a\u3044\u3057\u304b\u3063\u305f\u3068\u8a00\u308f\u308c\u305f\u3088"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/115/", "subtitle": "115", "discourse": ["\u306a\u306b\u304b\u3054\u8cea\u554f\u304b\u3057\u3089\uff1f", "\u306f\u3044\u3002\u3042\u306e\u3001\u624b\u82b8\u90e8\u3067\u4f7f\u3046\u30df\u30b7\u30f3\u306b\u3064\u3044\u3066\u6559\u3048\u3066\u3044\u305f\u3060\u304d\u305f\u3044\u306e\u3067\u3059\u304c\u2026", "\u30df\u30b7\u30f3\u306e\u306a\u306b\u3092\u805e\u304d\u305f\u3044\u306e\u304b\u3057\u3089\uff1f", "\u30ed\u30c3\u30af\u30df\u30b7\u30f3\u306e\u30e1\u30fc\u30ab\u30fc\u306f\u3069\u3053\u306e\u3067\u3057\u3087\u3046\uff1f\u305d\u308c\u304b\u3089\uff14\u672c\u7cf8\u306e\u30ed\u30c3\u30af\u30df\u30b7\u30f3\u306f\u3042\u308a\u307e\u3059\u304b\uff1f", "\u79c1\u306f\uff13\u672c\u7cf8\u3057\u304b\u4f7f\u3063\u305f\u3053\u3068\u304c\u306a\u3044\u3093\u3067\u3059\u3002\u51fa\u6765\u308c\u3070\uff14\u672c\u7cf8\u3082\u4f7f\u3063\u3066\u307f\u305f\u304f\u3066", "\u2026\u3061\u3087\u3063\u3068\u304a\u5f85\u3061\u306b\u306a\u3063\u3066\u3002\u79c1\u306f\u4eca\u3001\u624b\u304c\u96e2\u305b\u306a\u3044\u306e\u3067\u5225\u306e\u90e8\u54e1\u3092\u547c\u3093\u3067\u304d\u307e\u3059\u308f\u3002\u30ed\u30c3\u30af\u30df\u30b7\u30f3\u306e\u8aac\u660e\u3067\u3059\u308f\u306d", "\u3042\u306e\u304b\u305f\u306f\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u306e\u9e97\u83ef\u69d8\u3088\uff01\u30c0\u30e1\u3088\u3001\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u306e\u304b\u305f\u306e\u624b\u3092\u7169\u308f\u305b\u308b\u3088\u3046\u306a\u771f\u4f3c\u3092\u3057\u305f\u3089\uff01\u4e0d\u8208\u3092\u8cb7\u3063\u305f\u3089\u3069\u3046\u3059\u308b\u306e", "\u7533\u3057\u8a33\u3042\u308a\u307e\u305b\u3093\uff01\u79c1\u306f\u5916\u90e8\u304b\u3089\u4eca\u5e74\u5165\u5b66\u3057\u3066\u304d\u305f\u3070\u304b\u308a\u3067\u3001\u5168\u304f\u77e5\u3089\u306a\u304b\u3063\u305f\u3093\u3067\u3059\uff01", "\u9e97\u83ef\u3055\u3093\u305f\u3089\u3001\u3053\u3093\u306a\u5730\u5473\u306a\u90e8\u306b\u5165\u3063\u3066\u308b\u306e\uff01", "\u307e\u3041\u3044\u3089\u3063\u3057\u3083\u3044\u3001\u7483\u3005\u5948\u3055\u3093\u3002\u624b\u82b8\u90e8\u306b\u8208\u5473\u304c\u304a\u3042\u308a\u304b\u3057\u3089\uff1f", "\u307e\u3055\u304b\uff01\u79c1\u304c\u624b\u82b8\u90e8\u306a\u3093\u3066\u30de\u30a4\u30ca\u30fc\u306a\u90e8\u306b\u5165\u308b\u308f\u3051\u306a\u3044\u3058\u3083\u306a\u3044\u3002\u305f\u3060\u306e\u9e97\u83ef\u3055\u3093\u306e\u51b7\u3084\u304b\u3057\u3088\uff01", "\u50d5\u306f\u30af\u30e9\u30b9\u3082\u9055\u3046\u3057\u3001\u5b8c\u5168\u306b\u5206\u304c\u60aa\u3044\u3088\u306d\uff1f\u3069\u3046\u3057\u305f\u3089\u3044\u3044\u304b\u306a\u3002\u672c\u7530\u3055\u3093\u306f\u5f7c\u304c\u597d\u304d\u306a\u306e\u304b\u306a", "\u3046\uff5e\u3093", "\u79c1\u3001\u91ce\u3005\u702c\u3055\u3093\u304b\u3089\u63a2\u308a\u3092\u5165\u308c\u3066\u307f\u307e\u3057\u3087\u3046\u304b", "\u3048\u3063\uff01\u672c\u5f53\u306b\uff1f\uff01\u3042\u308a\u304c\u3068\u3046\u3001\u5409\u7965\u9662\u3055\u3093\uff01", "\u91ce\u3005\u702c\u3055\u3093\u306b\u306f\u4e2d\u7b49\u79d1\u306e\u590f\u5408\u5bbf\u3067\u3068\u3066\u3082\u304a\u4e16\u8a71\u306b\u306a\u308a\u307e\u3057\u305f\u308f\u306d\u3002\u3042\u306e\u6642\u3082\u4e0d\u51fa\u6765\u306a\u30ea\u30fc\u30c0\u30fc\u306e\u79c1\u3092\u3057\u3063\u304b\u308a\u30b5\u30dd\u30fc\u30c8\u3057\u3066\u304f\u308c\u3066\u3001\u3068\u3066\u3082\u611f\u8b1d\u3057\u307e\u3057\u305f\u306e\u3088\u3002\u4eca\u5e74\u3082\u79c1\u304c\u30af\u30e9\u30b9\u59d4\u54e1\u306a\u3069\u306b\u306a\u3063\u3066\u3057\u307e\u3044\u307e\u3057\u305f\u3051\u3069\u3001\u305c\u3072\u529b\u3092\u8cb8\u3057\u3066\u304f\u3060\u3055\u3044\u306d", "\u3082\u3061\u308d\u3093\u3067\u3059\uff01\u79c1\u304c\u9e97\u83ef\u69d8\u306e\u304a\u5f79\u306b\u7acb\u3066\u308b\u304b\u308f\u304b\u308a\u307e\u305b\u3093\u3051\u3069\u3002\u3067\u3082\u590f\u5408\u5bbf\u3001\u61d0\u304b\u3057\u3044\u3067\u3059\u306d", "\u78ba\u304b\u6bce\u5e74\u3001\u7f8e\u6ce2\u7559\u3055\u3093\u3082\u53c2\u52a0\u3057\u3066\u3044\u305f\u306e\u306b\u3001\u3042\u306e\u5e74\u3060\u3051\u7528\u4e8b\u304c\u3042\u3063\u3066\u6765\u3089\u308c\u306a\u304b\u3063\u305f\u306e\u3067\u3057\u305f\u3063\u3051", "\u3042\u3041\u3001\u305d\u3046\u3044\u3048\u3070\u305d\u3046\u3067\u3057\u305f\u3002\u3042\u306e\u6642\u306f\u7f8e\u6ce2\u7559\u3061\u3083\u3093\u306b\u590f\u5408\u5bbf\u306e\u30e1\u30fc\u30eb\u3092\u9001\u308a\u307e\u3057\u305f\u308f", "\u307e\u3041\u3069\u3093\u306a\u30e1\u30fc\u30eb\u3092\uff1f", "\u3048\u3063\u3068\u2026\u5fd8\u308c\u3066\u3057\u307e\u3044\u307e\u3057\u305f\u308f\u3002\u82b1\u706b\u304c\u697d\u3057\u3044\u3068\u304b\u305d\u3093\u306a\u611f\u3058\u306e\u3053\u3068\u3067\u3059\u306d\u3001\u304d\u3063\u3068", "\u305d\u3046\u3067\u3059\u306e\u3002\u7f8e\u6ce2\u7559\u3055\u3093\u3068\u3082\u540c\u3058\u30af\u30e9\u30b9\u306b\u306a\u308c\u305f\u3089\u697d\u3057\u304b\u3063\u305f\u3067\u3057\u3087\u3046\u306d", "\u672c\u5f53\u306b\u305d\u3046\u3067\u3059\u306d\u3002\u30af\u30e9\u30b9\u66ff\u3048\u306e\u6642\u3001\u6b8b\u5ff5\u3060\u3063\u305f\u306d\u3063\u3066\u8a71\u3057\u305f\u3093\u3067\u3059", "\u305d\u3046\u3002\u3082\u3057\u7f8e\u6ce2\u7559\u3055\u3093\u304c\u540c\u3058\u30af\u30e9\u30b9\u306a\u3089\u3001\u304d\u3063\u3068\u30af\u30e9\u30b9\u59d4\u54e1\u306f\u7f8e\u6ce2\u7559\u3055\u3093\u3060\u3063\u305f\u308f\u306d\u3002\u59d4\u54e1\u9577\u3068\u3082\u6c17\u304c\u5408\u3044\u305d\u3046\u3067\u3059\u3057", "\u305d\u3046\u3067\u3057\u3087\u3046\u304b\u2026\uff1f\u79c1\u306f\u59d4\u54e1\u9577\u3068\u9e97\u83ef\u69d8\u304c\u30af\u30e9\u30b9\u59d4\u54e1\u306e\u307b\u3046\u304c\u5408\u3063\u3066\u3044\u308b\u3068\u601d\u3044\u307e\u3059\u3051\u3069\u3002\u9e97\u83ef\u69d8\u306f\u59d4\u54e1\u9577\u3092\u3069\u3046\u601d\u3044\u307e\u3059\u304b\uff1f", "\u59d4\u54e1\u9577\uff1f\u771f\u9762\u76ee\u3067\u4fe1\u983c\u3067\u304d\u308b\u4eba\u3060\u3068\u601d\u3044\u307e\u3059\u308f\u3088\uff1f", "\u305d\u3046\u3067\u3059\u304b\uff5e\uff01\u9e97\u83ef\u69d8\u3001\u67d4\u9053\u90e8\u306e\u5ca9\u5ba4\u541b\u3068\u3082\u304a\u89aa\u3057\u3044\u3093\u3067\u3059\u3088\u306d\uff1f\u5f7c\u306e\u3053\u3068\u306f\u3069\u3046\u601d\u3044\u307e\u3059\uff1f", "\u5ca9\u5ba4\u541b\u3067\u3059\u304b\uff1f\u81ea\u5206\u306e\u3084\u308a\u305f\u3044\u3053\u3068\u3092\u3057\u3063\u304b\u308a\u3068\u6301\u3063\u3066\u3044\u308b\u4eba\u3067\u52aa\u529b\u5bb6\u3067\u3059\u308f\u306d", "\u306a\u308b\u307b\u3069\uff5e", "\u7f8e\u6ce2\u7559\u3055\u3093\u306f\u30af\u30e9\u30b9\u3067\u4ef2\u306e\u3044\u3044\u7537\u5b50\u306f\u51fa\u6765\u305f\u306e\u304b\u3057\u3089", "\u4ef2\u306e\u3044\u3044\u7537\u5b50\u3067\u3059\u304b\uff1f\u3055\u3041\u3001\u7279\u306b\u305d\u3093\u306a\u8a71\u306f\u7f8e\u6ce2\u7559\u3061\u3083\u3093\u304b\u3089\u805e\u3044\u3066\u3044\u307e\u305b\u3093\u3051\u3069\u3002\u3042\u3089\u3001\u9e97\u83ef\u69d8\u306f\u610f\u5916\u3068\u604b\u30d0\u30ca\u304c\u304a\u597d\u304d\u306a\u3093\u3067\u3059\u304b\uff1f", "\u3048\u3063\u3001\u305d\u3046\u3044\u3046\u308f\u3051\u3067\u306f\u2026", "\u9e97\u83ef\u69d8\u306e\u604b\u30d0\u30ca\u3001\u8208\u5473\u3042\u308a\u307e\u3059\u308f\u30fc\u3002\u3067\u3082\u805e\u3044\u3066\u306f\u3044\u3051\u307e\u305b\u3093\u3088\u306d\uff1f", "\u79c1\u9054\u306b\u805e\u3053\u3048\u3066\u304f\u308b\u9e97\u83ef\u69d8\u306e\u604b\u306e\u5642\u306f\u3001\u30ed\u30de\u30f3\u30c1\u30c3\u30af\u306a\u3082\u306e\u3070\u304b\u308a\u3067\u3059\u3082\u306e\u3002\u7fa8\u307e\u3057\u3044\u3067\u3059", "\u306f\uff1f\u604b\u306e\u5642\uff1f", "\u604b\u306e\u8a69\u96c6\u306e\u8a71\u306a\u3093\u3066\u3001\u79c1\u3046\u3063\u3068\u308a\u3068\u3057\u3066\u3057\u307e\u3044\u307e\u3057\u305f\u3002\u3084\u3063\u3071\u308a\u4f8d\u5f93\u3084\u50ad\u5175\u3067\u306f\u30c0\u30e1\u3067\u3059\u306d\u3002\u79c1\u3001\u9e97\u83ef\u69d8\u3092\u5fdc\u63f4\u3057\u3066\u3044\u307e\u3059\uff01", "\u3048\uff1f\u4f8d\u5f93\uff1f\u50ad\u5175\uff1f"]}
{"title": " \u304a\u96a3\u306e\u5929\u4f7f\u69d8\u306b\u3044\u3064\u306e\u9593\u306b\u304b\u99c4\u76ee\u4eba\u9593\u306b\u3055\u308c\u3066\u3044\u305f\u4ef6", "url": "https://ncode.syosetu.com/n4029bs/114/", "subtitle": "114", "discourse": ["\u5f1f\u306f\u4f53\u304c\u5f31\u3044\u304b\u3089\u3042\u307e\u308a\u9a12\u304c\u306a\u3044\u3067\u304f\u308c\u306a\u3044\u304b", "\u5186\u57ce\u69d8\u306b\u5f1f\u69d8\u304c\u3044\u3089\u3057\u305f\u306a\u3093\u3066\u3001\u79c1\u9054\u3061\u3063\u3068\u3082\u77e5\u308a\u307e\u305b\u3093\u3067\u3057\u305f\u308f\u3002\u305c\u3072\u4e00\u5ea6\u30b5\u30ed\u30f3\u306b\u304a\u9023\u308c\u306b\u306a\u3063\u3066", "\u305d\u3046\u3067\u3059\u306d\u3002\u6a5f\u4f1a\u304c\u3042\u308c\u3070", "\u96ea\u91ce\u69d8\u306e\u304a\u59ff\u3092\u4e00\u76ee\u898b\u308b\u305f\u3081\u306b\u3001\u521d\u7b49\u79d1\u307e\u3067\u51fa\u5411\u304f\u751f\u5f92\u9054\u3082\u591a\u3044\u305d\u3046\u3067\u3059\u308f\u306d\u3002\u304a\u5144\u69d8\u306b\u305d\u3063\u304f\u308a\u306e\u53ef\u611b\u3089\u3057\u3044\u5f1f\u69d8\u3060\u3068\u304b", "\u305d\u308c\u306a\u3093\u3067\u3059\u304c\u3001\u5f1f\u306f\u5c0f\u3055\u3044\u9803\u304b\u3089\u5598\u606f\u3067\u3042\u307e\u308a\u4e08\u592b\u3067\u306f\u306a\u3044\u306e\u3067\u3001\u5909\u306b\u9a12\u304e\u7acb\u3066\u3089\u308c\u3066\u4f53\u8abf\u3092\u5d29\u3057\u305f\u3089\u3068\u5fc3\u914d\u3057\u3066\u3044\u308b\u3093\u3067\u3059\u3002\u5f1f\u306b\u306f\u9759\u304b\u306a\u74b0\u5883\u3067\u904e\u3054\u3055\u305b\u3066\u3084\u308a\u305f\u3044\u306e\u3067", "\u307e\u3041\uff01\u305d\u308c\u306f\u5927\u5909\u3067\u3059\u308f\u306d\u3002\u3067\u3057\u305f\u3089\u96ea\u91ce\u69d8\u304c\u5065\u3084\u304b\u306b\u5b66\u9662\u751f\u6d3b\u3092\u9001\u308c\u308b\u3088\u3046\u306b\u3001\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u304b\u3089\u30d7\u30c6\u30a3\u306b\u3082\u6ce8\u610f\u3059\u308b\u3088\u3046\u306b\u4f1d\u3048\u3066\u304a\u304d\u307e\u3057\u3087\u3046\u3002\u611a\u304b\u306a\u9023\u4e2d\u304c\u96ea\u91ce\u69d8\u306e\u5468\u308a\u3092\u3046\u308d\u3064\u304b\u306c\u3088\u3046\u306b\u3068", "\u3088\u308d\u3057\u304f\u304a\u9858\u3044\u3057\u307e\u3059", "\u5409\u7965\u9662\u3055\u3093\u3001\u5f1f\u3068\u4f1a\u3063\u305f\u3093\u3060\u3063\u3066\uff1f", "\u3048\u2026", "\u3042\u3001\u79c1\u304c\u30d7\u30c6\u30a3\u306b\u884c\u3063\u305f\u6642\u306b\u5076\u7136\u304a\u4f1a\u3044\u3057\u307e\u3057\u305f\u306e\u3002\u79c1\u306f\u30d7\u30c6\u30a3\u306e\u5973\u306e\u5b50\u306b\u6e21\u3059\u3082\u306e\u304c\u3042\u3063\u3066\u884c\u3063\u305f\u3093\u3067\u3059\u3051\u3069\u3002\u305f\u307e\u305f\u307e\u6249\u3092\u958b\u3051\u3066\u304f\u308c\u305f\u306e\u304c\u5186\u57ce\u69d8\u306e\u5f1f\u69d8\u3067\u3057\u305f\u306e\u3088\u3002\u672c\u5f53\u306b\u5947\u9047\u3067", "\u3046\u3093\u3001\u5f1f\u304b\u3089\u805e\u3044\u3066\u3044\u308b\u3088\u3002\u5f1f\u306e\u304a\u3057\u3083\u3079\u308a\u306b\u4ed8\u304d\u5408\u3063\u3066\u304f\u308c\u305f\u3093\u3060\u3063\u3066\u306d\u3001\u3042\u308a\u304c\u3068\u3046", "\u3044\u3048\u2026", "\u5409\u7965\u9662\u3001\u96ea\u91ce\u306b\u4f1a\u3063\u305f\u306e\u304b", "\u3048\u3048\u3001\u307e\u3041", "\u3075\u30fc\u3093\u2026", "\u2026\u3042\u306e\u3001\u3068\u3066\u3082\u53ef\u611b\u3089\u3057\u304f\u3066\u3001\u512a\u3057\u3044\u5f1f\u69d8\u3067\u3059\u308f\u306d\uff1f", "\u3075\u30fc\u3093\u2026", "\u305d\u3046\u3002\u3042\u308a\u304c\u3068\u3046", "\u3055\u3041\u3001\u3053\u3061\u3089\u306b\u304a\u5ea7\u308a\u306b\u306a\u3063\u3066\u3002\u304a\u83d3\u5b50\u3082\u3042\u308b\u306e\u3088\uff1f", "\u3048\u2026", "\u304a\u8336\u306f\u3044\u304b\u304c\u304b\u3057\u3089\uff1f\u826f\u3044\u8336\u8449\u304c\u3042\u308a\u307e\u3059\u306e", "\u3086\u3063\u304f\u308a\u898b\u5b66\u3057\u3066\u3044\u3063\u3066\u3088\u308d\u3057\u3044\u306e\u3088\uff1f\u3055\u3041\u3055\u3041\u53ec\u3057\u4e0a\u304c\u3063\u3066", "\u3042\u306e\u2026\u3067\u3082\u3001\u98df\u5802\u4ee5\u5916\u3067\u306e\u98f2\u98df\u306f\u2026\u3002\u305d\u308c\u306b\u304a\u83d3\u5b50\u306f\u6821\u5247\u9055\u53cd\u3067\u306f\u2026", "\u3042\u3089\u5e73\u6c17\u3088\u3002\u3053\u308c\u306f\u30d4\u30f4\u30a9\u30ef\u30fc\u30cc\u306e\u30b5\u30ed\u30f3\u306e\u304a\u83d3\u5b50\u3067\u3059\u3082\u306e", "\u3042\u306e\u30fc\u2026", "\u2026\u2026", "\u3053\u308c\u3092\u98df\u3079\u3066\u3057\u307e\u3063\u305f\u3089\u2026", "\u3053\u3046\u3044\u3046\u304a\u5e97\u306f\u82e6\u624b\u304b\u306a\uff1f\u3082\u3057\u30a4\u30e4\u306a\u3089\u30a4\u30bf\u30ea\u30a2\u30f3\u306e\u304a\u3044\u3057\u3044\u304a\u5e97\u3082\u3042\u308b\u3093\u3060\u3051\u3069", "\u3044\u3044\u3048\u3002\u3053\u3061\u3089\u3067\u7d50\u69cb\u3067\u3059\u308f", "\u3053\u3053\u306e\u91dc\u98ef\u306f\u3068\u3063\u3066\u3082\u304a\u3044\u3057\u3044\u3093\u3060\u3002\u305c\u3072\u9e97\u83ef\u3055\u3093\u306b\u98df\u3079\u3066\u6b32\u3057\u304f\u3066\u3002\u3053\u306e\u524d\u3001\u59c9\u304b\u3089\u30d1\u30a8\u30ea\u30a2\u3092\u304a\u3044\u3057\u305d\u3046\u306b\u98df\u3079\u3066\u3044\u305f\u3068\u805e\u3044\u3066\u3001\u3054\u306f\u3093\u304c\u597d\u304d\u306a\u306e\u304b\u306a\u3063\u3066\u601d\u3063\u305f\u3093\u3060", "\u307e\u3041", "\u50d5\u306f\u7d05\u9bad\u3068\u3044\u304f\u3089\u306e\u5fa1\u81b3\u306b\u3059\u308b\u3051\u3069\u3001\u9e97\u83ef\u3055\u3093\u306f\uff1f", "\u3067\u306f\u79c1\u306f\u4e94\u76ee\u5fa1\u81b3\u3067", "\u9e97\u83ef\u3055\u3093\u3001\u826f\u304b\u3063\u305f\u3089\u3053\u3063\u3061\u3082\u98df\u3079\u3066\u307f\u306a\u3044\uff1f"]}
